[81080008] = {id = 81080008, name = "さぁさぁ。お買い得なアイテムが盛りだくさんですから、\n見ていくといいです！"}
, 
[81080009] = {id = 81080009, name = "みんな疲れたなら、温泉でゆ～っくり癒させて！"}
, 
[81080010] = {id = 81080010}
, 
[81080011] = {id = 81080011}
, 
[81080012] = {id = 81080012}
, 
[81080013] = {id = 81080013, name = "エマが船を買ったら、リーダーには航海士を頼むぞ！"}
, 
[81080014] = {id = 81080014, name = "困ったことがあったら言って。私、絶対に力になるから！"}
, 
[81080015] = {id = 81080015, name = "はい、乾杯～！　遠慮しないでいいからねぇ！"}
, 
[81080016] = {id = 81080016}
, 
[81080017] = {id = 81080017, name = "この世でもっとも素敵な女の子同士について、\n一緒に語らいませんか？"}
, 
[81080018] = {id = 81080018}
, 
[81080019] = {id = 81080019, name = "君の美しい顔は憂鬱と似合わないよ"}
, 
[81080020] = {id = 81080020, name = "ほーら、焼き立てのお菓子ですよ～。\nまずは一口、食べてみませんか？"}
, 
[81080021] = {id = 81080021, name = "ウィチタ団の団員として認めて欲しければ、俺様についてこい！"}
, 
[81080022] = {id = 81080022, name = "すべてはわたくしに、掃除をさせていただければ。"}
, 
[81080023] = {id = 81080023, name = "キミも恋愛について悩みがあるの？"}
, 
[81080024] = {id = 81080024, name = "貧しい子供達のために、あんたの誠意を寄付しなさい！"}
, 
[81080025] = {id = 81080025, name = "自分の歌、楽しんでくれたっすか？"}
, 
[81080026] = {id = 81080026, name = "あなたはどんな宝石がお好みですかな？"}
, 
[81080027] = {id = 81080027, name = "さぁさぁ！　とびっきりの芸をお見せいたしましょう！"}
, 
[81080028] = {id = 81080028, name = "ふふ。私がどんな人間かなんて…見ただけじゃわからないだろう？"}
, 
[81080029] = {id = 81080029, name = "何か配達したいものはありますか？　なんでも任せてください！"}
, 
[81080030] = {id = 81080030, name = "最高の絵を描いてみせるれすぉ～"}
, 
[81080031] = {id = 81080031, name = "ここであなたと出会えたことは、きっと神様のご意志でしょうね～。"}
, 
[81080032] = {id = 81080032, name = "わ、私に力を貸してください。がっかりさせたりしませんから…！"}
, 
[81080033] = {id = 81080033, name = "あ、あの……ボクと一緒に、ペットを見に行きませんか？"}
, 
[81080034] = {id = 81080034, name = "運命はあらかじめ決まっている。でも…あなたは違うのね。"}
, 
[81080035] = {id = 81080035, name = "ここには、かなり無謀な奴がいるようだな。"}
, 
[81080036] = {id = 81080036, name = "貴様か？　我輩を深淵の外から呼ぶ者は――"}
, 
[81080037] = {id = 81080037, name = "とっておきの武器と防具を揃えてあるぞ。"}
, 
[81080038] = {id = 81080038, name = "あなたも平和のためにすべてを捧げる戦友か？"}
, 
[81080039] = {id = 81080039, name = "めんどくさくなったら俺を呼べ！　全部ぶっ飛ばしてやる！"}
, 
[81080040] = {id = 81080040, name = "どうかあなたの人生、これからすべてを…私にください。"}
, 
[81080041] = {id = 81080041, name = "もっと遠くの、もっと広いところへ、一緒に行こう？"}
, 
[81080042] = {id = 81080042, name = "お姉さんと遊びたいのかい？　それなら、こちらにおいで。"}
, 
[81080043] = {id = 81080043, name = "お宝が見つかったら、少しは分けてあげなくもないけど？"}
, 
[81080044] = {id = 81080044, name = "報酬がいい感じの仕事、紹介してくれるー？"}
, 
[81080045] = {id = 81080045, name = "あなたは一目惚れを信じてくれますか？"}
, 
[81080046] = {id = 81080046, name = "リーダー、どうぞ私にご命令を。"}
, 
[81080047] = {id = 81080047, name = "あなたなら、私の欲望を満たしてくれるかしら…？"}
, 
[81080048] = {id = 81080048, name = "あたしのために楽しいゲームとか、見つけてきてくれる？"}
, 
[81080049] = {id = 81080049, name = "これからは、拙者がおぬしの刃となるでござるよ。"}
, 
[81080050] = {id = 81080050, name = "平和な未来のために、一緒に努力させてください。"}
, 
[81080051] = {id = 81080051, name = "見てなさい！　あたしは必ず、\n一流の歌手になってみせるんだから！"}
, 
[81080052] = {id = 81080052, name = "にゃはは、引っかかったー！　\nパロネスのイタズラ、大成功だぞー♪"}
, 
[81080053] = {id = 81080053, name = "私はレッドのために戦うわ。"}
, 
[81080054] = {id = 81080054, name = "ふふ、死にそうほど熱い夏を送りましょう？"}
, 
[81080055] = {id = 81080055, name = "ノンノの水着、似合ってるかな！？"}
, 
[81080056] = {id = 81080056, name = "初めまして、ツバキと申します。…初めまして、ですよね？"}
, 
[81080057] = {id = 81080057, name = "ルルリカに良い役あったら、紹介頼んだよ♪"}
, 
[81080058] = {id = 81080058, name = "あ、あの…こんなわたしでも、何かお役に立てることは\nありますか…？"}
, 
[81080059] = {id = 81080059, name = "かわいいものか、ミアか、ファッションに興味はある？　\nあ、全部一緒かぁ。"}
, 
[81080060] = {id = 81080060, name = "ふふ、あなたに懺悔したいことはありますか？　\n…なんてね。"}
, 
[81080061] = {id = 81080061, name = "トリックオアトリート！　\nさぁ、お菓子をよこすでござるよ。"}
, 
[81080062] = {id = 81080062, name = "ロールのこと、たーっくさん見ててねー！"}
, 
[81080063] = {id = 81080063, name = "この度はサンタクロースに任命されましたので\n全力でサンタをさせていただきます。\nもちろん、掃除人としても手を抜くつもりはございません。"}
, 
[81080064] = {id = 81080064, name = "メリークリスマス。さあ、プレゼントをあげよう。"}
, 
[81080065] = {id = 81080065, name = "えへへ、リンだよ～。ね、一緒にお菓子でも食べる？"}
, 
[81080066] = {id = 81080066, name = "いい子達に素敵なプレゼントを届けるために、頑張るわ！"}
, 
[81080067] = {id = 81080067, name = "新年一発、特大ネタを打ち上げますよー！"}
, 
[81080068] = {id = 81080068, name = "初詣に行くとするぞ、リーダー"}
, 
[81080069] = {id = 81080069, name = "お待たせしました、リーダーさん！　あなたのラヴィがなんと、水着を着ちゃいましたよっ♪　ふふっ、どうぞ好きなだけご堪能ください♪"}
, 
[81080070] = {id = 81080070, name = "水着は夏だけのものじゃないって、ラヴィさんに教わりました！　\nというわけでリリーも寒さに負けないよう頑張りますっ…ごふっ！"}
, 
[81080071] = {id = 81080071, name = "私はペンデュラムの次期ボス…クーニャだ。"}
, 
[81080072] = {id = 81080072, name = "あの…一応ペンデュラムの一員、ですっ…！"}
, 
[81080073] = {id = 81080073, name = "私、タネハって言います！　\nじゃあさっそく、\nレイナちゃんについて話をさせてくださいっ！"}
, 
[81080074] = {id = 81080074, name = "あの…わたしの声、聞こえますか…？"}
, 
[81080075] = {id = 81080075, name = "…貴公、面白い気配をしておられるな。"}
, 
[81080076] = {id = 81080076, name = "はふぅ…今日も暑いですね…。え？　\nそんなことないですか？"}
, 
[81080077] = {id = 81080077, name = "仲良くしてくれたら嬉しいけど…\nいきなりつついたりとかしちゃダメだからねっ！"}
