[81080078] = {id = 81080078}
, 
[81080079] = {id = 81080079, name = "腹減った、飯、よこせ。"}
, 
[81080080] = {id = 81080080, name = "育ちが悪く、何かと不躾な所もありますが、\nよろしくお願いします。"}
, 
[81080081] = {id = 81080081, name = "あぁ、何見てんだ？　ぶっ潰すぞ。"}
, 
[81080082] = {id = 81080082, name = "こ、こんな場所で何をするつもりですか！？"}
, 
[81080083] = {id = 81080083, name = "ふふ、私は口が裂けても嘘なんて言わないさ。"}
, 
[81080084] = {id = 81080084, name = "聞いて驚け！　アタシっちは極悪非道の大海賊だ！"}
