[81080092] = {id = 81080092, name = "ひと夏の思い出をつくりましょうか？"}
, 
[81080093] = {id = 81080093, name = "ねえねえ！　\n都会についていろいろ教えてくれないかな！？"}
, 
[81080094] = {id = 81080094, name = "…どうせあなたも、必要がなくなれば\n私のことを捨てるんでしょう？"}
, 
[81080095] = {id = 81080095, name = "ショウ姉さまをお迎えに上がりました。"}
, 
[81080096] = {id = 81080096, name = "クルセイダーダクネスだ。\n防御には自信がある。好きに使ってくれ。"}
, 
[81080097] = {id = 81080097, name = "私が、水の女神アクアよ。覚えておきなさい！"}
, 
[81080098] = {id = 81080098, name = "いらっしゃいませ、こちらのアイテムはいかがですか？"}
, 
[81080099] = {id = 81080099, name = "この邂逅は、世界が選択せし運命。\n私はあなた方のような者の出現を待ち望んでいた…"}
, 
