[81653008] = {id = 81653008, name = "最も攻撃が高い敵に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与え、[color=#3cbcff]陶酔[/color]を[color=#3cbcff]2[/color]ラウンドの間付与する。目標が死亡した場合、[color=#3cbcff]浸透[/color]効果を発動させる。\n\n[color=#ffac26]「陶酔：目標が受けたダメージを25%%アップさせる」\n「浸透：ランダムで目標の周囲の敵1名に拘束を1行動ターン付与する。[/color]"}
, 
[81651107] = {id = 81651107, name = "前の味方が放った奥義を[color=#3cbcff]コピー[/color]して即時発動する。コピーレベル[color=#3cbcff]damage_B[/color]。\n\n[color=#ffac26]「スキルコピー：味方の奥義目標と付加効果をコピーする。コピーレベルが高いほど効果が強い。味方が奥義を放てない場合、発動することができない」[/color]"}
, 
[81653010] = {id = 81653010, name = "敵縦列に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与え、自身より素早さが低い味方の中にいる素早さが最も高い者に[color=#3cbcff]SPを300[/color]点回復する。\n[color=#3cbcff]SPが100%%[/color]未満の時でも発動することができる。[color=#3cbcff]※60%%≤SP≤100%%[/color]\n[color=#3cbcff]SPが100%%[/color]になった状態で奥義を発動する場合、自身のHPが減少しない。\n[color=#3cbcff]SPが60%%≤SP<100%%[/color]になった状態で奥義を発動する場合、自身の最大HPの[color=#3cbcff]10%%[/color]を失う。\n※HPが足りない場合は発動不可。"}
, 
[81653009] = {id = 81653009, name = "敵全体に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与え、SPを[color=#3cbcff]150[/color]点減少させる。さらに[color=#3cbcff]1[/color]行動ターンの間、敵のHP回復効果を[color=#3cbcff]35%%[/color]ダウンさせる。"}
, 
[81652112] = {id = 81652112, name = "敵縦列に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与え、[color=#3cbcff]50%%[/color]の確率で失明を[color=#3cbcff]1[/color]行動ターン付与する。\n\n[color=#ffac26]「失明：すべての攻撃は目標に命中できない」[/color]"}
, 
[81652111] = {id = 81652111, name = "敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、応援を発動して自身のHPに転換する。応援するごとにライフリンク効果を持っている各味方に[color=#3cbcff]（攻撃*20%%）[/color]のHPを回復する。"}
, 
[81652208] = {id = 81652208, name = "最も防御が高い敵[color=#3cbcff]3[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える。[color=#3cbcff]2[/color]行動ターンの間、目標の防御を[color=#3cbcff]50%%[/color]低下させ、自身の防御を[color=#3cbcff]50%%[/color]上昇させる。"}
, 
[81653011] = {id = 81653011, name = "最も攻撃が高い敵[color=#3cbcff]3[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、目標人数が少ないほど、各単体に与えるダメージが高くなる。目標に[color=#3cbcff]威嚇[/color]を[color=#3cbcff]1[/color]行動ターンの間付与し、攻撃を[color=#3cbcff]20%%[/color]低下させる（[color=#3cbcff]2[/color]ターン持続）。\n\n[color=#ffac26]「威嚇：効果持続期間中、通常攻撃と特技による基礎SP回復はできない。攻撃されてもSP回復はできない」[/color]"}
, 
[81653012] = {id = 81653012, name = "敵前列に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、[color=#3cbcff]1[/color]行動ターンの間、[color=#3cbcff]20%%[/color]の確率で目標を気絶させ（自身の最大HPが[color=#3cbcff]5%%[/color]軽減ごとに、追加で気絶の発生確率を[color=#3cbcff]2%%[/color]アップさせる）、[color=#3cbcff]理性喪失[/color]を[color=#3cbcff]2[/color]大ラウンドの間付与する。さらに目標のSPを[color=#3cbcff]100[/color]点減少させ、自身に[color=#3cbcff]100[/color]点のSPを回復する。\n\n[color=#ffac26]「気絶：行動不能となる。ガードすることもできない」\n「理性喪失：毎ターン最大HPの6%%分失う」[/color]"}
, 
[81652209] = {id = 81652209, name = "敵全体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、ランダムで[color=#3cbcff]トリート[/color]か[color=#3cbcff]トリック[/color]効果を付与する。\n\n[color=#ffac26]「トリート：1行動ターンの間、味方全体の攻撃を10%%アップし、防御を20%%アップする。即座に（攻撃*20%%）のHPを回復する」\n「トリック：1行動ターンの間、敵全体の攻撃を10%%、防御を20%%ダウンさせる。1ラウンドの間（攻撃*20%%）点の持続ダメージを与える」[/color]"}
, 
[81653013] = {id = 81653013, name = "屍毒状態にある敵[color=#3cbcff]1[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、SPを[color=#3cbcff]200[/color]点減少させる。目標を撃破すると、追加で自身に[color=#3cbcff]150[/color]点のSPを回復する。"}
, 
[81653014] = {id = 81653014, name = "最も攻撃が高い敵[color=#3cbcff]4[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、[color=#3cbcff]50%%[/color]の確率で[color=#3cbcff]1[/color]行動ターン相手を[color=#3cbcff]挑発[/color]する。さらに自身に[color=#3cbcff]リフレクション[/color]を付与し、[color=#3cbcff]2[/color]行動ターンの間最もHPが低い味方にシールドを張り、[color=#3cbcff]（攻撃*80%%）[/color]のダメージを吸収する。\n\n[color=#ffac26]「挑発：目標が奥義を使用できない；目標の通常攻撃/特技を自身に集中させる」\n「リフレクション：行動ダメージを受けると、相手に（攻撃*80%%）のダメージを与える。最大4回まで。」[/color]"}
, 
[81652210] = {id = 81652210, name = "敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、自身にSPを[color=#3cbcff]200[/color]点回復する。"}
, 
[81653015] = {id = 81653015, name = "ランダムで敵[color=#3cbcff]3[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える。\n1．自身のHPが[color=#3cbcff]50%%[/color]以下の場合、自身に最大HP[color=#3cbcff]10%%[/color]分のHPを回復し、[color=#3cbcff]50%%[/color]の確率で相手に[color=#3cbcff]雪だるま[/color]を付与する。\n2．自身のHPが[color=#3cbcff]50%%[/color]以上の場合、自身に[color=#3cbcff]リフレクション[/color]を付与し、[color=#3cbcff]50%%[/color]の確率で相手に[color=#3cbcff]拘束[/color]を[color=#3cbcff]1[/color]行動ターン付与する。\n\n[color=#ffac26]「雪だるま：相手を雪だるまにする。効果持続期間中、行動及びガードができなくなり、防御が100%%ダウンする。1行動ターン持続。浄化不可」\n「リフレクション：行動ダメージを受けると、目標に（攻撃*80%%）のダメージを与える。最大2回まで」\n「拘束：通常攻撃と特技が使用できない。協撃に参加しない」[/color]"}
, 
[81653016] = {id = 81653016, name = "敵縦列に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、相手に[color=#3cbcff]沈黙[/color]を[color=#3cbcff]1[/color]行動ターンの間付与し、自身にSPを[color=#3cbcff]300[/color]点回復する。\n\n[color=#ffac26]「沈黙：奥義を発動できない」[/color]"}
, 
[81653024] = {id = 81653024, name = "敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える。目標を撃破した場合、目標の十字列にいる敵に[color=#3cbcff]（攻撃*40%%）[/color]のダメージを拡散する。"}
, 
[81652214] = {id = 81652214, name = "敵後列の単体に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与え、目標のSPが[color=#3cbcff]100[/color]点増加する度に、与ダメージが追加で[color=#3cbcff]10%%[/color]上昇する。"}
, 
[81653017] = {id = 81653017, name = "敵全体に[color=#3cbcff]コンフュージョン[/color]効果を[color=#3cbcff]2[/color]つ付与する。"}
, 
[81653018] = {id = 81653018, name = "最もHPが低い敵に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与える。"}
, 
[81653019] = {id = 81653019, name = "敵後列に[color=#3cbcff]（攻撃*damage_A%%damage_B）[/color]点のダメージを与える。[color=#3cbcff]30%%[/color]の確率で失明を[color=#3cbcff]1[/color]行動ターンの間付与する。目標が極寒効果を受けている場合、追加でダメージ[color=#3cbcff](攻撃*100%%)[/color]を与え、極寒状態の敵[color=#3cbcff]1[/color]体につき、追加で自身に[color=#3cbcff]100[/color]点のSPを回復する。\n\n[color=#ffac26]「失明：すべての攻撃は目標に命中できない」\n「極寒：ターン毎に与えたダメージの10%%分のダメージを与え（最大10回まで重複可能）、3ラウンド持続。」[/color]"}
, 
[81653030] = {id = 81653030, name = "敵後列の単体に[color=#3cbcff]（攻撃*damage_A%%damage_B) [/color] 点のダメージを与える。目標のHPが[color=#3cbcff]30%%[/color] 以上の場合、今回の攻撃が終わると自身のSPを[color=#3cbcff]200[/color] 点回復する；目標のHPが[color=#3cbcff]30%%[/color] 未満の場合、今回の奥義ダメージを[color=#3cbcff]20%%[/color] 上昇させる。"}
, 
[81653026] = {id = 81653026, name = "敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、[color=#3cbcff]70%%[/color]の確率で目標を[color=#3cbcff]2[/color]行動ターンの間混乱させる。\n\n[color=#ffac26]「混乱：奥義と特技を使用できない。通常攻撃の目標はランダムであり、協撃を発動しない」[/color]"}
, 
[81652213] = {id = 81652213, name = "敵全体に[color=#3cbcff]（攻撃*damage_A%%damage_B) [/color]点のダメージを与える。"}
, 
[81653022] = {id = 81653022, name = "ランダムに敵2名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える。目標より自身のHPが高い場合、今回の奥義による会心ダメージが[color=#3cbcff]20%%[/color]上昇する。"}
, 
[81653025] = {id = 81653025, name = "敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、目標を[color=#3cbcff]1[/color]行動ターンの間[color=#3cbcff]失明[/color]させる。\n\n[color=#ffac26]「失明：すべての攻撃は目標に命中できない」[/color]"}
, 
[81653027] = {id = 81653027, name = "目標のSPを[color=#3cbcff]20%%[/color]減少させる。防御を[color=#3cbcff]20%%[/color]無視したうえに、最も攻撃が高い敵2名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える。\n目標のSPが[color=#3cbcff]30%%[/color]以下の場合、減らしたSP[color=#3cbcff]100%%[/color]分のSPを自身のSPに転化する；\n目標のSPが[color=#3cbcff]30%%[/color]＜SP≤[color=#3cbcff]60%%[/color]の場合、減らしたSP[color=#3cbcff]70%%[/color]分のSPを自身のSPに転化する；\n目標のSPが[color=#3cbcff]60%%[/color]超えの場合、減らしたSP[color=#3cbcff]60%%[/color]分のSPを自身のSPに転化する。"}
, 
[81654001] = {id = 81654001, name = "敵全体にダメージを与え、[color=#3cbcff]1[/color]行動ターンの間、目標の攻撃を[color=#3cbcff]22%%[/color]ダウンさせる。"}
, 
[81653028] = {id = 81653028, name = "敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える上に、目標に自身の攻撃[color=#3cbcff]40%%[/color]分の持続ダメージ効果（重複可能）を付与する。[color=#3cbcff]3[/color]ラウンド持続。"}
, 
[81652215] = {id = 81652215, name = "敵後列に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、[color=#3cbcff]50%%[/color]の確率で目標を[color=#3cbcff]1[/color]ラウンドの間[color=#3cbcff]重傷[/color]させる。\n\n[color=#ffac26]「重傷：あらゆるHP回復手段が無効になる」[/color]"}
, 
[81653029] = {id = 81653029, name = "最もHPが低い敵[color=#3cbcff]2[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与え、目標のHPが[color=#3cbcff]10%%[/color]低下するたび、今回の攻撃によるダメージを[color=#3cbcff]5%%[/color]上昇させる；目標は「子分」効果を受け、かつHPが[color=#3cbcff]10%%[/color]未満の場合、[color=#3cbcff]「デッド」[/color]攻撃を受ける。\n\n[color=#ffac26]「デッド：シールドを無視し、直ちに目標を撃破する」[/color]"}
, 
[81653031] = {id = 81653031, name = "敵縦列に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color] 点のダメージを与える。[color=#3cbcff]「闘志高揚」[/color]がかかっている場合、「攻撃アップバフ」毎に追加で目標に（攻撃*[color=#3cbcff]8%%[/color]）のダメージを与える；奥義を発動した後、すべての「攻撃アップバフ」が解除される。"}
, 
[81652216] = {id = 81652216, name = "ランダムに敵[color=#3cbcff]3[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B) [/color]点のダメージを与え、[color=#3cbcff]1[/color]行動ターンの間、味方後列の攻撃を[color=#3cbcff]15%%[/color]上昇させる。"}
, 
[81653032] = {id = 81653032, name = "最もSPが高い敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B) [/color]点のダメージを与え、[color=#3cbcff]40%%[/color]の確率で追加で[color=#3cbcff]（攻撃*20%%）[/color]のダメージを与える；目標が[color=#3cbcff]「傀儡」[/color]マークを付いていると、目標の十字列にいる敵に[color=#3cbcff]（攻撃*50%%）[/color]のダメージを拡散する。\n\n[color=#ffac26]「傀儡：沈黙効果の発動に成功したら、目標に傀儡を付与する；リューゲの奥義が当該効果を受けている目標を優先的に攻撃する；この効果は同時に1つまで存在できる」[/color]"}
, 
[81653023] = {id = 81653023, name = "「通常状態」\nランダムに敵[color=#3cbcff]4[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B) [/color]点のダメージを与え、[color=#3cbcff]40%% [/color]の確率で[color=#3cbcff]1 [/color]行動ターンの間[color=#3cbcff]挑発 [/color]する；目標を撃破すると、追加で自身のSPを[color=#3cbcff]60 [/color]点回復する。\n\n[color=#ffac26]「挑発：目標が奥義を発動できなくなり、目標の通常攻撃/特技を発動者に集中させる」 [/color]\n\n「暴走状態」\n敵後列の単体が持っている[color=#3cbcff]シールド/守護/ガード/聖佑[/color]のうち[color=#3cbcff]1[/color]つをランダムで解除させ、[color=#3cbcff]（攻撃*damage_A_42003123%%damage_B_42003123)[/color]点のダメージを与える；[color=#3cbcff]「海賊の魂」[/color]ごとに今回の攻撃に[color=#3cbcff]（攻撃*8%%）[/color]のダメージを追加で与える；今回の攻撃によって目標が撃破された場合、追加で自身のSPを[color=#3cbcff]200[/color]点回復する。"}
, 
[81653033] = {id = 81653033, name = "敵全体に[color=#3cbcff]（攻撃*damage_A%%damage_B)  [/color]点のダメージを与え、[color=#3cbcff]35%% [/color]の確率で[color=#3cbcff]1 [/color]行動ターンの間[color=#3cbcff]沈黙 [/color]させる。\n※今回の攻撃で敵のHP回復は発生しない\n\n[color=#ffac26]「沈黙：奥義を使用できない」 [/color]"}
, 
[81652217] = {id = 81652217, name = "最も防御が低い敵単体に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color]点のダメージを与える；自身の防御が目標より高い場合、今回の攻撃では会心力を[color=#3cbcff]50%%[/color]上昇させる。"}
, 
[81653034] = {id = 81653034, name = "最もHPが低い敵[color=#3cbcff]2[/color]名に[color=#3cbcff]（攻撃*damage_A%%damage_B)[/color] 点のダメージを与え、[color=#3cbcff]2[/color]ラウンドの間[color=#3cbcff]重傷[/color]させる；さらに目標のSPを[color=#3cbcff]200[/color]点吸収して、そのSPの[color=#3cbcff]40%%[/color]分を最低SPの味方に分け合う。\n\n[color=#ffac26]「重傷：あらゆるHP回復手段が無効になる」[/color]"}
, 
