[83900214] = {id = 83900214, name = "ピカピカと光っている。"}
, 
[83900311] = {id = 83900311, name = "この剣にかっこいい名前をつければ、きっといい値段で売れるです!——リッタ"}
, 
[83900312] = {id = 83900312, name = "熟練冒険者のオススメ。軽いながらもしっかりしている。"}
, 
[83900313] = {id = 83900313, name = "指輪の内側に何か刻まれているようだ……もしかして上古の呪？！"}
, 
[83900314] = {id = 83900314, name = "この水晶のデカさから制作者の財力が分かる……いや、センスが分かる！"}
, 
[83900411] = {id = 83900411, name = "全能の神よ、教会はもうこんなに貧しいのに、何か救いがありませんか？ーーアーク教会のある神父"}
, 
[83900412] = {id = 83900412, name = "ベロア製のローブ。暖かくて着心地も良く、いくら風が吹いても怖くない。"}
, 
[83900413] = {id = 83900413, name = "キラキラしたサファイアの指輪には、強い力が込められているようだ。"}
, 
[83900414] = {id = 83900414, name = "冒険者よ、あなたが探しているのはルビーかそれともシトリンか？……じゃあ、このサファイアを差し上げましょう！"}
, 
[83900511] = {id = 83900511, name = "この弓は高い値段で手に入れたのです！これ以上安くするわけにはいかないです！ーーリッタ"}
, 
[83900512] = {id = 83900512, name = "見かけは普通だが、身につければ結構いい感じになりそう……重い！"}
, 
[83900513] = {id = 83900513, name = "指輪から暖かいものが湧き出ている。身につけると寒さが怖くなくなる！"}
, 
[83900514] = {id = 83900514, name = "このような精巧なネックレスなら、アンティークとして転売すれば、結構儲かるかしら？ーーシャノン"}
, 
[83900611] = {id = 83900611, name = "当たったらさぞかし熱いだろう！"}
, 
[83900612] = {id = 83900612, name = "炎のような形をしているが、涼しい着心地。"}
, 
[83900613] = {id = 83900613, name = "この指輪をつけている者は皆情熱的になっていくらしい！——ある情熱的な冒険者"}
, 
[83900614] = {id = 83900614, name = "「奉献！希望！栄光！」——ネックレスに変な言葉が刻まれている"}
, 
[83900711] = {id = 83900711, name = "ククク……吾辈「冥夜の堕天使」と共に、常世の神話を作り上げようぞ！——カノン"}
, 
[83900712] = {id = 83900712, name = "真っ黒なアーマー。夜に装着していれば暗闇に溶け込める。"}
, 
[83900713] = {id = 83900713, name = "深淵をのぞく時、深淵もまたこちらをのぞいているのだ。"}
, 
[83900714] = {id = 83900714, name = "古代文明が遺したネックレース。神秘な力が秘められている。"}
, 
[83900811] = {id = 83900811, name = "この剣はあまりにも輝き過ぎて、戦闘中では相手が目を開けられない！"}
, 
[83900812] = {id = 83900812, name = "この鎧は盤石ように、どんな攻撃にも耐えられます！——謎の商人"}
, 
[83900813] = {id = 83900813, name = "この指輪を手に嵌めれば、神様にも負けりゃしない！"}
, 
[83900814] = {id = 83900814, name = "この品物はどこかで見たことがあるような気がする……まあ、思い出せない。——エヴァンテ"}
, 
[83900911] = {id = 83900911, name = "極めて鋭い切れ味以外、必要なのはあらゆるものを破壊できるほどの重さだ！"}
, 
[83900912] = {id = 83900912, name = "威風堂々たる鎧だが、かなり重いので、長時間着ていると背が伸びなくなる恐れがある。"}
, 
[83900913] = {id = 83900913, name = "ある王族や神に属するものだったかもしれないが、今は力の強い者に与えられるべきだ。"}
, 
[83900914] = {id = 83900914, name = "「女王様、御誕生日おめでとうございます」――裏にこのような言葉が刻まれている。"}
, 
[83901011] = {id = 83901011, name = "様々な神への祈祷文が記載されているが、その多くは宣教師たちが自作したものだという。"}
, 
[83901012] = {id = 83901012, name = "走るにはちょっと不便があるが、それ以外ほとんど非の打ち所がない。"}
, 
[83901013] = {id = 83901013, name = "かつて教会が裕福だった頃に敬虔な信者に与えた指輪。今ではめったに見られなくなったものだ。"}
, 
[83901014] = {id = 83901014, name = "とてもきれいなペンダント！軽く叩くだけで美しい音が聞こえる。"}
, 
[83905010] = {id = 83905010, name = "「炎の操者」"}
, 
[83905011] = {id = 83905011, name = "溶岩で鍛えたという無双の斧。振るうと灼熱の炎を吹き出す。"}
, 
[83905012] = {id = 83905012, name = "全身が赤い輝きに満たされ、高い防御力を持つ。"}
, 
[83905013] = {id = 83905013, name = "身につけた瞬間、全身に力がみなぎってくる。"}
, 
[83905014] = {id = 83905014, name = "中には視力に関して尋常ならざる能力を宿っているため、対象者に呪いを掛ける力があるだという。"}
