, 
[83905022] = {id = 83905022, name = "堅牢な防御力を持ち、あらゆる強力な攻撃から身を守る盾になる！"}
, 
[83905023] = {id = 83905023, name = "神秘な力を宿っている。今は選ばれし冒険者にのみ使用が許されるという。"}
