, 
[87330100] = {id = 87330100, name = "修行学園で、様々なレッドたちが自分の能力を向上させるように切磋琢磨している。"}
, 
[87330101] = {id = 87330101, name = "学園内で散歩をしているショウは剣の達人に出会い、そこで腕試しの勝負を挑むことに。"}
, 
[87330102] = {id = 87330102, name = "キャストレッドが他のレッドをカツアゲしているところを目撃したグローリア。その光景に怒りが込み上げたグローリアは、カツアゲしたキャストレッドを呼び止め、ボコボコにした。"}
, 
[87330103] = {id = 87330103, name = "モンスターが学園に侵入!非常事態なのに、イザベラはどこか興奮を隠せない様子だった。"}
, 
[87330104] = {id = 87330104, name = "リーダーに近づきすぎたがるレッドを見つめながら、アロエは謎の液体が入った薬瓶を差し出す…"}
, 
[87330105] = {id = 87330105, name = "みんなで奥義を修行中。エイプリルだけが日陰でぐうぐう眠れていた。"}
, 
[87330106] = {id = 87330106, name = "速報！修行学園でのリッタのアイテムショップが開店！"}
, 
[87330107] = {id = 87330107, name = "修行学園の隅っこが騒がしいようだ。調査の結果、パロネスがいたずらをしていたことが判明した。"}
, 
[87330108] = {id = 87330108, name = "学園の掃除を手伝おうとするが、力が強すぎてつい家具を壊してしまったマルタは落ち込み中。"}
, 
[87330109] = {id = 87330109, name = "学園の隅で、今日も穴掘りに精を出すフィナを発見！"}
, 
[87330110] = {id = 87330110, name = "突然「こっち見て～」と声がかかり、振り向くとショコラは見事な出来栄えのショートケーキを見せる。"}
, 
[87330111] = {id = 87330111, name = "ジュリアンとルルリカの熱演に、観客席から拍手と歓声が鳴り止まない！"}
, 
[87330112] = {id = 87330112, name = "コンチータはマルタの怪力に感動しながら、マルタを一座に誘う方法を考え中。"}
, 
[87330113] = {id = 87330113, name = "アイリーンの公演券を買うため、正規の何倍より高い値段を払った人も多かったらしい。"}
, 
[87330114] = {id = 87330114, name = "「修行学園のバトルで負けた者はこっそり殺されるんだってさ。…嘘だけどね」とリューゲはマシロに言う。"}
, 
[87330115] = {id = 87330115, name = "学園内のある部屋で、ラヴィは他国から手に入れた怪しいお飲み物をリーダーに迫るのだった。"}
, 
[87330116] = {id = 87330116, name = "学園でやっと静かに勉強できると思っていたフランチェスカだったが、バカ達の声が至るところに満ちあふれていた。"}
, 
[87330199] = {id = 87330199, name = "修行学園で勉強することで、みんなの能力が大きく上がった！"}
, 
[87330200] = {id = 87330200, name = "学園内で散歩をしているショウは剣の達人に出会い、そこで腕試しの勝負を挑むことに。"}
