[87330201] = {id = 87330201, name = "同好者を見つけたカノンは興奮し、冥界について熱く語り始める。"}
, 
[87330202] = {id = 87330202, name = "いくら推してもドアを開けられなかったエイプリルは、混乱に陥った。その時、通りすがりのオリヴィアがドアを横に引くと、ぱっと開いた。"}
, 
[87330203] = {id = 87330203, name = "校庭を通りかかると、ハリーはまた楽しそうに誰かとレースをしている。"}
, 
[87330204] = {id = 87330204, name = "どうもどうも！記者のシルヴィです♪修行学園ではどんな面白いことが起きているのだろうか。さて続いてまいりましょう！"}
, 
[87330205] = {id = 87330205, name = "裏社会のことを報道しようとしたシルヴィは最近、密かにクーニャの後をつけて取材している。"}
, 
[87330206] = {id = 87330206, name = "「メイを狙う変なヤツは全部俺が倒してやるんだ！」と言うブリジットは周囲を警戒中。"}
, 
[87330207] = {id = 87330207, name = "いつか海と船の大先輩フォーゼと一緒に航海するために、エマは今日も頑張って貯金に励んでいる。"}
, 
[87330208] = {id = 87330208, name = "ソフィアに「どちらさま…？」と聞かれたのは、今日でもう10回目。"}
, 
[87330209] = {id = 87330209, name = "過労でネネルが倒れ、学園には落ち葉が散乱すようになった。"}
, 
[87330210] = {id = 87330210, name = "執筆のネタを集めているイルダは、今日も仲良い女の子たちの様子を背後から見守っている。"}
, 
[87330211] = {id = 87330211, name = "学園マスターに街の掃除を手伝ってくれるネネル。話をしながら修行術を教えてくれた。"}
, 
[87330299] = {id = 87330299, name = "修行学園で勉強することで、みんなの能力が大きく上がった！"}
, 
[87330300] = {id = 87330300, name = "モンスターの群れに襲われ、木に登って難を逃れた一行は一安心。"}
, 
[87330301] = {id = 87330301, name = "丘には濃霧がかかっていた。何も見えないまま、恐る恐る前進していく一行…"}
, 
[87330302] = {id = 87330302, name = "急に草むらから何かの音が立ててると思ったら、モンスターが襲ってきた！"}
, 
[87330303] = {id = 87330303, name = "丘の奥深くでモンスターに襲われた冒険者を助け、アイテムを貰った。"}
, 
[87330304] = {id = 87330304, name = "探索で必要な素材を探していたところ、数が全然足りないらしい。「そういうことなら、みんなで手分けして探そう！」と、そう言ったエイプリルに、「それは危険だ。バカの言うことは無視」とフランが突っ込む。"}
, 
[87330305] = {id = 87330305, name = "一行は探索を続け、目の前に現れた森を抜けようとするが、大きなモンスターが立ちふさがる。"}
, 
[87330306] = {id = 87330306, name = "地図に記された場所に辿り着いた一行。先に宝石を発見したシャノンは、こっそりポケットにしまいこもうとするが、オリヴィアに阻止した。"}
, 
[87330307] = {id = 87330307, name = "霧の丘ではぐれないように、体を縄で縛られた状態で進んでいく一同だった。"}
, 
[87330308] = {id = 87330308, name = "一行の周りにはいくつかのテントが現れた、知らないうちに冒険者のキャンプ場へ流れ着いたことに気がついた。"}
, 
[87330309] = {id = 87330309, name = "夜、夕食を楽しんでいる一行。そんな中ベルは、霧の丘の伝説について教えた。なんでもその深い丘の奥には、偉大な宝が眠っているというお話だ。"}
, 
[87330310] = {id = 87330310, name = "探索中、キャストレッドに遭遇。一行は戦うことに。"}
, 
[87330399] = {id = 87330399, name = "モンスターに追いかけられたりキャストレッドに遭遇しながらも、謎の洞窟を発見。伝説の宝を見つけたかと思ったが、中には何にもなかったらしい。"}
, 
[87330400] = {id = 87330400, name = "ある湖に到着した一同。「こんなところに湖があるなんて不思議ね」と感心していると、突然モンスターが湖から飛び出してきた！"}
, 
[87330401] = {id = 87330401, name = "急に草むらから何かの音が立ててると思ったら、モンスターが襲ってきた！"}
, 
[87330402] = {id = 87330402, name = "道に迷ったとき、一行の目に映ったのは、トレジャーハンター・ベルが持ってきた古びた丘の地図だった。"}
, 
[87330403] = {id = 87330403, name = "一行は丘のある屋敷に立ち寄り、その屋敷の主から助言を受ける。"}
, 
[87330404] = {id = 87330404, name = "地味な色のキノコなら大丈夫だと思って食べたヌエットは気絶し、アロエの薬のおかげで目を覚めた。"}
, 
[87330405] = {id = 87330405, name = "一行は霧ふり丘を通過しようとしていたところ、雷雨を避けるため一旦休憩することに。"}
, 
[87330406] = {id = 87330406, name = "一行は探索を続け、目の前に現れた森を抜けようとするが、大きなモンスターが立ちふさがる。"}
, 
[87330407] = {id = 87330407, name = "フィナは夜中に自分が見張りをすると言い、穴を掘り続けることで眠りを避けようとする。"}
, 
[87330408] = {id = 87330408, name = "森の中に入ると、すでに立派なツリーハウスが建っていた。ちょっと変な感じがするオリヴィアに、「とりあえず上に登って見てみよう！」と、エイプリルが言い出す。"}
, 
[87330409] = {id = 87330409, name = "一行は歩き続けているため、中には疲労している者も出てくる。気を紛らわそうと歌をうたうこともあった。"}
, 
[87330499] = {id = 87330499, name = "探索を続ける一行は遠くに立ち上る煙を見つけ、急いでその煙のほうに向かう！もしかして、人…？しかしそこにあったのは、温泉？！探索で疲れた心と体を温泉で癒す一行だった！"}
, 
[87330500] = {id = 87330500, name = "雪山の薬草採取のクエストでは、シャイは寒い所になれて可愛い草花を集めることをひたすら楽しむ。"}
, 
[87330501] = {id = 87330501, name = "一行は寒さで意識を失った冒険者を発見し、治療した。"}
, 
[87330502] = {id = 87330502, name = "あまりにも寒いので、リリーはくしゃみばかりしていた。"}
, 
[87330503] = {id = 87330503, name = "探索に没頭する一行。気付けば方角を見失って、あたりが暗くなってきた。一行は方向の捜索へ向かいつつ、暖を取ろうと策を考えるが…。"}
, 
[87330504] = {id = 87330504, name = "雪山を進んでいくと、丁度ほかの冒険者たちを発見。探索に役立つ情報を提供してくれた。"}
, 
[87330505] = {id = 87330505, name = "疲れたから自分を背負って山に登るようにリーダーに頼むアロエだったが、そこでラヴィが突然現れる。"}
, 
[87330506] = {id = 87330506, name = "一行が進んでいくと、大きなゆきなだれに遭遇。パニックになるなか、すぐとなりの洞窟の中に潜り込み難を逃れた。"}
, 
[87330507] = {id = 87330507, name = "道を進んだ一行は、ある門を発見。そこには2匹のモンスターが監視役としていたため、アロエは毒を投げつけてモンスターを無力化した。"}
, 
[87330508] = {id = 87330508, name = "怪我をしたペットを見つけ、ノエルの世話で元気になった。"}
, 
[87330509] = {id = 87330509, name = "道に迷った冒険家を発見。冒険者を連れ出すことを決めた一行。そんな時、突然、巨大なモンスターが現れ、一行に襲い掛かってくる。"}
, 
[87330510] = {id = 87330510, name = "宝を狙うキャストレッドが現れ、一行は戦うことに。"}
, 
[87330511] = {id = 87330511, name = "雪の上にたくさんのアイテムを発見。傷み具合から判断し、ずいぶん前の冒険者が残したみたいだ。"}
, 
[87330599] = {id = 87330599, name = "氷の穴の中に落ちたリーダーを慌てて助ける一行だった！"}
, 
[87330600] = {id = 87330600, name = "雪山の奥からかすかな歌声が聞こえてきて、「雪山に住んでいる幽霊が歌ってる」と淡々とした表情で言うリューゲ。みんな怯えているが、本当は風の音だけだった。"}
, 
[87330601] = {id = 87330601, name = "雪の中になんとか古い小屋に到着した一行。雪が降りだし、小屋で雪が降りやむのを待つほうが安全と決断して一旦休憩を。"}
, 
