[87330609] = {id = 87330609, name = "氷の穴の中に落ちたリーダーを慌てて助ける一行だった！"}
, 
[87330699] = {id = 87330699, name = "みんなで力を合わせて、薬草を守るモンスターを倒した。クエストの薬草を入手！"}
, 
[89200001] = {id = 89200001, name = "ふふ、この年になると誕生日なんて照れちゃうけど…でもリーダーさんがお祝いしてくれるなら嬉しいわ♪　…それで、プレゼントの金貨袋はどこかしら？"}
, 
[89200002] = {id = 89200002, name = "まぁ、忙しくてすっかり忘れてたわ…。ふふ、でも忘れず祝ってもらえるのは、やっぱり嬉しいわね。あなたの誕生日のときも任せてちょうだい。"}
, 
[89200003] = {id = 89200003, name = "ふっふっふ、驚いた？　実は四月一日生まれじゃないんだなー！　ま、とにかく今日は朝から晩まで、盛大に祝って、盛大に遊ぼうねーっ！"}
, 
[89200004] = {id = 89200004, name = "今日のリリーは、いつもの何万倍も元気で絶好調です！　だって、今日はリリーの誕生日ですから！　成長したリリーは、誰にも止められませんよーっ！"}
, 
[89200005] = {id = 89200005, name = "…去年の私より、確実に成長できていると思う。また冒険を重ねて、さらに知識と経験を積んでみせる。…あと、身長も伸ばす。"}
, 
[89200006] = {id = 89200006, name = "えっっ…わたしのために、お祝いしてくれるんですか…！？　あ、ありがとうございます…！　でも、あの、お祝いされるのは慣れてなくて…わたしは何をすれば…？"}
, 
[89200007] = {id = 89200007, name = "うう～～っ、嬉しいですぅっ…！　だってみんな忘れてるだろう私の誕生日を、覚えていてくれたなんてぇ…！　ううっ、プライドに入って…本当によがっだぁ…！"}
, 
[89200008] = {id = 89200008, name = "タダより高いものはありませんから、誕生日プレゼントは不要ですよ。でも、どうしてもと言うなら…うちの店でたーーーっくさん、アイテム買ってけですっ！"}
, 
[89200009] = {id = 89200009, name = "わーい！　今日でもうひとつ、お姉さんになれたよ！　もっと大きくなって、たくさんの人癒せるようになるんだ！　いーっぱい頑張るから見ててね、リーダー！"}
, 
[89200010] = {id = 89200010, name = "リーダーだけに特ダネを教えちゃいますっ…！　なんとなんと…今日はあたしの誕生日なんです！　ってことでリーダー、最高のネタをくださいっ！　えへへ！"}
, 
[89200011] = {id = 89200011, name = "リーダー、誕生日プレゼントください！　あっもう全然気は遣わず大丈夫なので、とりあえずお金とか大金とか大富豪とかください！　なるべく早くお願いしますっ♪"}
, 
[89200012] = {id = 89200012, name = "今年の誕生日は、これでもかってくらい『シエラグルメ』の研究をする日にしようって決めてたの！　えへへ、今から楽しみだなぁ～。あ、リーダーも試食してくれる？"}
, 
[89200013] = {id = 89200013, name = "今日はエマの誕生日だぞ！　だからリーダー、一緒に海へ漁に出るぞ！　海でフィシュラを釣って、フィシュラパーティーを開かないとなー！"}
, 
[89200014] = {id = 89200014, name = "あのね、お誕生日だからってみんなが優しくしてくれるの。それは嬉しいんだけど…私、誰かを甘やかせないのが耐えられないの！　リーダー、甘えてくれないかな！？"}
, 
[89200015] = {id = 89200015, name = "えっへへぇ～、今日はめでたいあたしの誕生日だぁ～！　ってことだからぁ、リーダーも一緒に飲もぉ～！　あ、もちろんリーダーはジュースでもいいよぉ！"}
, 
[89200016] = {id = 89200016, name = "私の誕生日会ということで…今夜、食事会を開くんだが、どうか招かれてくれないかな？　世界中の最高の美食をそろえてあるから、気軽に楽しもうじゃないか。"}
, 
[89200017] = {id = 89200017, name = "リーダーさん、一生のお願いがあります！　どうか、どうか私の誕生日に免じてリーダーさんの世界の百合をできる限り、私に伝授してください！　お願いしますッ…！"}
, 
[89200018] = {id = 89200018, name = "誕生日は好きなだけ穴を掘るって決めてるので、今日はずーっと穴を掘るんですっ…！　リーダーさんも、穴に入りたいときは言ってくださいね？　ふふ。"}
, 
[89200019] = {id = 89200019, name = "ああ、リーダー…麗しの君よ！　僕の誕生日を祝ってくれるというのかい？　ありがとう、とても嬉しいよ…！　お礼に、君の誕生日はもっと素敵な日にしてみせよう！"}
, 
[89200020] = {id = 89200020, name = "今日は誕生日だから、ついたくさんお菓子を作っちゃいました～。リーダーも一緒に食べて、お祝いしてください～。あ、もちろんお菓子の持ち込みも大歓迎です♪"}
, 
[89200021] = {id = 89200021, name = "ウィチタ団団長である俺様の誕生日には団員全員が集まり、誕生日パーティが開かれるんだ！　だからリーダーもプレゼントを持って、参加することだ！　いいな！"}
, 
[89200022] = {id = 89200022, name = "本日は誕生日ということで、掃除用具を一新させていただきました。新しいホウキ、モップ…より一層掃除が捗ります。もちろんリーダー様のお部屋もお任せください。"}
, 
[89200023] = {id = 89200023, name = "うぅ、もう誕生日が来ちゃったの？　他の子達の恋愛相談ばっかりで、自分のほうはまだ全然…リーダーは私を置いて抜け駆けしたりしないでよねっ！"}
, 
[89200024] = {id = 89200024, name = "誕生日くらい好きなもの買って、美味しいもの食べて…なんて、夢見てる場合じゃないわよね…。今日も貧しい子たちの為に、働くとするわ。さぁ行くわよ、リーダー！"}
, 
[89200025] = {id = 89200025, name = "誕生日プレゼントなら、ファンからたくさんもらえたっすよ。でも、まだ足りないっす。リーダーさんから祝いの言葉とプレゼントもらえたら…やっと満足っす♪"}
, 
[89200026] = {id = 89200026, name = "誕生日プレゼントには、もちろん宝石を…なんて、さすがに言わないですぞ。ただリーダーには、ジブンと宝石談議に花を咲かせてもらえれば、十分ですからな！"}
, 
[89200027] = {id = 89200027, name = "リーダー…誕生日、お祝いしてくれる…？　う、嬉しい…ありがとう…。私、友達とお誕生日会するのが、夢で…あぅ、えっと…誘ったら、来て、くれる？"}
, 
[89200028] = {id = 89200028, name = "リーダーくん、今日は時間あるかな？　自分に誕生日プレゼントを買おうと思ってね、ついでにリーダーくんとデートでもしようじゃないか♪"}
, 
[89200029] = {id = 89200029, name = "私は大人ですから、誕生日プレゼントは結構ですっ。…でも、もう用意してもらったなら、仕方がないので受け取りますっ。…えへへ、ありがとうございます。"}
, 
[89200030] = {id = 89200030, name = "うぇひひ、ベロちゃん絶好調なのれす～。リーダーさんもベロちゃんのお誕生日、お祝いしてくれるれすかぁ～？　では、プレゼントはアロエさんの毒薬をくらさいれす～！"}
, 
[89200031] = {id = 89200031, name = "あら～、私の誕生日をお祝いしに来てくださったんですか～？　ありがとうございます～。どうやら今日は、試練よりも祝福を得られる日だったみたいですね～ふふ～。"}
, 
[89200032] = {id = 89200032, name = "わぁ、リーダーさんにお祝いしてもらえるなんて思ってなかったから、嬉しいですっ…！　えへへ、なんだか胸のあたりがあったかい気がしますっ…。"}
, 
[89200033] = {id = 89200033, name = "今日は、ボクの誕生日なんです。だからリーダーさん、その…どうか「おめでとう」って言ってくれませんか…？　あなたに、お祝いしてもらいたくて…ダメ、ですか？"}
, 
[89200034] = {id = 89200034, name = "…ふあぁ～。今日の占いは、お休みだよぉ～。誕生日はゆっくりしたいしぃ…。…ふふ。アナタも一緒にゆっくりするなら、隣においで？"}
, 
[89200035] = {id = 89200035, name = "誕生日なんて、大した意味はないだろう。ひとつ歳を重ねただけだ。…だが、祝ってくれるなら、礼は言う。その…ありがとう。"}
, 
[89200036] = {id = 89200036, name = "はぁーっはっはっは！　聞け、闇の同志よ！　今日は冥界の堕天使たる我輩の生誕祭だ！　さあ、崇め称えよ！　そして、遠慮なく我輩に貢物をするがいい！"}
, 
[89200037] = {id = 89200037, name = "な、なんだよ、リーダーも祝ってくれるのか？　…いつもは、家族ばっかりと祝ってたから…なんか、むずむずするっつーか…とにかく、ありがとな。"}
, 
[89200038] = {id = 89200038, name = "誕生日祝われるのは、なんだかくすぐったいな…。欲しいものは特に浮かばないが…そうだな、リーダーの直筆の手紙をくれないか？　きっと、良い記念になる。"}
, 
[89200039] = {id = 89200039, name = "今日は俺が生まれた日だからな、メイがパーティーを開いてくれるんだってさ！　リーダーも来いよ、美味いご馳走がいっぱい食えるからな！"}
, 
[89200040] = {id = 89200040, name = "誕生日をお祝いしてくれるなんて…私はリーダーさんがそばにいてくれるだけで十分です。今日も、明日も、明後日も、それからこの先もずーっと…永遠に…"}
, 
[89200041] = {id = 89200041, name = "お誕生日はね、みんなに優しくしてもらえる日なの。だからね、リーダーにはおめでとうって言ってほしいし、いっぱいなでなでしてほしいな。えへへ。"}
, 
[89200042] = {id = 89200042, name = "やあ、お姉さんの誕生日だとわかってて会いに来てくれたのかな？　ふふ、そうでなくとも嬉しいよ。今日は少し冷えるし…お姉さんと、身を寄せ合おうじゃないか。"}
, 
[89200043] = {id = 89200043, name = "おかしいわね、予定だと今頃は金銀財宝ざくざく囲まれて誕生日を迎えてたはずなのに…でも、今から見つければ大丈夫よね！　さぁリーダー、宝探しへ行くわよ！"}
, 
[89200044] = {id = 89200044, name = "あー、リーダー。今日誕生日なんだけど、なーんにも予定なくて暇してんの。だからさー、ケーキ買ったりアイリス誘ったりして、家でパーティーでもしよー？"}
, 
[89200045] = {id = 89200045, name = "誕生日にあなたと過ごせるだなんて、こんな幸せ、他にありません…♪　今日は一日中、ラヴィとベッドの中でいちゃいちゃしましょうね…？"}
, 
[89200046] = {id = 89200046, name = "主人が従者の誕生日を祝うなど不要なこと…。ですが…お気持ち、大変うれしいです。ありがとうございます、リーダー。あなたが主人で、本当に良かったです。"}
, 
[89200047] = {id = 89200047, name = "別にいつもと変わりないわ。でも、あなたが祝ってくれるというなら…今日一日、私に付き合って。そしてとんでもなく危険なところへ行って、楽しみましょう…？"}
, 
[89200048] = {id = 89200048, name = "今日がなんの日か、知らないフリはなしだよ。てわけで誕生日プレゼントに、新作ゲーム機と新作ゲームちょうだい。あ、あとプレイ中は肩のマッサージもよろしく。"}
, 
[89200049] = {id = 89200049, name = "おや、誕生日を祝ってくれるとは…やはりリーダー殿は、気遣いができる御人でござる。ふふ…贈り物よりも、やはりその気持ちが何より嬉しいものでござるな…"}
, 
[89200050] = {id = 89200050, name = "ふふ、これまでいろんなプレゼントをもらいましたが…やはり「おめでとう」という言葉が、何よりも嬉しいですね。お祝い、ありがとうございます♪"}
, 
[89200051] = {id = 89200051, name = "ふふっ。未来の歌姫の誕生日を祝えること、喜ぶといいわ！　景気づけに、新曲でもいっちゃおうかしらっ？　リーダー、よーく聞いてなさいよーっ！"}
, 
[89200052] = {id = 89200052, name = "今日はパロネスのお誕生日だぞー！　パーティーしてご馳走食って、ケーキに立てたローソクの火を消すんだ！　リーダーは、プレゼントよこせーーーっ！"}
, 
[89200053] = {id = 89200053, name = "誕生日なんて意味はないのに…あなたは私よりも、ずっと嬉しそうな顔をするのね。…本当に、変なアンレッドだわ。嬉しい、なんて…絶対に言ったりしないから。"}
, 
[89200054] = {id = 89200054, name = "今日はなんだか特別な日だったような気がしているんですが…なんでしたっけ？　リーダーさんはわかりますか？　うーん、うーん…ああ、私の誕生日でした。"}
, 
[89200055] = {id = 89200055, name = "今日は誕生日だけど、やっぱり節約のためには派手に祝えないかなー。あ、でもリーダーが食事をおごってくれるって言うなら、甘えさせてもらうけど…どうかな♪"}
, 
[89200056] = {id = 89200056, name = "リーダーさんにもお祝いしてもらえて、いつもの誕生日よりずっと嬉しいですっ…。大女優になれるように、またハミィとふたりで頑張りますね。"}
, 
[89200057] = {id = 89200057, name = "ふふっ、今日はとっておきの服でお誕生日を祝わなくちゃ！　あ、もちろんリーダーにも思いっきりおめかししてもらうから♪　覚悟しててね！"}
, 
[89200058] = {id = 89200058, name = "今日の主役、ロールが来たよー！　えへへっ、リーダーにお誕生日おめでとうって言ってもらえるの、すっごくすっごく嬉しいなっ！　今日はいーっぱい構ってねー！"}
, 
[89200059] = {id = 89200059, name = "誕生日はいつもお腹いっぱい、まんじゅうを食べるって決めてるんだ～。だからリーダー、おごってくれる？　…まぁ、今までお腹いっぱいになったことないんだけど。"}
, 
[89200060] = {id = 89200060, name = "誕生日プレゼントは、クリスマスプレゼントと同じぐらい素敵なものよね。でもいいの、お祝いの言葉さえもらえれば、私はいつもより速く走れる気がするから♪"}
, 
[89200061] = {id = 89200061, name = "誕生日か。ペンデュラムの構成員達も毎年、よく祝ってくれる。…リーダーとプデルも祝ってくれて…父がいなくとも、私は寂しくないな。"}
, 
[89200062] = {id = 89200062, name = "マフィアだから、誕生日とか気にしないほうがそれっぽいかなって思うんですけど…あとでケーキでも買いに行って、クーニャちゃんと食べますね。えへへ…"}
, 
[89200063] = {id = 89200063, name = "えへへ、今年もレイナちゃんにお祝いしてもらえちゃった♪　レイナちゃんのそばにいられることが、最高のプレゼントなんだから気にしなくていいのに～♪"}
, 
[89200064] = {id = 89200064, name = "ふふ、毎年家族とタネハとパーティをしてたんですが…今年はもっとたくさんの人に祝ってもらえてうれしいです。でも、あなたのお祝いの言葉は…やっぱり特別です。"}
, 
[89200065] = {id = 89200065, name = "某の生まれた日など、どこで知ったのやら…。ふ、親以外でこうも祝う者がいるとは、思う以上に嬉しいものだな。こちらこそ、祝ってくれたことに感謝しよう。"}
, 
[89200066] = {id = 89200066, name = "はふぅ…夏は暑いので、あんまり得意じゃないですが…。誕生日だけはやっぱり…ちょっぴり浮かれて、元気になっちゃいますね…えへへ。"}
, 
[89200067] = {id = 89200067, name = "誕生日かぁ…嬉しいけど、また今年も敏感なままで…うぅ、今年はせめてちょっとぐらいマシになるといいけど…リーダーも、協力してねっ！"}
, 
[89200068] = {id = 89200068, name = "今日、ワタシ、生まれた。だから、お祝いする、らしい…。みんな、準備してる。ワタシ、何すればいい？　リーダー、一緒に、暇する。"}
, 
[89200069] = {id = 89200069, name = "今日は、わたくしの誕生日なのです。いろんな方がプレゼントを持ってきてくださるのですが…リーダー様もひとつ、お祝いしていただけますか？　ふふ。"}
, 
[89200070] = {id = 89200070, name = "人間の誕生なんて、大地や海から見ればちっぽけなもんだ。…けど、誕生日を覚えていたことには礼は言っとく。…一応だからな。"}
, 
[89200071] = {id = 89200071, name = "姉はいつも変な祝い方しかしてくれないので…普通に祝ってもらえて、嬉しいです。…はっ！　でももしや、私を油断させていやらしいことを…！？　変態っ！"}
, 
[89200072] = {id = 89200072, name = "平和な誕生日だね。…ああ、決して嫌ではないよ。ひりつくよう命のやりとりは、こうした日常があってこそ映えるものさ。まぁ…物足りなくはあるけれど、ね。"}
, 
[89200073] = {id = 89200073, name = "リーダー、今日は海に名をとどろかすアタシっちが主役の日だぞ！…おいおいまさか知らないとか言わないよな？アタシっちの誕生日だぞ！ほら、祝ってくれよ！"}
, 
[89200074] = {id = 89200074, name = "城でペットたちと過ごしてた頃は、また誰かに誕生日を祝ってもらえるとは思ってもいなかったな…。リーダー、プライドに誘ってくれたこと、感謝している。"}
, 
[89200075] = {id = 89200075, name = "ああ、感動です…！　私、また誕生日まで生き延びられました！　はっ、で、でもっ、もしかして今日が命日に！？　そ、そんなの嫌ですー！　リーダーさん、助けて！"}
, 
[89200076] = {id = 89200076, name = "誕生日におめかししたり、お花もらったり、素敵なディナーなんかに行ったりするの、憧れるよねー！　…リ、リーダーとそういうことできたら…いいなー、なんて…"}
, 
[89200077] = {id = 89200077, name = "誕生日…ああ、そういえばそうね。もうずっと祝ってくれる人がいなかったから忘れていたわ。…誕生日を祝ってもらえるって、こんなに嬉しいことなのね。"}
, 
[89200078] = {id = 89200078, name = "ショウ姉さまから贈り物をいただけただけでも十分ですが…リーダーにリン姉さままで、祝っていただいて…私は幸せ者ですね。"}
, 
[89200079] = {id = 89200079, name = "あたしの誕生日ー？　あ、本当だー。えへへ、ありがとー。リーダーが覚えててくれたのすっごく嬉しいなー。来年も覚えててくれるー？"}
, 
[89200080] = {id = 89200080, name = "誕生日なんて、貴重な十代が過ぎた証明に過ぎないと思うけど…君が祝ってくれるなら、そう悪くはない日になったよ。ありがとう。"}
, 
[89200081] = {id = 89200081, name = "誕生日が来るたび、子供のころはこんな大人になるとは想像できてなかったな、なんて思うんです。…でも、昔想像したより、今のほうがずっと幸せですね…ふふ。"}
, 
[89200082] = {id = 89200082, name = "え？今日ってあたしの誕生日なんだ？そういえばそうだったかも？ねー、リーダーお祝いしてよ。…え。ほんとに？なにする？どこいく！？"}
, 
[89200083] = {id = 89200083, name = "今日はこの女神ピース様に貢物ができる光栄な日だぞっ！さぁさぁ、遠慮なくドーンとプレゼントをよこしなさいっ！…エヴァンテ先輩には秘密で！"}
, 
[89200084] = {id = 89200084, name = "うちの誕生日、知ってはったんどす？　リーダーはんはなんでも知ってはるなぁ。ほんなら、きっとえらいええプレゼントをくれはるんやろなぁ…期待しますえ♪"}
, 
