[97010010] = {id = 97010010, name = "生活のためには、お金をたっぷり稼がないと。リーダーさんもそう思うでしょ？"}
, 
[97010011] = {id = 97010011, name = "プライドと受付嬢の掛け持ちは、たしかに疲れるわ…でもその代わり、給料は２倍になるじゃない？　そう考えるとお得だわ♪"}
, 
[97010012] = {id = 97010012, name = "リーダーさん、今日も頑張ってるわね。良ければ、私がリラックスさせてあげましょうか？ もちろん、有料サービスだけど…リーダーさんだし、特別に値引きしてあげる♪"}
, 
[97010013] = {id = 97010013, name = "報酬が豪華なクエストは、まるで黄金の山ねぇ。うちのプライドも強くなったら、ぜひ挑戦したいわ♪"}
, 
[97010014] = {id = 97010014, name = "あらあら。私の情報は高いわよ？　…でも、初回特別サービスにしてあげる♪　私はホルスタイン・フリーシアンのレッドだから、ときどき反芻するの。リーダーさんも興味あるかしら♪"}
, 
[97010020] = {id = 97010020, name = "リーダー、疲れているのなら…気分転換に散歩でもどうかしら？　その…私も付き合ってあげてもいいのだけど。"}
, 
[97010021] = {id = 97010021, name = "はぁ～～このぬいぐるみ、すっごく可愛い…って、リーダー！？　いつからそこに…い、今のは、えっと…！"}
, 
[97010022] = {id = 97010022, name = "可愛いものには、その…ちょっと気を抜いたときに癒されるだけ！　だから別に、大好きとかじゃなくてっ…"}
, 
[97010023] = {id = 97010023, name = "最近また、エイプリルに振り回されがちなの…もっと特訓が必要ね。"}
, 
[97010024] = {id = 97010024, name = "私はジャーマン・シェパード・ドッグのレッドよ。あなたの指示を信じて、共に戦ってみせるわ。"}
, 
[97010030] = {id = 97010030, name = "リーダー、お昼寝したいの？　じゃあエイプリルちゃんもゴロゴロしちゃおっと！"}
, 
[97010031] = {id = 97010031, name = "し～。こっそり教えちゃうけど、オリヴィアちゃんはああ見えて可愛いものに弱いんだ。 あははは、可愛いでしょ！"}
, 
[97010032] = {id = 97010032, name = "エイプリルちゃん、すっごく暇だから～…ねぇリーダー、誰かにいたずらしに行こう！　ほらほら、オリヴィアちゃんがいないうちに早く早くー！"}
, 
[97010033] = {id = 97010033, name = "リーダーにいたずらの極意を教えてあげる。何よりも大事なのはね…素早い逃げ足、だよ！"}
, 
[97010034] = {id = 97010034, name = "アビシニアンキャットって、すっごく賢いんだよ。つまりエイプリルちゃんは…天才ってこと！"}
, 
[97010040] = {id = 97010040, name = "リリーは正義の味方になりたいんですっ！　リーダーさんもとっても頑張ってますし…リリーも負けていられませんっ！"}
, 
[97010041] = {id = 97010041, name = "リリーは騎士団に入るつもりでしたが、今思えば…このプライドに入れて、とっても良かったですっ！"}
, 
[97010042] = {id = 97010042, name = "リリーにできることがあれば、なんでも言ってください！　皆さんのお役に立てたら、リリーはとっても嬉しいのでっ！"}
, 
[97010043] = {id = 97010043, name = "お兄ちゃんやパパ、今ごろ何してるのかな。…よぉし！　リリーも負けないよう、今日も張り切っていきますよーっ！"}
, 
[97010044] = {id = 97010044, name = "サバクツノトカゲのレッドとは言え、血を吐くと引かれちゃうんですっ。…でもリリーも頑張ってるんですっ！　だって、目から血を吹き出してないんですよっ！？"}
, 
[97010050] = {id = 97010050, name = "本なら、ここにいくらでもある。読みたいなら…貸してもいい。"}
, 
[97010051] = {id = 97010051, name = "命をかけても…この大図書館を守る。それが、祖母との約束だから。"}
, 
[97010052] = {id = 97010052, name = "この大図書館には、生まれてからずっといる。本があれば、退屈じゃない。"}
, 
[97010053] = {id = 97010053, name = "エイプリルみたいな遊び人じゃないから、このままで構わない。…私は本が読めれば、それでいい。"}
, 
[97010054] = {id = 97010054, name = "私のレッドはアブラコウモリ。太陽が嫌いで、樹洞と岩の隙間が好き。それから、東の国に生息していたらしい。…他に質問は？"}
, 
[97010060] = {id = 97010060, name = "わ、わたしは、本当になんにもできないですが…か、肩を揉むぐらいだったら、大丈夫だと思うので…たぶん、きっと…"}
, 
[97010061] = {id = 97010061, name = "じ、自由にしていいんですか…？　は、初めて言われた言葉です…。ず、ずっと今まで、誰かに言われて働いてきたので…その…ど、どうしたらいいんでしょうか…？"}
, 
[97011011] = {id = 97011011, name = "地味なのはもう嫌なんですっ！　もっと注目されたいですっ！　私が主役になれるお仕事をたーっくさんください！"}
, 
[97011012] = {id = 97011012, name = "あ、リーダーさん！　今日そちらから挨拶してくれましたね、今後もその調子でお願いします！"}
, 
[97011021] = {id = 97011021, name = "暇だからと言って、ここで時間を浪費するよりクエストをこなしたほうがいいですよ！　リーダーとして、もう少し頼れるところを見せてくださいです！"}
, 
[97011022] = {id = 97011022, name = "リーダーが店の手伝いをしてくれるのは、とても助かるですが…恩を着せたとは思わないことです！　給料はちゃんと払うですからね！！"}
, 
[97011031] = {id = 97011031, name = "ノンノも、リーダーみたいな癒しの力が欲しいんだ。そしたら、もっとたくさんの人を助けられて、みーんな癒せるよねっ。えへへ！"}
, 
[97011032] = {id = 97011032, name = "ノンノの家の温泉はすごいんだよ！　疲れが取れるし、病気も治せちゃうんだ！　後でリーダーも入ってね！"}
, 
[97011041] = {id = 97011041, name = "急ぎの原稿が多すぎて、もう手が回りませんよー！　リーダー、どうか手伝って下さい！　原稿料なら分けますからー！"}
, 
[97011042] = {id = 97011042, name = "いやいや、私は『災いを呼ぶ死神』なんて呼ばれていますけど、 記者ですからね。 大変な場所に首を突っ込むのが仕事なんです！ あ、リーダーさんもこういう仕事に興味ありますか？"}
, 
[97011051] = {id = 97011051, name = "もし幸運の女神がいてくれたら…ギャンブルで楽して大儲けできるのに！"}
, 
[97011052] = {id = 97011052, name = "今回は運が悪かっただけです、次は…次こそは絶対勝ちます！　リーダー、アタシが勝ったらおごり――いえ、オヤツをちょっとだけ分けてあげますね！"}
, 
[97011061] = {id = 97011061, name = "私は戦うことにはあんまり自信はないけど…料理なら任せて。リーダーが食べたい物を教えてくれれば、ちゃんと作ってみせるから！"}
, 
[97011062] = {id = 97011062, name = "ミエルは私の料理を気に入ってくれてるんだ。でも、シエラグルメ以外に限るんだけどね。１回は食べてくれたんだけど、その後は全然食べてくれなくて…残念…"}
, 
[97020011] = {id = 97020011, name = "海の冒険は、普通の冒険よりきっとずーっと楽しいぞ！　だからお金をためて船を買ったら、すぐに出発だ！"}
, 
[97020012] = {id = 97020012, name = "すごい航海士になるためには、まず自分の船を持つこと！　だからエマはちゃんと働くんだ、一日も早く船を買えるようにな！　リーダーも協力してくれるよなっ！"}
, 
[97020013] = {id = 97020013, name = "ここは暖かいなー、エマの生まれた場所とは全然違うぞ！　エマなんて小っちゃい頃な、故郷で何回も凍死しかけたものだぞ！"}
, 
[97020021] = {id = 97020021, name = "リーダーは自分がどんなに大変でも、人を助けることばかり考えてるよね。本当に優しくて、強い人だなぁ…。私も見習わなくちゃっ！"}
, 
[97020022] = {id = 97020022, name = "冒険とか、家事とか、買い出しとか…何事も危険が伴っているからね、だから全部私に任せて！　リーダーは大人しく、私を待っててくれるだけでいいから。ね。"}
, 
[97020023] = {id = 97020023, name = "イヴリンちゃんもヌエットちゃんも、私を頼りにしてくれると嬉しいよ。リーダーも遠慮なく、私に甘えてね！"}
, 
[97020031] = {id = 97020031, name = "そんなに見つめられても、あたしはリーダーのために踊ってあげることしかできないよぉー。だってあたしは、踊り子だからねぇ。…あはは、でも一踊りする前に、まずはカンパーイ！"}
, 
[97020032] = {id = 97020032, name = "ぷは～！美味しいお酒って、 この世界でいっちばんいいものだよねぇ！ こんな素晴らしいものが飲める幸せに、カンパーイ！"}
, 
[97020033] = {id = 97020033, name = "お酒のために仕事しない…なんてありえないよぉ。むしろ、働いた後の一杯がたまんないだからぁ！　ま、とにかく仕事があったらいつでも呼んでねぇ。"}
, 
[97020041] = {id = 97020041, name = "『金の皿』会長として、美食を追うことは私の生涯をかけて成すべきことなんだ。だから美食に関わるすべての情報は、私に教えるように！"}
, 
[97020042] = {id = 97020042, name = "リーダーの世界には奇妙な食べ物が多そうだな…ふふ、再現するのが楽しみだ。リーダーも楽しみにしていてくれたまえ。"}
, 
[97020043] = {id = 97020043, name = "美食について、何か手がかりはあったか？　うっ…シエラグルメの話しかないなら…他の美食を探しに行こう！"}
, 
[97020051] = {id = 97020051, name = "はぁ、はぁ…女の子同士の友愛！　本当に最高っ――あっ！　リ、リーダーさん、いつからそこに…！"}
, 
[97020052] = {id = 97020052, name = "すみません…コーヒーをください…あとちょっとで…新作が完成するので…"}
, 
[97020053] = {id = 97020053, name = "エイプリルさんとオリヴィアさんは今夜、ふたり寄り添い、手を取って――あっ！　ま、また妄想を止まらず…すみません！"}
, 
[97020061] = {id = 97020061, name = "一人で穴を掘っているときは、誰にも邪魔されず、自由で、救われてなきゃだめなんです…"}
, 
[97020062] = {id = 97020062, name = "私、穴を掘るのが大好きなので…だからあの、もし穴を掘れる仕事があったら、ぜひ私に教えて下さいね。ふふ…"}
, 
[97020063] = {id = 97020063, name = "暑い時は、ひんやりとした穴の中にいるのが一番ですよ。今まで他の人を穴の中に招待したことはないですが、リーダーさんなら…"}
, 
[97020071] = {id = 97020071, name = "『人生は短い。だがその短い時も、有意義に過ごせなければ長すぎるだろう』リーダー、一緒に新たな物語を作りましょう。"}
, 
[97020072] = {id = 97020072, name = "『狂人と詩人と恋する者は、  想像力で満たされている』  リーダー、君の脚本の中で  僕はどんな役をしているのかな？"}
, 
[97020073] = {id = 97020073, name = "優秀な役者は、脚本を超えて演じられるものだよ。わかるでしょう、リーダー？"}
, 
[97020081] = {id = 97020081, name = "モグモグ～（食べる音）。うーん、美味しい～♪　あっ、これはつまみぐいじゃなくて、あくまで味見ですから、勘違いしないでくださいねっ！　…でももう一口、モグモグ♪"}
, 
[97020082] = {id = 97020082, name = "私は戦いは苦手なので…代わりに、皆にお菓子を作らせてください。これでみんなが幸せになってくれたら、私も幸せです～♪"}
, 
[97020083] = {id = 97020083, name = "うっ…体重がまた少し増えたような…。はぁ、このチョコを食べたら運動しなくちゃ…。でも運動した後のお菓子って、またたまらないんですよね～♪"}
, 
[97021011] = {id = 97021011, name = "新入り、お前はそれなりに優秀だが、もっと活躍しろ。なにせ俺様達は、ウィチタ団をもっと有名にしないといけないんだからな！"}
, 
[97021012] = {id = 97021012, name = "何、ショコラが作ったケーキだと？　俺様はそんなものは食べない！　…が、お前がどうしても俺様に食べてほしいというなら、食べてやろう！"}
, 
[97021013] = {id = 97021013, name = "よく覚えておけ、新入り。何か悩みや面倒事があったら、いつでも俺様のところへ来るんだ。それも団長のやるべきことだからな。"}
, 
[97021021] = {id = 97021021, name = "掃除をするときは根本から綺麗にしないと、掃除の意味がございません。ですから、わたくしはありとあらゆる手段を使うのです。"}
, 
[97021022] = {id = 97021022, name = "想像していたより悪くはないですが、まだまだです。ですから、掃除をしましょう。不要なものは処分して、大事なものは清潔に。さぁ、始めますよ。"}
, 
[97021023] = {id = 97021023, name = "申し訳ありませんが、 わたくしはメイドではありませんので、 掃除以外のことはいたしかねます。 …ですが、掃除に関わることならば、 どんな場所も、どんなものも、 あらゆる人も、片付けてみせましょう。"}
, 
[97021031] = {id = 97021031, name = "リーダーの世界の恋愛って、どんな感じなのかな？　リーダーも、その…恋愛経験はある？　ちょっとだけ、そっちの恋愛について教えてもらってもいい？"}
, 
[97021032] = {id = 97021032, name = "れ、恋愛についてはその…実は、まったく経験がないのっ…！　全部本を読んで仕入れた知識で…ほ、他の人に言ったりしないでよね！　万が一言ったりしないよう…きみを見張らせてもらうんだから！"}
, 
[97021033] = {id = 97021033, name = "これはずっと私が気に入っている恋愛小説なんだ。もう、とっても素敵なお話なの！　ああ、そういえばこの作者もレッドだって噂があるんだよね。いつか会ってみたいなぁ…"}
, 
[97021041] = {id = 97021041, name = "教会の食料がもうなくなったわ…もしかして、今月も雑草を食べなくちゃいけないわけ？　はぁ…子供達がパンを食べられるならいいけど…"}
, 
[97021042] = {id = 97021042, name = "あの不幸な人達のため、一緒にお祈りを…はぁ、やっぱりいいわ。祈っても何も変わらないもの。汗水垂らして働いて助けるほうが手っ取り早いわ。"}
, 
[97021043] = {id = 97021043, name = "私とモニカは幼馴染だけど…あいつ、わたしの顔を全っ然覚えてないのよ！　そもそも、わたしのこと覚えてたことなんてないかも…どっちにしても、酷すぎない！？"}
, 
[97021044] = {id = 97021044, name = "ふふ、メリノの毛並みってこんなに柔らかいのよ？　…売っ払ったら、2食分くらいのお金にならないかしら。でもレッドの毛なんて誰も買わないか…はぁ…"}
, 
[97021051] = {id = 97021051, name = "ああ〜♪　今日はいい天気っすね、最高の公演日和っす。"}
, 
[97021052] = {id = 97021052, name = "公演でティーネさんの曲を歌ったら、ファンたちから大好評っす！　やっぱりティーネさんはかっこいいっす！"}
, 
[97021053] = {id = 97021053, name = "自分は歌でお金をもらってるっすからね。 ファンに甘えたりしないで、 ずっと努力しなきゃいけないっす。"}
, 
[97021061] = {id = 97021061, name = "宝石の原石は、見つけただけでは意味がないですぞ。職人の手で磨いてやっと、輝き出すものですぞ！　…フフフ、まずはこうした基本から教えるですぞ！"}
, 
[97021062] = {id = 97021062, name = "ジ、ジブン、体力には自信がないですぞ…でも体力が必要なときには、体力がいらない他の方法で解決してみせますぞー！"}
, 
[97021063] = {id = 97021063, name = "宝石の輝きは、この世でもっとも美しいものですぞ！ 赤、青、緑、黄…  どんな色も、人々を魅了して離さないですぞ！"}
, 
[97021071] = {id = 97021071, name = "う…私はしゃべるのは苦手…だけど…でもクエストは、ちゃんとする、から…！　よ…よろしくお願いします！"}
, 
[97021072] = {id = 97021072, name = "さぁさぁ皆様、とくとご覧あれ！　コンチータの百発百中ナイフ飛ばし――あっ、あの、違…わ、私はただ、芸の練習を…"}
, 
[97021073] = {id = 97021073, name = "芸をしている時だけ、普通に喋れる、けど…い、今は芸をしてなくてもしゃべれてる、気がする…しゃべれてる、よね？"}
, 
[97021081] = {id = 97021081, name = "巷だとこう言われてるの。「ドミニクはとっても野性的なんだ」「それに大人の男性らしいよ」。ふふっ、面白いよね。だって少ししか当たってないんだもの。あたしはただの、大人の女性♪"}
, 
[97021082] = {id = 97021082, name = "ベルって子にはね、優秀なトレジャーハンターになる素質があるんだよ。今は経験や運がちょっと足りないだけ。いつかきっと、あたしよりも立派な成果を残すって信じてるよ。"}
, 
[97021083] = {id = 97021083, name = "この小っちゃい体も冒険じゃいろいろ有利だからね、満足してるよ！　ただしリーダーくん、あたしを子供扱いするのはだめだぞ！"}
, 
[97021091] = {id = 97021091, name = "リーダーはここで仕事していてください。 その間、私は配達に行ってきます。 では…って、配達鞄を忘れるところでした…！"}
, 
[97021092] = {id = 97021092, name = "私もう子供じゃありませんから。配達人の仕事もプライドの仕事も、ちゃんとやりきりますよっ。頼みたいことがあったら、遠慮なく言ってください！"}
, 
[97021093] = {id = 97021093, name = "私は一人前の配達人ですから、どんな危険な場所でも行けますっ！　…ただそういうところに行くと母が心配するので、あんまり行ったことはありませんが…"}
, 
[97021101] = {id = 97021101, name = "ベロちゃんの絵を理解してくれる人は少ないれすが…リーダーはちゃんとわかってくれる人でうれしいれす。リーダーと会えてよかったれす～。"}
, 
[97021102] = {id = 97021102, name = "絵を描いていると、何日も食べ忘れちゃうときがあるれすよ～。でも限界のときこそ、いいものが描ける気がするんれす～。気のせいれすかねぇ？"}
, 
[97021103] = {id = 97021103, name = "アロエさんがベロちゃんのために、特製の毒薬ドリンクを作ってくれたんれす～！　これで想像力がみなぎって、絵が描きたい放題れす～。アロエさんはとっても優しい人れすね～"}
, 
[97022011] = {id = 97022011, name = "リーダーさんはとっても面白くて、勇敢で…まるで英雄みたいです～。そんな方の傍にいられるなんて、吟遊詩人冥利に尽きますね～。"}
, 
[97022012] = {id = 97022012, name = "つらいことは全て、神様が与えた試練ですから～。どんな逆境も、詩にして歌えば大丈夫です～。"}
, 
[97022013] = {id = 97022013, name = "アイリーンさんは、私の詩を誰よりも綺麗に歌い上げてくれるんです～。だからこの詩も完成したら、アイリーンさんにも見てほしいですね～。"}
, 
[97022021] = {id = 97022021, name = "私は小さい頃から背が高くて、力もとても強かったんですけど…村の皆さんはずっと普通に接してくれたんですよ、えへへ。"}
, 
[97022022] = {id = 97022022, name = "皆さんが戦ってる姿を見ると「このままじゃいけない、私も強くならなくちゃ」って思うんですけど…どうすれば強くなれるんでしょう？"}
, 
[97022023] = {id = 97022023, name = "力が強すぎて、困ることが多いんです。料理しようとしてフライパンを壊しちゃったり、掃除しようとして家具を壊しちゃったり…もっと力加減を覚えなくちゃ。"}
, 
[97022031] = {id = 97022031, name = "リーダーさん、いつもお疲れ様です。ちょうど紅茶を淹れたので、少し休みませんか？"}
, 
[97022032] = {id = 97022032, name = "ペットとモンスターはどこから生まれたんでしょうか？　雰囲気は似てるけど…何かが違うんです。"}
, 
[97022033] = {id = 97022033, name = "ずっと人から隠れて、避けるようにしていましたから…  リーダーさんに出会えたこと、 本当に幸運だと思っています。"}
, 
[97022034] = {id = 97022034, name = "レッドの影響で、ちょっと偏食家なんです…でも紅茶やコーヒーは、いつも美味しく飲めるので、嬉しくて…♪　リーダーさんもいかがですか？"}
, 
[97022035] = {id = 97022035, name = "ワタシのこと、兎に見えましたか…？　たしかに、この耳でよく誤解されるんですけど…  実は、ウミウシのレッドなんです。 雌雄同体っていう、男でも女でもある  珍しい性質で…だから、実はワタシも…ふふ。"}
, 
[97022041] = {id = 97022041, name = "人は運命から逃れられないけど、変えることならできるから…もしかしたら、ワタシたちの選択は神による試練なのかもしれないね。"}
, 
[97022042] = {id = 97022042, name = "ワタシはどんな人にも嘘をつかないんだ。どんな結果が出ても、全部教えて、自分の未来を選んでもらうの。"}
, 
[97022043] = {id = 97022043, name = "占星術、水晶球、タロットカード、夢見…ワタシからしたら、どれでも一緒だよ。だってワタシの占いは、百発百中だからね。"}
, 
[97022051] = {id = 97022051, name = "怪我をしたやつがいたら、すぐに呼ぶんだぞ。…あと、もし出血が多かったら先に掃除できるやつを呼んでおくこと。よく覚えておくんだぞ。"}
, 
[97022052] = {id = 97022052, name = "私からしたら、便りがないことはいい便りだ。私の仕事がなくても、気にすることはない。"}
, 
[97022053] = {id = 97022053, name = "シャノンのやつと関わると面倒なことになるからな、リーダーも気をつけたほうがいい。さもないと、何が起きるかわからないぞ。"}
, 
[97022061] = {id = 97022061, name = "天界か…ああ、もちろん知っている。いつの日か我輩は、天界を占領してみせると決めているのだ…！　人間よ、そのときは貴様の同行を許可しよう！　…こ、こらリーダー、断ろうとするんじゃないっ！"}
, 
[97022062] = {id = 97022062, name = "ククク…この黙示録は我が生涯の記憶、そして多くの秘密が記されている…フフ、見てみたいだろう！　貴様が忠誠を誓うなら、見せてやらなくもないが！　どうだ、契約するか！"}
, 
[97022063] = {id = 97022063, name = "フン！　神もしょせん、我輩の故郷である天界の記憶を封印することはできないのだ！　ん…天界はどんなところかだと？　そ、それはもちろん…て、天界らしいところだ！"}
, 
[97022071] = {id = 97022071, name = "親父達を超えるためには、もっと勉強しないとな。だからよその国を巡って、いろんな武器を見てみたいんだ。"}
, 
[97022072] = {id = 97022072, name = "リーダーを見てると、たまに弟達のことを思い出すんだ。まぁ、あいつらはリーダーより厄介だけどな。"}
, 
[97022073] = {id = 97022073, name = "冷やかしならやめろよ。そんな目で見ても、絶対値引きしないからな。…あ、いや…そ、そんなに見つめるなよ…だ、だから、値引きはしないって…"}
, 
[97030011] = {id = 97030011, name = "勤勉はいいことだ。だが、適度な休憩はすべきだぞ。 目的を達成するまえに、リーダーが倒れては事だがらな。"}
, 
[97030012] = {id = 97030012, name = "戦いにはいつも悲傷と絶望が伴うものだ。 だが、平和を守るためには戦うしかない…  なんてことだ…！"}
, 
[97030013] = {id = 97030013, name = "そろそろ見回りに行かないとな。周りの町さえ守れなくて、どうやって世界を守るんだ？　さぁ行くとしよう、リーダー。"}
, 
[97030014] = {id = 97030014, name = "私たちの目的は遠い。だが、せめて近づけるよう、私にできることがあったら言ってくれ。貴君も手を貸してくれると嬉しい。"}
, 
[97030021] = {id = 97030021, name = "俺はつよくなる！すべての敵を倒せるくらいに！ そしたらお姫を守れるし、リーダーの役にも立つだろ！"}
, 
[97030022] = {id = 97030022, name = "何か問題があったら任せとけ！　リーダー、見てろ！　俺が拳でぜーんぶ解決してやる！"}
, 
[97030023] = {id = 97030023, name = "俺の装備は、古代から引き継がれた遺物で貴重なんだぞ！　昔、有り金はたいて商人から買ったんだ！　へへっ、いいだろー！"}
, 
[97030024] = {id = 97030024, name = "メイの護衛だからな、アイツを狙う変なヤツはみんな倒してやるんだ！　でもメイは優しいからな、面倒ごとは見せないようにしないと…！"}
, 
[97030025] = {id = 97030025, name = "えーっと、俺のレッドってなんだっけ…  あっ、アメリカン・ピット・ブルテリア！ おかげで俺の素早さや力が上がったんだよな！ …でも勢い余って物とか人にぶつかっちまう  ことがあるから、 メイと一緒に謝ったりするんだ…へへ。"}
, 
[97030031] = {id = 97030031, name = "リーダーさんの望みなら、誰でも助けに行きますし、誰でも殺してみせますよ。リーダーさんのためなら、何でもできますから…！"}
, 
[97030032] = {id = 97030032, name = "私にとってリーダーさんは大事な存在ですから、誰もリーダーさんのそばに近づけさせたくないんです。ねえ、リーダーさんなら、わかってくれますよね…？"}
, 
[97030033] = {id = 97030033, name = "リーダーさん、ここにある薬に触っちゃダメですよ。いえ、どうしても触りたいなら止めませんが…間違えてリーダーさんの指が腐り落ちたりしたら、大変ですから。…ね？"}
, 
[97030034] = {id = 97030034, name = "最近、いやらしい女がリーダーさんの周りをウロウロしていますよね。でもリーダーさんの心は変わらないって信じてますから。私だけいれば、満足してくれますよね…？"}
, 
[97030041] = {id = 97030041, name = "リーダーの願いは、フラワーの願いでもあるの。フラワー、良い子にするから。リーダーの話をよく聞いて、一生懸命頑張るから…リーダーのお願い、叶えようね。"}
, 
[97030042] = {id = 97030042, name = "リーダー、リーダー。あのね、早口言葉を練習したから聞いてほしいな。いくよー。なまむぎ、なまごめ、なまタバ…あう…舌、噛んじゃった…"}
, 
[97030043] = {id = 97030043, name = "フラワーね、とっても楽しいんだ。このプライドに入ってね、みんなと遊べて、リーダーがいて…すっごく『良かったー』って思うよ。ありがとう、リーダー。"}
, 
[97030044] = {id = 97030044, name = "フラワーのことね、いろんな知らない人が気に入ってくれるんだ。でも、他のところに行こうって思わないの。リーダーと一緒にいるのが、フラワーはいちばん楽しいから。だからリーダー、フラワーを置いていったりしないでね。"}
, 
[97030051] = {id = 97030051, name = "リーダー。私の遊びに付き合ってくれるなら、とっておきのご奉仕をしてあげよう。…って、どちらも一緒かな？"}
, 
[97030052] = {id = 97030052, name = "こちらのお仕事は終わったよ。他に命令はあるかな？　家事から戦闘まで…もちろん、夜のことも任せてくれ。"}
, 
[97030053] = {id = 97030053, name = "私の過去が知りたいなら…ほら、口を開けて。あーん。…ふふ、ケーキを食べるときのリーダーも愛らしいね。"}
, 
[97030054] = {id = 97030054, name = "リーダー、オリヴィアのお嬢を見なかったかな？ 探しているんだが、どこにもいなくてね。 やれやれ、彼女が怪我をしたら私が怒られてしまうよ。"}
, 
[97030055] = {id = 97030055, name = "私の能力はクロヒョウから生まれたんだ。三歩歩けば手が届く獲物なら、すぐに食い破れるよ。…ふふ、怖くなったかい？　"}
, 
[97030061] = {id = 97030061, name = "いつか私は必ずとびっきりのお宝を探し出して、お兄ちゃ…じゃなくて、兄のような素晴らしいトレジャーハンターになるんだから！　リーダーはそんな伝説の証人になれること、喜びなさい！"}
, 
[97030062] = {id = 97030062, name = "ドミニクがまた新しい冒険録を出したみたい…  ううっ、私ももっと頑張らなくっちゃ…！ よしっ。リーダー、今すぐ出発よ！ 彼女に早く追いつくためにね！"}
, 
[97030063] = {id = 97030063, name = "あんたはなかなか目が利くみたいね。だからその…私は孤高のトレジャーハンターだけど、特別に付き添うことを許可してあげる！　その代わり、途中で絶対にあきらめたりしないこと。いいわね！"}
, 
[97030071] = {id = 97030071, name = "今日は何して遊ぶー？ でも、もういろいろ遊んだしなー…  んー、まぁなんでもいっか。 食べ歩きとか行こー。"}
, 
[97030072] = {id = 97030072, name = "ヤッホー。リーダーあのさぁ、最近お金がなくなってきたんだー。だから雑用でもいいから、何か稼げる仕事を教えてー。"}
, 
[97030073] = {id = 97030073, name = "え、特訓しないのかって？　いやぁ、あたしは狙いを外したことなんてないからね。特訓なんて必要ないないー。"}
, 
[97030074] = {id = 97030074, name = "アイリス…って、なーんか聞いたことあるような…えーっと…ああ、あたしの幼馴染かぁ。あはは、ちゃんと覚えてるってばー。忘れるわけないって、もう、ねぇ。あはは…"}
, 
[97031011] = {id = 97031011, name = "最近のラヴィは、お仕事を頑張っていると思いませんか？ だからリーダーさん、ご褒美をくれますよね…？　ふふ、いつものところで…待ってますよ♪"}
, 
[97031012] = {id = 97031012, name = "リーダーさんのためならどんなことでもしますから、いくらでもご命令くださいね♪　"}
, 
[97031013] = {id = 97031013, name = "リーダーさんの瞳を見ていると、 なんだか体が熱くなるんです…  まるで世界のすべてが、灰になってしまいそうなくらい…"}
, 
[97031014] = {id = 97031014, name = "アロエという女は、よくわからない薬をいっぱい持っていて危険ですから！　リーダーさんは決して近づかないでください！　あとノエルとかにもっ！"}
, 
[97031015] = {id = 97031015, name = "寂しすぎると死んじゃうアナウサギですよ♪　だから、もっともっともーっと愛してくださいね、リーダーさん！　あ、もちろん今すぐでも構いませんよ♪"}
, 
[97031021] = {id = 97031021, name = "自分の意志など不要です。 戦いの中で邪魔になるだけですから。 あなたのご命令さえあれば、私には…充分です。"}
, 
[97031022] = {id = 97031022, name = "さあ、ご命令ください。私はあなたの武器です。 いつでもあなたの言葉に応えましょう。"}
, 
[97031023] = {id = 97031023, name = "どんなことでも、私にお任せください。主人の信頼を得られなければ、私が存在する意味はありませんから…"}
, 
[97031024] = {id = 97031024, name = "主人になっていただいて、ありがとうございます。どうかわたしを、ずっと従者としておいてください。もしあなたがいなくなったら…私は…"}
, 
[97031031] = {id = 97031031, name = "男はみんな可愛らしい馬鹿よね。少し飴をやるだけで言うことを聞くし、少し驚かせばとっても驚いちゃうんだもの。おかしいわ、あはは！"}
, 
[97031032] = {id = 97031032, name = "戦いだけが私を楽しませてくれるの。 強者と戦う快感、強者を倒す快感…  ああ、やめたくてもやめられないわ！"}
, 
[97031033] = {id = 97031033, name = "もっと強い相手を探してちょうだい。私の剣はもう待ちきれないの。…どんな敵だろうと私が全て倒してあげる！"}
, 
[97031034] = {id = 97031034, name = "料理だとか掃除だとかは苦手だから、全部誰かに任せてたの。でも、これからはあなたに任せればよさそうね。大丈夫、私は戦いでお返しするわ。"}
, 
[97031035] = {id = 97031035, name = "キングコブラの毒は、たった一口で象をも殺すの。ああ、安心して。あなたが死んだりしないよう、量は抑えてあげるから。"}
, 
[97031041] = {id = 97031041, name = "ふわぁ～眠すぎ…ちょっと夜更かししすぎたみたい。この世界の作家も、なかなか面白い本書くじゃない…"}
, 
[97031042] = {id = 97031042, name = "言っとくけど、あたしはこの世界の女神なの。だから、少しでも敬ってほしいわけ。つまり…あたしに冒険とか面倒くさいことさせないで。"}
, 
[97031043] = {id = 97031043, name = "全知全能の神は、頭の固い嫌なやつよ！　あたしをこの世界に来させたうえに冒険までさせるなんて…もはや労働基準法に違反してると思わない？"}
, 
[97031044] = {id = 97031044, name = "仲間になったら弱くなるなんて、ゲームでよくある設定でしょ？　だから、そんなあたしにクエストなんてやらせないでよ！"}
, 
[97031051] = {id = 97031051, name = "飯は喰ったか？腹が減っているわけではないが聞いてみた。——拙者の国元では挨拶のようなものでござるよ。"}
, 
[97031052] = {id = 97031052, name = "なにをたまげているでござるか？ 拙者は気配を消し、長らくここにいたでござるよ。 フッフーン、この技を学びたいでざるか？"}
, 
[97031053] = {id = 97031053, name = "高い木を知らぬか？高いところの空気を吸いたくての……しかして仮眠するでござる。"}
, 
[97031054] = {id = 97031054, name = "ゴクゴク～はぁーー　一緒にどうでござるか？ あはは、いやいや、酒にはござらんて。お茶でござる。"}
, 
[97031061] = {id = 97031061, name = "皆様のためなら、わたくしはなんでもしたいと思っているのです。皆様の幸せを見守り、皆様の期待に応えることこそ、わたくしの喜びなのです。"}
, 
[97031062] = {id = 97031062, name = "リーダー様も皆様もとても優しくて…いつも、心があたたかく感じるのです。わたくしもそんな存在になりたいと、心から思います。"}
, 
[97031063] = {id = 97031063, name = "ブリジットはわたくしの護衛ですが…それ以上に、良き友人だと思っています。リーダー様、どうかブリジット共々よろしくお願いいたします。"}
, 
[97031064] = {id = 97031064, name = "皆様と普通の仲間として仲良くなってこそ、 より多くのことが成し遂げられると思うのです。 ですからリーダー様も、 どうかわたくしと仲良くしてくださいね。"}
, 
[97030081] = {id = 97030081, name = "暇なら練習に付き合ってくれない？今日の特訓は…筋トレフルコースよ！"}
, 
[97030082] = {id = 97030082, name = "またオーディション不合格の連絡が来たわ…。どうせあたしなんか…歌手にもなれないポンコツよ…。うう…。"}
, 
[97030083] = {id = 97030083, name = "プライドの子達はよく喫茶店に遊びに来てくれるわ。あんたも…たまになら、顔出しに来てもいいけど？"}
, 
[97030084] = {id = 97030084, name = "今日もノエルと一緒にウェイトレスよ。あの子も本当に働き者よね。でも、疲れた顔なんてちっとも見せなくて…仕事が終わったら、ちょっとはいたわってあげようかしら。"}
, 
[97011071] = {id = 97011071, name = "暇だー！パロネスを構って、なでなでしろー！"}
, 
[97011072] = {id = 97011072, name = "にひひっ…今日の夕食に激辛調味料を仕込んだら…きっと、みんな驚くぞ…！"}
, 
[97011073] = {id = 97011073, name = "勉強は嫌いだー。でもリーダーが褒めてくれるなら、ちょっと頑張れる気がするぞ。"}
, 
[97011074] = {id = 97011074, name = "エイプリルはイタズラのシショーだ！それなりにソンケーしてるぞっ！"}
, 
[97030101] = {id = 97030101, name = "リーダーはレッドに優しいわね。あなたみたいな人ばかりだといいのだけれど。"}
, 
[97030102] = {id = 97030102, name = "ええと……今は何をしているの？リーダーは瞑想がお好きなのかしら。"}
, 
[97030103] = {id = 97030103, name = "にらめっこをご要望なら、他の子達に声をかけてちょうだい。"}
, 
[97030104] = {id = 97030104, name = "ひとりの時間が好きなの。だから、あなたも気にせず他の場所へ行くといいわ。"}
, 
[97030091] = {id = 97030091, name = "こんな姿で戦うと、相手は私を傷つけやすいと勘違いするみたい。ふふ、本当に無防備なのはどちらか…教えてあげなくちゃ。"}
, 
[97030092] = {id = 97030092, name = "リーダーものんびり日焼けタイム？それよりも…もっと熱くなれることをしましょう。とっておきの刺激的な戦いを…ね？"}
, 
[97030093] = {id = 97030093, name = "本当に強い者はどんな場所でもどんな格好でも、実力は変わらないわ。さぁリーダー、私の力を試してみない？"}
, 
[97030094] = {id = 97030094, name = "水中戦の経験は多くないけど、とっても好きなの。満足に息ができない中で、命を賭けて戦うあの瞬間…ああ、危険すぎて最高ね…！"}
, 
[97021121] = {id = 97021121, name = "もし暑いなら、ノンノがいつでも扇ぐよ！パタパタってするの、すっごく上手なんだからっ。"}
, 
[97021122] = {id = 97021122, name = "この前ね、シエラさんが夏バテ解消用の料理を作ってくれたんだ！ちょっと酸っぱくて、とっても美味しかったよ～！"}
, 
[97021123] = {id = 97021123, name = "海を見ながら入れる温泉も気持ち良さそうだなぁ。海水浴で疲れたみーんな、きっとゆっくりできて癒せるよねっ！"}
, 
[97021124] = {id = 97021124, name = "お日様の下にずっといるとね、だんだんお肌がピリピリってなっちゃうんだよ。リーダーも日焼けのしすぎには気をつけてね！"}
, 
[97021111] = {id = 97021111, name = "あなたのお名前はもちろん、覚えていますよ。えーっと…なんでしたっけ？"}
, 
[97021112] = {id = 97021112, name = "こんなに私を見つめるということは…リーダーさんも、なかなか人の顔を覚えられないタイプですか？"}
, 
[97021113] = {id = 97021113, name = "記憶に残るような刺激的なことには惹かれますが…こうしたのんびりとした時間も、大事にしなくちゃですよね。"}
, 
[97021114] = {id = 97021114, name = "リーダーさんが素敵な一日を送れるよう、お祈りしますね。………あれ？今私、何のお祈りをしていました？"}
, 
[97022081] = {id = 97022081, name = "ひぃふぅみぃっと…むふふ、今日の報酬も悪くないっ♪　でも豪邸を手に入れるまではまだまだ遠いからね。ルルリカにはもっともーっと稼いでもらわないとー♪"}
, 
[97022082] = {id = 97022082, name = "ふんふふーん、今日のご飯は「その辺に生えてた草」だよー♪　こういう節約が、豪邸への一歩だからね。リーダーも豪邸が欲しいなら、草を食べるべし！　だよ。覚えときな♪"}
, 
[97022083] = {id = 97022083, name = "こういう時間もいいよねー。労働なんてほんと、するもんじゃないよ。でもあたしは豪邸のため…ちょっと休んだらお仕事再開かなー？"}
, 
[97022084] = {id = 97022084, name = "リーダーもあたしがルルリカを利用してるって思うかい？　…にしし、なんとでも思うといいよ。あたしとルルリカは一心同体、引きはがせるものならやってみな♪"}
, 
[97030111] = {id = 97030111, name = "あぅ…ど、どうしてリーダーさん、黙っているんですか…？　わ、わたし…そんな見つめられたら…死んでしまいそうですっ…"}
, 
[97030112] = {id = 97030112, name = "あ、あの…リーダーさんは何をしてるんですか？　わたしはその…台本を読んでるところで…。新しい舞台は大変ですけど、わたしじゃない誰かになれる喜びは…何物にも代えがたいです。"}
, 
[97030113] = {id = 97030113, name = "ハ、ハミィとこんなに離れているのは、あまりないので…いったい、何をしたらいいのか…。ええと、ええと…その…か、かくれんぼでもしてみますか…？"}
, 
[97030114] = {id = 97030114, name = "ふふ…さっき、とても素敵な物陰を見つけたんです…♪　じめじめしてなくて、人通りが少なくて、でも危なくなさそうで…。あ…気になるならリーダーさんにも、ご紹介しますね…♪"}
, 
[97030121] = {id = 97030121, name = "はあぁぁ…やっぱりミアのかわいさは世界一なんですけど…！リーダーもそう思うでしょ？でしょ？"}
, 
[97030122] = {id = 97030122, name = "うちのプライドにはかわいい子が多くて、ミアのオシャレ魂がうずいちゃうんですけど…！コーデし放題って感じ！ふふふ…！"}
, 
[97030123] = {id = 97030123, name = "むむぅ…リーダーをかわいくするには、どうすればいいんだろ。ネクタイ…それともリボン…？ね、実際に試させて！"}
, 
[97030124] = {id = 97030124, name = "こういうとき、リーダーはミアを構うべきだと思うんですけど！例えば、異世界の服とかコーデのお話するとかして！"}
, 
[97022091] = {id = 97022091, name = "いつもの服よりね、この服の方がいろんな人に慕われるんだぁ。教会の子供達に、信心深い人…それから…ふふ、あとは秘密～。"}
, 
[97022092] = {id = 97022092, name = "ハロウィンでお菓子を欲しがる子達のために、キャンディやクッキーを持ち歩いてるんだぁ。リーダーもおひとつ、どうかな～？"}
, 
[97022093] = {id = 97022093, name = "リーダー、のんびりしてたらもったいないよぉ。いたずらして、お菓子ももらって、楽しまないとぉ。ふふ。"}
, 
[97022094] = {id = 97022094, name = "ふふふ。ワタシ、占い師なのにシスターの格好しちゃってる～。でも、占いでも運命を大事にしてるから…きっと神様も許してくれるよねぇ？"}
, 
[97030131] = {id = 97030131, name = "ハロウィンというのは良い祭りでござるな。ただ仮装をしただけで、これほど菓子がもらえるとは。まぁ…時折、拙者を見るなり逃げられてしまうこともあるでござるが。"}
, 
[97030132] = {id = 97030132, name = "ぴょんっ、ぴょんっ…むぅ。キョンシーは両手を前に伸ばして、跳ねながら進むらしいでござるが…。これで戦うのは難しそうでござるな。"}
, 
[97030133] = {id = 97030133, name = "故郷で恐れられていた化け物に、自分からなるとは不思議なものでござる。ふふふ、これで里帰りでもしたらきっと誰もが驚くでござろうなぁ。"}
, 
[97030134] = {id = 97030134, name = "この季節には、キョンシーだけでなく様々な怪異が現れやすいと言う。リーダー殿も夜道には気を付けるでござるよ。"}
, 
[97030141] = {id = 97030141, name = "むぅ、リーダーに構ってもらえないよぅ。でも、ロールが目立ってないせいだよね。よーしっ…もっと目立って、もーっとリーダーに構ってもらわなくちゃ！"}
, 
[97030142] = {id = 97030142, name = "リーダー、今ロールのこと見てくれてるの？　やったぁ！　リーダーがたーっくさん見つめてくれたら、ロールはとーってもうれしいよ！"}
, 
[97030143] = {id = 97030143, name = "ロールは良い子だから、大人しくここでリーダーを待ってるよ！　待ってるけど…ねっ。あとどれぐらい待ったら、ロールのこと構ってくれるかなっ？"}
, 
[97030144] = {id = 97030144, name = "ふわぁ…今日もとってもいい天気で、お日様が気持ち良くって～…リーダー、一緒にお昼寝…しよ…すや…すや…"}
, 
[101100101] = {id = 101100101, name = "お金さえあれば、できないことはないのよ♪　私はシャノン。クエスト受付嬢よ、ぜひ覚えてちょうだいね。"}
, 
[101100111] = {id = 101100111, name = "あら、このプレゼントには驚いたわ。リーダーさんは、やっぱり優しいのね。"}
, 
[101100121] = {id = 101100121, name = "私にも優しくしてくれるだなんて…そういうことをすると好きになっちゃうわよ、ふふ♪"}
, 
[101100201] = {id = 101100201, name = "オリヴィアよ、これからよろしくね。あなたの期待を超えられるよう、努力させてもらうわ。"}
, 
[101100211] = {id = 101100211, name = "こんな私…どう思うかしら？　少しは、大人らしくなった？"}
, 
[101100221] = {id = 101100221, name = "ここまで来れたのは、すべてリーダーのおかげよ。恩を返せるよう、もっと努力しなくちゃね。"}
, 
[101100301] = {id = 101100301, name = "やっほー！　みーんな大好き、エイプリルちゃんだよ！　ちゃんと覚えてくれた？　これからいっぱい遊ぼうね！"}
, 
[101100311] = {id = 101100311, name = "じゃじゃーん！　きらめくエイプリルちゃん、登場！　リーダーもこのかっこいい姿にメロメロ？　あははは。"}
, 
[101100321] = {id = 101100321, name = "今のエイプリルちゃん、もう完璧だよ！　騎士団にも絶対負けないぐらいね！"}
, 
[101100401] = {id = 101100401, name = "初めまして、リリーと申します！　やる気だけならリリー、誰にも負けませんので！！　リリーの実力にご期待くだ――ごふーっ！？"}
, 
[101100411] = {id = 101100411, name = "リーダーさんに認めてもらえて、リリーはとっても嬉しいです！　ありがとうございます、リーダーさん！"}
, 
[101100421] = {id = 101100421, name = "リリー嬉しいですっ、リーダーさんがリリーのことを大事にしてくれて…ああっ、嬉しすしぎて涙と血がっ…リリー、少しだけ泣いても…ううっごふ～っ！！"}
, 
[101100501] = {id = 101100501, name = "私はフランチェスカ。祖母は私を、『フラン』と呼んでいた。"}
, 
[101100511] = {id = 101100511, name = "ん…少しは、変化があったみたい。"}
, 
[101100521] = {id = 101100521, name = "あまり私に期待するな。…でも期待したいと言うなら、好きにすればいい。"}
, 
[101100601] = {id = 101100601, name = "イ、イヴリンです。わ、わたしは弱くて、不器用です、けど…雑用でも、なんでもやりますからっ…！　どうか、あなたについていかせてくださいっ…！"}
, 
[101100611] = {id = 101100611, name = "わ、わたしもリーダーさんみたいに、かっこよくなりたいですっ…リ、リーダーさんっ、いろんなこと、教えてもらえますか…？"}
, 
[101100621] = {id = 101100621, name = "つ、強さや自由を教えてくれたリーダーさんに、 とっても…感謝してますっ。 これからもわたしを、そばにいさせてくださいっ…  すこしでも、あなたのために…  働かせてくださいっ。"}
, 
[101110101] = {id = 101110101, name = "こんにちは、リーダーさん！　…ん？　あのー、もしもし？　聞こえてますか？　私はここにいるんですけどもっ！　と、ともかくっ…これからよろしくお願いしますっ！"}
, 
[101110111] = {id = 101110111, name = "も、もしかして…少しは普通じゃなくなりましたっ？"}
, 
[101110121] = {id = 101110121, name = "私っ、すっごい感じてますよ！　人気急上昇中～って！"}
, 
[101110201] = {id = 101110201, name = "こんにちは、雑貨商人のリッタです。子供扱いは決してしないよう、気を付けてくださいです。それから品のない商人とも一緒にしないように、です！"}
, 
[101110211] = {id = 101110211, name = "リッタのためにここまでのことをしてくれたこと、感謝しないわけにはいきません。リッタはしっかりお仕事で借りを返すですから！"}
, 
[101110221] = {id = 101110221, name = "リッタはもう成熟した大人です！　リーダーももっと私を信じて、いろいろ任せて構わないですからね！"}
, 
[101110301] = {id = 101110301, name = "温泉宿の娘、ノンノだよー！　たーっくさんの人たちを助けたいから、このプライドに入れさせてね！　頑張って戦うよーっ！"}
, 
[101110311] = {id = 101110311, name = "ノンノ、前より強くなれたよねっ？　リーダーのおかげだよ～！　ノンノ、もっともっと癒してあげるね！"}
, 
[101110321] = {id = 101110321, name = "リーダーがノンノにしてくれたこと、ぜーんぶ覚えてるし、すーっごく嬉しいよ！　リーダーがノンノを助けてくれたみたいに、ノンノもみーんなを助けるからね！"}
, 
[101110401] = {id = 101110401, name = "どうもどうも、ここに面白いネタがあると聞いてやってきましたよ！　ん、私ですか？　私はシルヴィ、ご覧の通り記者をやっています！　そんなことより…さぁ、取材を始めましょうー！"}
, 
[101110411] = {id = 101110411, name = "心も体も、もう勢いが止まりません！ さぁ、新たな現場にむかいましょう！"}
, 
[101110421] = {id = 101110421, name = "今ならどんなネタも、私から逃げられませんよ！　業界最強の情報通に、あなたを独占取材させてくださいっ！"}
, 
[101110501] = {id = 101110501, name = "は、はじめまして、ヌエットです…その、頑張りますけど…あんまり面倒な仕事は勘弁してください…！"}
, 
[101110511] = {id = 101110511, name = "できることが増えちゃいました！　ふふ…褒めちぎってくれてもいいんですよ？"}
, 
[101110521] = {id = 101110521, name = "今っ、これ、もはやっ…アタシがこの世でいちばん最強ですよ！　もう誰も怖くないです！　借金取りでさえも！"}
, 
[101110601] = {id = 101110601, name = "こんにちは、リーダー。私は食堂の娘、シエラだよ。お腹が空いたときは、いつでも美味しい料理を用意してあげるから、ぜひ来てね！そうそう、とっておきのシエラグルメもお楽しみに！"}
, 
[101110611] = {id = 101110611, name = "リーダーへのお礼は、やっぱり料理がいいよね。でも残念、今回はシエラグルメを用意できてないんだ…"}
, 
[101110621] = {id = 101110621, name = "今の私なら、絶対に最高のシエラグルメを作れるよ！　リーダーはかならず一番に味見してね！"}
, 
[101200101] = {id = 101200101, name = "ヤッホー、漁港から来たエマだぞ！　海のことならドーンと任せてくれ、エマは海のことならたーっくさん知ってるからなっ！"}
, 
[101200111] = {id = 101200111, name = "ヤッホー、リーダー！　今のエマが航海士になるため必要なのは、あとは船だけだぞ！"}
, 
[101200121] = {id = 101200121, name = "あははっ、エマは準備万端みたいだなっ！　さぁ。エマ船長は命令するぞ、今すぐ出航だ！"}
, 
[101200201] = {id = 101200201, name = "初めまして、コゼットって言います。何か困ったことがあればいつでも私を頼ってね。どんなことでも解決してみせるから！"}
, 
[101200211] = {id = 101200211, name = "みんなの力になりたいんだ。だから私、もっともっと頑張るから！　強くなるから！"}
, 
[101200221] = {id = 101200221, name = "リーダーの優しさを、自分の肝に銘じるよ！必ずみんなを守れるように！"}
, 
[101200301] = {id = 101200301, name = "あたし達の出会いを祝して、カンパーイっ！　…ぷは～っ！　あ。あたしはねぇ、ニコリータだよぉ。気持ち良ぉく酔っぱらうまで、帰さないよぉ～！"}
, 
[101200311] = {id = 101200311, name = "リーダー、いろいろよくしてくれてありがとうねぇ。 お礼に酒でもジュースでも、なんでもおごらせて。 そして…潰れるまで飲み続けましょ～！"}
, 
[101200321] = {id = 101200321, name = "この特別な日を祝うため、一緒に飲んで歌って踊りましょう！　一日中、祝杯だよぉ～！"}
, 
[101200401] = {id = 101200401, name = "美食を追い求める組織『金の皿』会長、ミエルだ。そちらの美食を是非とも教えてもらえるかな？　代わりにどんな危険なことでも私に任せてくれ。"}
, 
[101200411] = {id = 101200411, name = "完璧だ！　こんな時こそ、お祝いの豪華な料理は欠かせないものだ！"}
, 
[101200421] = {id = 101200421, name = "極上のご馳走をリーダーに振る舞うとしよう！　今日は特別に、私の手作りだ！　さぁ、テーブルの特等席へどうぞ！"}
, 
[101200501] = {id = 101200501, name = "わ、私はイルダと言います…売れてない作家をしてまして…。えっと、書いてるものはその、詳しくは言えないのですが…女の子と女の子が…ごにょごにょ…"}
, 
[101200511] = {id = 101200511, name = "ちょっと、ひらひらすぎる…うーん…  これは人の目を引きすぎません…？"}
, 
[101200521] = {id = 101200521, name = "私が書いた世界…リーダーさんにも、楽しんでもらえるでしょうか？"}
, 
[101200601] = {id = 101200601, name = "は、初めまして、フィナです！　あの、えっと……もうしばらく、穴にいてもいいですか？"}
, 
[101200611] = {id = 101200611, name = "こ、こういうのは…本当に恥ずかしいです…！"}
, 
[101200621] = {id = 101200621, name = "私に、こんな衣装は似合いますか…？　もし、ちょっとでも可愛く見えたら…いいな…"}
, 
[101200701] = {id = 101200701, name = "やぁ、親愛なるリーダー！　君に出会えたこの運命、神に感謝するよ。僕は今夜の主役ジュリアン、これからの舞台をご期待あれ！"}
, 
[101200711] = {id = 101200711, name = "『上辺は天使のようでも、人間の内側に何を隠し持っているか分からない』――なんてね、冗談だよ！"}
, 
[101200721] = {id = 101200721, name = "It is here, and I\'am the sun."}
, 
[101200801] = {id = 101200801, name = "こんにちは、リーダー♪　私はショコラ、パティシエです～。リーダーと皆のために、たーーーっくさん美味しいお菓子を作りますよ～！　えっと、まずは人の頭ぐらいある、おっきなキャンディーから…♪"}
, 
[101200811] = {id = 101200811, name = "お菓子を作るだけじゃ、みんなのためになりませんよね…戦うことも頑張ります～！"}
, 
[101200821] = {id = 101200821, name = "リーダーに認めてもらえたことが、 一番嬉しいです！でも甘えたりしませんよー♪ 戦うのもお菓子作りも全力でいきます！！"}
, 
[101211001] = {id = 101211001, name = "おおっ、リーダーじゃないれすか～。画家のベロニカ、ベロちゃんれすよ～。リーダーのために、この絵筆を振るいまくるれすよ～！"}
, 
[101211011] = {id = 101211011, name = "今のベロちゃん、とってもイイ感じれす～！　この感じで、リーダーの絵を描くれすよぉ～！"}
, 
[101211021] = {id = 101211021, name = "おおっ、今まで思いつかなかった絵が浮かんだれす～！　この絵が頭から逃げないうちに、描いて描いて描きまくらないとれすぅ～～！"}
, 
[101210101] = {id = 101210101, name = "俺様はウィチタ団団長のウィチタだ！　お前がここの団長か？　よし、それじゃあ今からお前を、俺様の部下にしてやる！"}
, 
[101210111] = {id = 101210111, name = "俺様はもっと強くなれる。そして、お前のために戦ってやろう。…くしゅん！　…なんかここ、寒くないか？"}
, 
[101210121] = {id = 101210121, name = "ウィチタ団は最強の組織であり、その団長である俺様は当然無敵だ！　どんなことでも全部俺様に任せろ！"}
, 
[101210201] = {id = 101210201, name = "あらゆる汚れを排除するためなら、どんな手段でも取りましょう。わたくしは掃除人のネネル。すべての汚れを見逃しません、掃除のことならお任せくださいませ。"}
, 
[101210211] = {id = 101210211, name = "何事も表面だけを整えても意味がありません。 ですからリーダー様、 まずは掃除に対する心構えからお教えしましょう。"}
, 
[101210221] = {id = 101210221, name = "掃除の特訓終了です。この成果、リーダー様に見せてさしあげましょう。"}
, 
[101210301] = {id = 101210301, name = "私はチェルシー。女の子たちから恋愛マスター、なんて呼ばれてるけど、本当は…ううん、なんでもないの。あなたも恋愛の悩みがあったら、私に言ってくれてもいいからね。仲良くしてくれると嬉しいわ！"}
, 
[101210311] = {id = 101210311, name = "調子に乗ったりしないってば。だって私は、経験豊富で落ち着いた大人だからね…ふふ。"}
, 
[101210321] = {id = 101210321, name = "リーダーにたくさん手助けしてもらったけど、私、上手くやれるかな…。…ううんっ、ここまで来たらやるしかないんだよねっ…！"}
, 
[101210401] = {id = 101210401, name = "わたしはシスターのアイリスよ。教会の子供達にご飯を食べさせないといけないの。だから、たくさん仕事をさせてちょうだい。"}
, 
[101210411] = {id = 101210411, name = "リーダーのこと、もっと手伝わせてちょうだい。もちろん、これも子供達のためよ！"}
, 
[101210421] = {id = 101210421, name = "お祈りするより、リーダーに頼ったほうがいいようね。リーダーのお恵みを大切にして、もっとたくさん人助けしてやるわ！"}
, 
[101210501] = {id = 101210501, name = "ラララ～♪　自分は天才歌姫アイリーンっす。あんたもアイリーンのファンっすか？"}
, 
[101210511] = {id = 101210511, name = "今回の演出のためにデザインしてもらった衣装っす！　どうっすか、可愛いっすか？　せっかくだから、回って見せちゃうっすよー！"}
, 
[101210521] = {id = 101210521, name = "リーダーは本当に最高のファンっすね！　お礼に、ぜひ歌わせてほしいっす！　自分には、これぐらいしかできないっすから…！"}
, 
[101210601] = {id = 101210601, name = "このキラキラとした輝き…もしや、ダイヤですぞ！？　おっと失礼…ジブンは宝石商人キキですぞ、よろしくですぞ～！"}
, 
[101210611] = {id = 101210611, name = "リーダーの優しさは宝石の輝きと同じく、人を感動させますぞ！"}
, 
[101210621] = {id = 101210621, name = "初めて宝石を見せてもらった時のような、 胸の高鳴り！ジブン、決して忘れませんぞ！ リーダーにもそんな経験をしてもらえるよう、 何でもしますぞー！"}
, 
[101210701] = {id = 101210701, name = "あ…えと…あの…私、コンチータ…芸をしていないと、人と上手く、話せなくて…努力はするけど、そのっ…わ、私、先に休憩してくるっ…！"}
, 
[101210711] = {id = 101210711, name = "む、胸のあたり…あたたかい感じが、する…。たぶん、リーダーの愛情、が…伝わってきたから…"}
, 
[101210721] = {id = 101210721, name = "この記念すべき一瞬のため、最高の感謝を伝えさせてください！　さぁ行きますよ、コンチータのナイフ飛ばし～！"}
, 
[101210801] = {id = 101210801, name = "あたしは冒険家ドミニク！　ふふっ。いろんな人がドミニクを男だって思ってるみたいだけど、間違えなくあたしが本物だからね！　よろしく！"}
, 
[101210811] = {id = 101210811, name = "リーダーくん、いろいろ世話をしてくれてありがとう。こういう成長ができるのは、ひとりの冒険じゃ味わえない醍醐味だ。"}
, 
[101210821] = {id = 101210821, name = "今のあたし、とっても素晴らしい冒険家になれたと思うの。だからリーダーくん、誰も行ったことのない場所へもっと行こう！"}
, 
[101210901] = {id = 101210901, name = "配達人アリシア、到着しました！　新人なのでご迷惑をかけるかもしれませんが、子供扱いは不要ですから。よろしくお願いします！"}
, 
[101210911] = {id = 101210911, name = "成長が早いですが、私はもっとできるはずです！　一緒に頑張りましょう、リーダー！"}
, 
[101210921] = {id = 101210921, name = "今の私は、皆さんと同じ大人ですから！　どんな配達でもクエストでも、任せてください！"}
, 
[101220101] = {id = 101220101, name = "リーダーさん、やっと見つけました～。これはきっと、神様のご加護ですね～。私はティーネ、吟遊詩人をしています～。どうか、よろしくお願いしますね～。"}
, 
[101220111] = {id = 101220111, name = "気遣ってくれてありがとうございます～。リーダーさんに出会えたことに、神様の慈愛を感じます～。"}
, 
[101220121] = {id = 101220121, name = "この恩はきっと返しきれませんが…  それでも、リーダーさんと一緒にいさせてください～。あなたの英雄物語を詩にして、 いつまでも歌われるようにしますから～。"}
, 
[101220201] = {id = 101220201, name = "プライドに入れてくれてありがとうございますっ。私はうっかり者だし、お役に立てるかわからないですけど…精一杯頑張るので、よろしくお願いします！"}
, 
[101220211] = {id = 101220211, name = "力がまた強くなったみたいですっ…。ちゃんと力加減できるかな…？"}
, 
[101220221] = {id = 101220221, name = "力の加減が上手くできるようになりましたっ。リーダーさん、私にもっとお手伝いをさせてください！"}
, 
[101220301] = {id = 101220301, name = "こ、こん…にちは。ワタシは、ノエルと言います。 今はペットの研究のために、 いろんなところに旅をしています。 …あ、あの、プライドに誘ってもらえて、 本当に嬉しいです。 これからよろしくお願いしますね。…えへへ。"}
, 
[101220311] = {id = 101220311, name = "いつもありがとうございます、リーダーさん。 私はきっとリーダーさんみたいな、 強くて努力も欠かさない人になります。"}
, 
[101220321] = {id = 101220321, name = "リーダーさんの優しさ、ちゃんと受け取りました。 …私も強くなって、 リーダーさんの気持ちに 応えてみせますっ。"}
, 
[101220401] = {id = 101220401, name = "星を読めば万物を知れて、占えば昔も今も読めるの…全ては運命の導きだからね、あなたとの出会いもわかってたよぉ。"}
, 
[101220411] = {id = 101220411, name = "これもワタシの運命だよ。でも導かれるままじゃなくて、リーダーにはお返ししないとね。"}
, 
[101220421] = {id = 101220421, name = "また私の占いじゃわからなかったコトが起きたね…リーダーと一緒にいると、楽しいことばっかりだぁ。"}
, 
[101220501] = {id = 101220501, name = "おまえがリーダーか。これから怪我をしたときには、私に任せておけ。…もちろん、そんな仕事はないほうがいいがな。"}
, 
[101220511] = {id = 101220511, name = "たくさんの患者を救えるのは悪くない。だが…怪我も病気も、もっと少なくあるべきだ。"}
, 
[101220521] = {id = 101220521, name = "ムチャをして怪我したやつらは、全員私に任せておけ。私がしっかり『治療』してやるからな。"}
, 
[101220601] = {id = 101220601, name = "まさか我輩を召喚する、勇敢な人間がいるとはな…！　我が名は冥夜の堕天使カノン！　ククク…貴様のその勇気、称賛してやろう！"}
, 
[101220611] = {id = 101220611, name = "光なき闇夜でさえ…この冥夜の力と影で、何もかも覆い隠してみせよう…！"}
, 
[101220621] = {id = 101220621, name = "堕天使の完全なる復活だ！　ククク…我輩がこの世界に破滅と恐怖をもたらしてみせよう！　アーッハッハッハッハー！"}
, 
[101220701] = {id = 101220701, name = "よう、リーダー。あたしは鍛冶師のユーナだ。まぁ、まだ新米だけどな。うちの店共々、よろしく頼む。"}
, 
[101220711] = {id = 101220711, name = "剣は鉄に火を入れて、ひたすら叩いてできるもんだ。今のあたしは、まだ火加減が弱いんじゃないか？"}
, 
[101220721] = {id = 101220721, name = "へへっ、あたしもちょっとは親父達に近付けてるみたいだな。"}
, 
[101300101] = {id = 101300101, name = "プライド加入を認めてくれたこと、感謝する。私の名はグローリアだ。人間とレッドの平和のためならば、どんな任務でもやらせてもらおう。よろしく頼む。"}
, 
[101300111] = {id = 101300111, name = "まだ油断はできない。私たちの力は発展途上で、できていないことも多い。…少しずつ、進んではいるがな。"}
, 
[101300121] = {id = 101300121, name = "まだまだだな。だが、私にできることは以前よりも多くなったと思う。…期待していてくれ、リーダー。"}
, 
[101300201] = {id = 101300201, name = "ブリジットだ！　難しいことはよくわからないけどな、悪いやつがいたら、とにかく俺に任せとけ！　安心しろ、何せ俺は強いからなっ！"}
, 
[101300211] = {id = 101300211, name = "これが特訓の結果か、いい感じだな！　俺はもっと強くなるために特訓しまくるぞ！"}
, 
[101300221] = {id = 101300221, name = "ふぅ…やっと特訓が終わったな。今の俺なら、どんな戦いでも楽に勝てる自信があるぜ！"}
, 
[101300301] = {id = 101300301, name = "ああ、やっと会えてうれしいです、リーダーさん…。私は薬師のアロエです。あなたのためなら私、どんな薬でも作りますから。痺れ薬に、眠り薬に、毒薬に…ふふ、いつでも声をかけてくださいね。"}
, 
[101300311] = {id = 101300311, name = "リーダーさんが優しいことは、もうずっと前から知っていますから。うふふふふ…リーダーさんを離しません…"}
, 
[101300321] = {id = 101300321, name = "ふふ。これでリーダーさんには、私一人いれば充分ですね。そろそろ、他の人にもそれをわかってもらわなくちゃ、うふふふふ…"}
, 
[101300401] = {id = 101300401, name = "フラワーだよ。フラワーはね、えっと…『よしよし』ができるよ。疲れたら、たくさん『よしよし』してあげるね。それから、お仕事終わったら、一緒に遊ぼうね。えへへ。"}
, 
[101300411] = {id = 101300411, name = "リーダーと一緒ならね、フラワーが今までできなかった、いろんなことができるって思うよ。だから、もっともっと、フラワーにいろんなことさせて、頼ってね。"}
, 
[101300421] = {id = 101300421, name = "ぜんぶ、フラワーに任せていいよー。フラワーがね、ぜーんぶ『よしよし』ってしてあげるからね。"}
, 
[101300501] = {id = 101300501, name = "サラだ。かわいいリーダーには特別に、この世界の楽しみ方をお姉さんが案内してあげようか。ふふ。"}
, 
[101300511] = {id = 101300511, name = "かわいいリーダーに頼られてうれしいよ、ふふ。リーダーが望むなら、お姉さんはいつでも助けに来よう。"}
, 
[101300521] = {id = 101300521, name = "これが君の望む姿なのかい？　ふふ。お姉さんは君を満足させてあげられたかな？"}
, 
[101300601] = {id = 101300601, name = "ごきげんよう、リーダー。私は孤高のトレジャーハンター、ベル様よ！　一応挨拶はしておくけど、私は今言った通り孤高なの。馴れ合おうなんて思わないことねっ！"}
, 
[101300611] = {id = 101300611, name = "いい感じね！　この勢いで、必ず世界中を驚かせるお宝を見つけちゃいましょう！"}
, 
[101300621] = {id = 101300621, name = "完璧ね！　これで最強のトレジャーハンターに、また一歩近づけたわ…！　ま、まぁ少しはあんたを褒めてあげるけど…調子乗らないのよ！"}
, 
[101300701] = {id = 101300701, name = "やっほー。あたし、モニカ。仕事は傭兵っていうか、えーっと…つまりは依頼されたら、誰でもぶっ飛ばすよーって感じかなー。"}
, 
[101300711] = {id = 101300711, name = "今までより遠くがはっきり見えるかも…うーん、ちょっとやる気出てきたし、何か仕事を任せてくれてもいいよー。"}
, 
[101300721] = {id = 101300721, name = "うーん、この新しい装備はなかなか悪くないねー。っていうか、最高かも。"}
, 
[101310101] = {id = 101310101, name = "あなたのラヴィがやってきましたよ♪　ふふ、この運命はずっと前から決まっていたこと…リーダーさんとの愛のためなら、この身を滅ぼしても構いませんっ！"}
, 
[101310111] = {id = 101310111, name = "リーダーさんの気持ちに応えるためなら、ラヴィはなんでもしてみせますっ！"}
, 
[101310121] = {id = 101310121, name = "リーダーさんが望むなら…あなたのために、この身の全てを捧げましょう！"}
, 
[101310201] = {id = 101310201, name = "あなたが、私の主人となる方でしょうか？　私はウルスラと申します。あなたのご命令ならば、この命いくらでも尽くしましょう。"}
, 
[101310211] = {id = 101310211, name = "従者として、あなたに信頼していただけたら…私は、より強くなれると思うのです。"}
, 
[101310221] = {id = 101310221, name = "あなたの意志は私が貫き、あなたの望みは私が叶えましょう。今の私は――決して、誰にも負けることはありません！"}
, 
[101310301] = {id = 101310301, name = "このプライドには強いレッドがたくさんいるのね。なんだか興奮してきたわ…ねぇリーダー、彼女たちと遊んできてもいい？　ああ、安心して。手加減はしてあげるから。"}
, 
[101310311] = {id = 101310311, name = "これだけ強くしてくれたんだから、これからも責任を持って、私を楽しませてちょうだい！"}
, 
[101310321] = {id = 101310321, name = "あら。この感覚はなかなか良いじゃない。今ならどんな相手だろうと、容易く倒せそうね。"}
, 
[101310401] = {id = 101310401, name = "初めまして、今後とも別によろしくしないで――ってあんた、あの時の…！　…いや、なんでもない。あんたとは間違いなく初めましてよ、あたしはエヴァンテ。じゃ、挨拶もしたし、そろそろ寝るから。"}
, 
[101310411] = {id = 101310411, name = "女神たるもの、力が回復しても冒険はするべきじゃない。"}
, 
[101310421] = {id = 101310421, name = "もし面白そうなことを見つけてくれたら、少しぐらいは手伝っても構わないけどね。"}
, 
[101310501] = {id = 101310501, name = "お初にお目にかかる。拙者、篠と申す者。拙者のことはあまり気にせずともよい。ふぅぅ。少々休める所はあるでござるか？"}
, 
[101310511] = {id = 101310511, name = "誠意をもって、誠の力を見せてしんぜよう！"}
, 
[101310521] = {id = 101310521, name = "友よ、ありがたき幸せ！ここからは拙者が勝利への道を切り開くでござる！"}
, 
[101310601] = {id = 101310601, name = "わたくしはメイと申します。この国のため、人類のため、そしてレッドのため…わたくしの力の限り、貢献させていただきます。よろしくお願いいたします。"}
, 
[101310611] = {id = 101310611, name = "わたくしを支えてくださって、ありがとうございます。まだまだ、わたくしは成長しますから…期待していただけると嬉しいです。"}
, 
[101310621] = {id = 101310621, name = "リーダー様のことも皆様のことも、 守れる強さを手に入れられたと思います。 ですから、わたくしを頼ってください。 かならず力になりましょう…！"}
, 
[101300801] = {id = 101300801, name = "あたしは未来の歌姫、リサよ。今はまだ下積み中だけど…いつか一流の歌手になってみせるわ。私が最高の歌を歌えるように、あんたも協力しなさい！"}
, 
[101300811] = {id = 101300811, name = "ち、ちょっとあんまり見ないでよ…！こういう綺麗な服を着るのって…慣れてないのよ！"}
, 
[101300821] = {id = 101300821, name = "リーダーがいたから、夢をあきらめずにここまで来れたの。これからも最高の歌姫を目指して、突っ走っていくわよ！"}
, 
[101110701] = {id = 101110701, name = "お前がパロネスのリーダーか！？パロネスに気に入られたいなら、いっぱい構って、いっぱい遊ぶことだな！"}
, 
[101110711] = {id = 101110711, name = "強くなってハデハデになったぞ！どうだ、リーダー。似合ってるか？"}
, 
[101110721] = {id = 101110721, name = "リーダーといると面白いことばっかりだ！これからも一緒に遊ぼうな！"}
, 
[101301001] = {id = 101301001, name = "ごきげんよう、私の名はシェリー。あなたがどんなことをするのか…楽しみにしてるわ。"}
, 
[101301011] = {id = 101301011, name = "レッドの未来のためなら……この力を、私は惜しまない。"}
, 
[101301021] = {id = 101301021, name = "あなたは弱いアンレッドだもの。だから……大人しく私に守られているといいわ。"}
, 
[101300901] = {id = 101300901, name = "夏の装いというのも…悪くないわね。さぁリーダー。私の興奮が冷めないうちに、たっぷり遊びましょう？"}
, 
[101300911] = {id = 101300911, name = "ふふ、あなたといる夏はこんなに刺激的なのね。熱くて、無防備で…体のうずきが止まらないの。でもまだ足りないわ。もっと、もっと…私を興奮させて？"}
, 
[101300921] = {id = 101300921, name = "私の欲望は海のように深くて…大きいの。けど、あなたなら…私の欲望を満たせる『はじめての人』になってくれるのかしら？これからも楽しみにしてるわ。"}
, 
[101211201] = {id = 101211201, name = "見て見てー！ノンノ、水着着たよーっ！リーダーにすっごく見て欲しかったんだ。一緒に楽しい夏にしようね、リーダー！"}
, 
[101211211] = {id = 101211211, name = "リーダーに優しくしてもらった後はね、いつもより海が綺麗に見える気がする。だからね、この夏はずーっと、海が綺麗に見えたんだ。…えへへ。ありがとう、リーダー！"}
, 
[101211221] = {id = 101211221, name = "夏が過ぎて、寒くなってきても…またノンノと一緒に遊ぼうね。えへへっ。約束だよ、リーダーっ！"}
, 
[101211101] = {id = 101211101, name = "はじめまして、私はツバキです。…あの、私達どこかでお会いしたことがありましたっけ？"}
, 
[101211111] = {id = 101211111, name = "前の私より可愛くなっていますか…？そうであればいいのですが…"}
, 
[101211121] = {id = 101211121, name = "たとえ記憶を失くしても、あなたと一緒にいた証があれば…それでいいんです。"}
, 
[101220801] = {id = 101220801, name = "やぁやぁ、何を隠そうあたしがルルリカ専属マネージャーのハーミットだよ。ルルリカが思いっきり稼げそうで、さっさと豪邸建てられそうなお仕事募集中ー♪　あ、でもエッチなのはダメだよ？"}
, 
[101220811] = {id = 101220811, name = "もうリーダーとルルリカがいれば、豪邸なんていらない…なんて言わないよ。あたしは、あんた達と一緒に欲しいものを何もかも手に入れてやる、それだけだ！　むふふ、覚悟しておきなー♪"}
, 
[101220821] = {id = 101220821, name = "リーダー、あんたって本当に最高だよ！　だから特別に、約束してあげる。いつか世界中にあるどこの家よりもすんばらしーあたしの豪邸ができたら…そこに住まわせてあげる。にしし、光栄でしょー？"}
, 
[101301101] = {id = 101301101, name = "ル、ルルリカと申します…。舞台女優をしていて…でも、あまり人前に出るのが得意でなくて…きゃっ…そ、そんなに見つめないくださいっ…"}
, 
[101301111] = {id = 101301111, name = "わたしを導いてくれるあなたの手が、これほどあたたかいことを…この先ずっと、忘れたりはしません。…ふふ。今のは台本じゃない、わたし自身の言葉…ですからね？"}
, 
[101301121] = {id = 101301121, name = "ルリはずっと「自分以外の誰か」になるため、演技してきました…。でも今…あなたの前では、ルリのままでありたいって思うんです。リーダーさん…どうかこの気持ちを、受け止めてもらえますか…？"}
, 
[101301201] = {id = 101301201, name = "世界一オシャレでかわいい女の子こと、ミアよ！ファッションのことならお任せなんですけど！"}
, 
[101301211] = {id = 101301211, name = "ふふん、リーダーってば見とれてる？ミアのオシャレはまだまだ止まらないよ！"}
, 
[101301221] = {id = 101301221, name = "リーダー、もっともっとミアを見て。そしたらミア、もっと可愛くなれちゃうから！"}
, 
[101220901] = {id = 101220901, name = "ワタシとアナタが出会ったのは運命。これぞ神の思し召し…なんて。どう、シスターらしくみえるかなぁ？"}
, 
[101220911] = {id = 101220911, name = "わぁ、本物のシスターみたいにとっても素敵だね～。ふふ、これでいたずらなんてしていいのかなぁ？"}
, 
[101220921] = {id = 101220921, name = "これなら、どんな彷徨える魂も救ってあげられるよ。ワタシひとりじゃできないことも…リーダーとなら、なんでもできちゃうねぇ。ふふ。"}
, 
[101301301] = {id = 101301301, name = "ふふふ、ショウでござるよ。ハロウィンなる祭りのために拙者の国に伝わる怪物…キョンシーに仮装してみたでござる。なかなか様になってるでござろう？"}
, 
[101301311] = {id = 101301311, name = "おお、なんと素晴らしいでござる！　この姿をもっと見せつけて、もっと菓子を貰いに行かねばな。"}
, 
[101301321] = {id = 101301321, name = "ふふふ、リーダー殿のおかげで菓子が山ほど貰えたでござるよ。さぁリーダー殿、祝いに山分けして一緒に食べようではないか。"}
, 
[101301401] = {id = 101301401, name = "じゃじゃ～ん！　呼ばれて飛び出た、ロールだよーっ！　どうかな？　今の登場、ちゃんと目立ってたかな！？"}
, 
[101301411] = {id = 101301411, name = "わぁ！　リーダーと遊んでただけなのに、今までのロールと違うみたい！　これがリーダーの特別な力なの？　すごーい！"}
, 
[101301421] = {id = 101301421, name = "リーダーの言葉はすごいよね。まるで晴れた日に降る雪みたいに、キラキラしてる！　おかげでロールは、こーんな素敵になれちゃった！　ありがとう、リーダー！"}
, 
[102100101] = {id = 102100101, name = "あらあら。のんびりしてると、良いクエストが取られちゃうわよ♪"}
, 
[102100201] = {id = 102100201, name = "リーダー、お帰りなさい。今日も気を引き締めていきましょう。"}
, 
[102100301] = {id = 102100301, name = "リーダー、今日も一緒に楽しくいたずらしちゃおうね♪"}
, 
[102100401] = {id = 102100401, name = "リーダーさんっ、リリーの準備はもう完璧です！　いつでも出発できますよっ！"}
, 
[102100501] = {id = 102100501, name = "ん…リーダーも、図書館が好き？　…私の邪魔をしないなら、好きにするといい。"}
, 
[102100601] = {id = 102100601, name = "お、お帰りなさい、リーダーさん…えへへ、なんだか照れますね…"}
, 
[102110101] = {id = 102110101, name = "リーダーさん、私を忘れてないですよね？　えへへ、確認したかっただけですっ！"}
, 
[102110201] = {id = 102110201, name = "いいところに来たですね。もうすぐ営業時間ですから、商品の整理を手伝うです！"}
, 
[102110301] = {id = 102110301, name = "今日もみんなを助けようね、リーダー！"}
, 
[102110401] = {id = 102110401, name = "はいはい、ちょっと待っていてくださいね。 今つかんだスクープを、記事にしてるところですから！"}
, 
[102110501] = {id = 102110501, name = "リーダー急いで来てください！　今、すっごい大金を稼ぐ方法を思い付いたんですっ！"}
, 
[102110601] = {id = 102110601, name = "リーダー、新しいシエラグルメを作ったところだよ！　食べてくれるかな？"}
, 
[102200101] = {id = 102200101, name = "いっち、にー、さん、しー！　ほら、リーダーも一緒に体操するぞ！"}
, 
[102200201] = {id = 102200201, name = "今日も私をたーっくさん頼ってね、リーダー。"}
, 
[102200301] = {id = 102200301, name = "リーダー来た来たぁ。今日のお酒も美味しいからさぁ、さぁ、飲み会だぁ～！"}
, 
[102200401] = {id = 102200401, name = "おやリーダー、今日はどんな美食の情報を教えてくれるのかな。"}
, 
[102200501] = {id = 102200501, name = "ふわぁ…おはようございます…。実は昨日の夜、急に小説のネタを思いついて…それから、筆が止まらなくて…"}
, 
[102200601] = {id = 102200601, name = "あっ、リーダーさんお待ちしてましたっ。今、穴から出ますね。よいしょっと…！"}
, 
[102200701] = {id = 102200701, name = "僕ジュリアンは、君がやってくるまで、ずっとここで足を止めていよう――"}
, 
[102200801] = {id = 102200801, name = "美味しい美味しいクッキーの出来上がり～♪　食べてください、リーダー！"}
, 
[102210101] = {id = 102210101, name = "遅いぞ、新入り！ 今度遅れたら、おやつ抜きだからな！"}
, 
[102210201] = {id = 102210201, name = "少々お待ちください、リーダー様。まだ部屋の掃除をしていますから。"}
, 
[102210301] = {id = 102210301, name = "今日もしっかりきみを見てるからねっ。"}
, 
[102210401] = {id = 102210401, name = "リーダー、来たわね。今日もいい感じのお恵み――っていうか、仕事を寄こして！"}
, 
[102210501] = {id = 102210501, name = "あ、あ、あ、あ、あ〜♪　あ、リーダー。自分の声の調子、ちょっと良くないっすか？"}
, 
[102210601] = {id = 102210601, name = "今日もダイヤの話をしますぞ〜"}
, 
[102210701] = {id = 102210701, name = "あ、あの…お帰りなさい。わ、私の芸…見てみる？"}
, 
[102210801] = {id = 102210801, name = "リーダーくん、一緒に次の冒険の計画を立てよう！"}
, 
[102210901] = {id = 102210901, name = "遅いですよ、リーダー。もう出発しちゃいますからね！"}
, 
[102211001] = {id = 102211001, name = "リーダー。ベロちゃんの新作の絵、見てくらさい～！"}
, 
[102220101] = {id = 102220101, name = "リーダーさん、今日もよろしくお願いします～。まずは、詩を歌わせてください～。"}
, 
[102220201] = {id = 102220201, name = "リーダーさん、おかえりなさい。このあたりは、もう片付けておきましたよ。"}
, 
[102220301] = {id = 102220301, name = "リーダーさん、今日もお疲れ様です。良ければ、コーヒーでもいかがですか？"}
, 
[102220401] = {id = 102220401, name = "ふわぁ…リーダーやっと来たんだ？　ワタシも今起きたところだよぉ。"}
, 
[102220501] = {id = 102220501, name = "また面倒ごとか？　――なんだ、リーダーか。ほら、早くこちらに来ればいいだろう。"}
, 
[102220601] = {id = 102220601, name = "ククク…やっと来たか、我が盟友！　この深淵で待ちわびていたぞ！"}
, 
[102220701] = {id = 102220701, name = "おいリーダー、まだやらなきゃいけないことがあるだろ。こんなところでダラダラしてる場合か？"}
, 
[102300101] = {id = 102300101, name = "よく来た、リーダー。では、一緒に見回りに行くとしよう。"}
, 
[102300201] = {id = 102300201, name = "今日も俺と一緒に鍛えようなっ、リーダー！"}
, 
[102300301] = {id = 102300301, name = "ああ、やっと来てくれた…他の女の子と遊んだりしてませんよね？　ふふ…"}
, 
[102300401] = {id = 102300401, name = "フラワーは、リーダーが来ると『うれしいー』ってなるよ。えへへ"}
, 
[102300501] = {id = 102300501, name = "お帰り、リーダー。ご飯にするかい、お風呂にするかい？　それとも…お姉さんにするかい？"}
, 
[102300601] = {id = 102300601, name = "私の助手ともあろうものが、 そんなのんびりしてないの。 もっと本気でやらなくっちゃ！"}
, 
[102300701] = {id = 102300701, name = "やっほー。リーダー、今日もダラダラするのに付き合ってくれるのー？"}
, 
[102310101] = {id = 102310101, name = "お帰りなさい、あ、な、た♪　…あぁんっ、ラヴィはもう我慢できません…っ！"}
, 
[102310201] = {id = 102310201, name = "リーダー、あなたのご命令をおまちしております。"}
, 
[102310301] = {id = 102310301, name = "あら、来るのが遅かったわね。でも大丈夫、お楽しみはこれからよ。"}
, 
[102310401] = {id = 102310401, name = "ああ、また来たんだ。今日は何の用？　まさか…あたしを引きずって冒険に行くつもり？"}
, 
[102310501] = {id = 102310501, name = "……ふあぁぁ、そなたか。 ……少々、疲れただけだ。 気にすることはないでござるよ。"}
, 
[102310601] = {id = 102310601, name = "リーダー様、本日のご機嫌はいかがでしょう？　ふふ。"}
, 
[102300801] = {id = 102300801, name = "今日も未来の歌姫に近づくための練習よ！"}
, 
[102110701] = {id = 102110701, name = "ふふん。パロネス、ちゃんとお留守番できたぞ！どうだ、偉いだろう？"}
, 
[102301001] = {id = 102301001, name = "今日は何をするつもりかしら。私にも付き合わせてちょうだい。"}
, 
[102300901] = {id = 102300901, name = "リーダー、今日も熱い一日を過ごしましょう…？"}
, 
[102211201] = {id = 102211201, name = "リーダー、今日も海でたーっくさん遊ぼうね！"}
, 
[102211101] = {id = 102211101, name = "こんにちは。ええと、あなたは…プライドの新人さんでしょうか？"}
, 
[102220801] = {id = 102220801, name = "今日も精が出るねぇ。あたしもルルリカの仕事探しに行こっかなー♪"}
, 
[102301101] = {id = 102301101, name = "きゃっ…リ、リーダーさん…！　急にいらっしゃると…心の準備が間に合いませんっ…"}
, 
[102301201] = {id = 102301201, name = "リーダー、今日も世界一カワイイを追求しに行こ！"}
, 
