[105100501] = {id = 105100501, name = "読書中だ、邪魔をするな。"}
, 
[105100502] = {id = 105100502, name = "質問がある。…なぜリーダーは、オリヴィアのようにおかしくなった？"}
, 
[105100503] = {id = 105100503, name = "頼りないあいつらと比べたら、リーダーはまし。…別に、褒めてるわけじゃない。"}
, 
[105100504] = {id = 105100504, name = "リーダーのお節介で、本を読めなかったりしたけど…いろいろ経験できたから、問題ない。…ありがとう。"}
, 
[105100505] = {id = 105100505, name = "バカばかりだけど…昔みたいに、退屈じゃない。　ただ、このバカたちはちゃんと管理すべき。…リーダーと私で。"}
, 
[105100601] = {id = 105100601, name = "はわっ…は、はいっ！　ぼ、ぼんやりしてて、ごめんなさいっ…な、なんでもお手伝いしますからっ…！"}
, 
[105100602] = {id = 105100602, name = "リ、リーダーさんっ、あの…わたし、言いたいことが…あっ、いいえ…何でもないです…。あ、あの、そろそろ冒険の準備、してきます…"}
, 
[105100603] = {id = 105100603, name = "リ、リーダーさんっ…あの、えっと、その…ぼ、冒険…お疲れさまでした！　…ふぅっ…ずっと言いたかったこと、やっと言えましたっ…。"}
, 
[105100604] = {id = 105100604, name = "リ、リーダーさんっ…よ、よかったら、おしゃべりしませんか…？　その、ちょっとだけでいいので…えへへ…"}
, 
[105100605] = {id = 105100605, name = "こ、このプライドに入ってから…人生で一番、幸せな時間を過ごしてるんです…。毎日リーダーさんとおしゃべりして、働いて、のんびりして…これからもリーダーさんと過ごせたらいいなって思います…ずっとずっと、この先も…"}
, 
[105110101] = {id = 105110101, name = "おおっ、リーダーさん自ら挨拶に来るだなんてっ！　…もしかして、私の顔に何か付いていたかな？"}
, 
[105110102] = {id = 105110102, name = "今日のリーダーさんはしっかり私を見てますね！　私の隠れた派手さにとうとう気づいたんでしょうか…！"}
, 
[105110103] = {id = 105110103, name = "私はいつも目立たないことを心配してますが…でも、リーダーさんがずっと私を見てくれてるのは、ちゃんとわかってますよ。"}
, 
[105110104] = {id = 105110104, name = "リーダーさん、手をつないでいいですか？　ほら、リーダーさんはみんなの注目の的ですから！　これで私も大注目されて…うふふ♪"}
, 
[105110105] = {id = 105110105, name = "今の私って、もしかしてリーダーさんにとってのトップスターですかっ？　えへへ、もっとド派手にいきますから見ててくださいね！"}
, 
[105110201] = {id = 105110201, name = "あ、金ヅ――じゃなくて、リーダー。リッタは子供じゃないので、自分のことは自分でできるですから。気にかけることはないです！"}
, 
[105110202] = {id = 105110202, name = "本当におせっかいが好きですね…ま、リーダーらしいですが。…助けられてるとは思ってるですから、お礼にアイテムをちょっとだけ値引きしてやるですよ！"}
, 
[105110203] = {id = 105110203, name = "あと何度か来店すれば、 うちの店のダイヤモンド会員になるですよ！ 金ヅ…じゃなくてリーダー、何か特典をつけますか？"}
, 
[105110204] = {id = 105110204, name = "忙しそうなのに、よくわざわざリッタに構うですね。もう気にしないで、クエストにでも行ってくるですよ！　…リッタは、ずっとここでリーダーを待ってますから。"}
, 
[105110205] = {id = 105110205, name = "リーダー、長い間うちの店を応援してくれてありがとうございますです。お礼にここにあるアイテムをいくらでも――えっ、せ、聖剣なんてあるわけないです！そんなものが欲しいなら、ユーナのところに行けばいいですよ！もうっ…このバカバカバカ！"}
, 
[105110301] = {id = 105110301, name = "なんでもお手伝いするよ、リーダー！　あっ、肩もみなんてどうかな？　ノンノ、得意なんだよっ！"}
, 
[105110302] = {id = 105110302, name = "リーダーと一緒にいる人、みーんな幸せそうでいいなぁ…ノンノもリーダーみたいに、いっぱい笑顔にしてあげたいなぁ！"}
, 
[105110303] = {id = 105110303, name = "リーダーはいつも優しくて、みんなのお世話をしててえらいけど…自分のことを後回しにしてばっかりなのは、よくないよっ。でも大丈夫、リーダーのお世話はこれからノンノがしてあげるからねっ。"}
, 
[105110304] = {id = 105110304, name = "お風呂の準備できたよー！　一番風呂はもちろんリーダーから！　ノンノがお背中を流してあげるからね〜っ！"}
, 
[105110305] = {id = 105110305, name = "冒険で疲れたら、いつでもノンノの家の温泉で休憩していってね！　リーダーのこと、しっかりおもてなしするから、絶対に来てねーっ！"}
, 
[105110401] = {id = 105110401, name = "リーダー、何か新しい事件はありましたか！？"}
, 
[105110402] = {id = 105110402, name = "私の仕事を応援してくれてありがとうございます。原稿料がもらえたらご飯でも奢りますよ！"}
, 
[105110403] = {id = 105110403, name = "ずーっと取材に付き合ってもらって、いっつも良くしてもらって、本当にありがたいです。もう私にとって一番の良き理解者って感じですね！"}
, 
[105110404] = {id = 105110404, name = "リーダーの身の回りは、面白い事件ばかりですっ！　…でも私があなたと一緒にいるのは、それだけが理由じゃないですよ♪"}
, 
[105110405] = {id = 105110405, name = "いつもなら、いいネタは記事にしてみーんなに読んでほしいんですが…リーダーのことは、私の中だけに収めておきたいんです！"}
, 
[105110501] = {id = 105110501, name = "今日も金儲けできるいい考えは浮かびませんでしたが…アタシはくじけませんから！　リーダーも何か思いついたら絶対教えてくださいね、えヘヘ。"}
, 
[105110502] = {id = 105110502, name = "リーダー、カジノに連れて行ってください！　リーダーが応援してくれたら、今日こそ全戦全勝できる気がするんですっ！"}
, 
[105110503] = {id = 105110503, name = "リーダーは故郷のご老人達に似ています…あ、いえ！　老けて見えるんじゃなくて…リーダーは優しくて…そう、『お小遣いくれそう』ってことです！"}
, 
[105110504] = {id = 105110504, name = "リーダー、勝負しましょう！　賭けるのは…お互いのいちばん大事な…え？　アタシ、『もう二度とギャンブルしない』なんて言いましたっけ…？"}
, 
[105110505] = {id = 105110505, name = "リーダーのそばは、 カジノにいるより楽しくて良いことばっかりです！ もしかして幸運の女神がリーダーさんについてるんじゃないでしょうか！？"}
, 
[105110601] = {id = 105110601, name = "リーダー、お腹空いた？　ちょうど美味しい料理作ったところだから、今持ってくるね。"}
, 
[105110602] = {id = 105110602, name = "一日お疲れ様。ジュースもお酒も料理も用意したから、好きなだけ食べて！　その後はゆっくり休んで、明日からまた元気に頑張ろうね！"}
, 
[105110603] = {id = 105110603, name = "リーダーのおかげで、たくさん美味しいものが作れてるんだよね。…ねえ、リーダー。料理のこと以外でも、困ったことがあったらお姉さんに相談してね？　約束だよ！"}
, 
[105110604] = {id = 105110604, name = "ずっとシエラグルメを味見してくれる人は、リーダーだけだよ。これからもお姉さんに付き合ってくれると嬉しいな。お姉さんもリーダーの期待に応えるからね、それはもう…どんなことでもね♪"}
, 
[105110605] = {id = 105110605, name = "お帰りなさい！　今日は先にお風呂に入る？　ご飯を食べる？　それとも…ふふっ、冗談だよ。でもリーダーの体が大事なのは本当だから、これからは食事以外でもリーダーのお世話をさせてねっ。"}
, 
[105200101] = {id = 105200101, name = "どんなときにも準備運動をちゃんとしないとな！　だからリーダーもエマと一緒にやるぞ！"}
, 
[105200102] = {id = 105200102, name = "リーダーは泳げるか？　もし苦手なら、エマのこの泳ぎやすい服を特別に貸してやるぞ！"}
, 
[105200103] = {id = 105200103, name = "船を買ったら  リーダーの生まれた場所にも行ってみたいな！ 船さえあればどこでも行けるからな、楽しみだ！"}
, 
[105200104] = {id = 105200104, name = "船長の指示がないと、上手く航海できないんだ。…そう考えると、リーダーもプライドの優秀な航海士みたいなものだな！"}
, 
[105200105] = {id = 105200105, name = "同じ船に乗ったらな、お互い信頼して、ずーっと一緒にいなきゃいけないんだ。だからエマは…リーダーと旅がしたい！　なぁリーダー、エマと一緒に海へ出てくれるかっ？"}
, 
[105200201] = {id = 105200201, name = "あ、リーダー！　何か私にやってほしいことないかな？　お部屋の掃除をするとか、ご飯を作るとか…一緒に遊んでほしいとか、添い寝してほしいとか！"}
, 
[105200202] = {id = 105200202, name = "リーダーはもうご飯食べたみたいだね。 ふふ、見ればわかるよ。 だって口元にご飯つぶが付いてるから。 今取ってあげるから、そのまま動かないでね。 …ん、はい取れた。リーダーは可愛いね、ふふ。"}
, 
[105200203] = {id = 105200203, name = "こんな早い時間にもう起きたんだね、リーダー。とってもえらいよ！　えっ、掃除をするだなんて…そんなことしなくていいんだよっ。リーダーはいつも苦労してるんだから、それぐらい私に任せて！"}
, 
[105200204] = {id = 105200204, name = "リーダー、前にも言ったけど敵に一人で立ち向かったりしちゃ、絶対にだめだよ！だってリーダーが怪我なんてしたら、大変でしょう？…でも、リーダーのかっこいい姿を見られるのは、ちょっと嬉しいんだけどね。ふふ。"}
, 
[105200205] = {id = 105200205, name = "ふふ、ちゃんとわかってるよ。リーダーがいつも私の後ろを、ちゃんと守ってくれてること。だから私も、リーダーの正面をずーっと守り続けるんだ！　優しいリーダーのこと、誰にも傷つけさせないから！"}
, 
[105200301] = {id = 105200301, name = "あたしと踊りたいの？　それならまずは、宴に付き合ってもらわなくちゃねぇ。"}
, 
[105200302] = {id = 105200302, name = "リーダーと飲んでると、 とっても楽しくて気持ちいい気がするぅ…。 だからさぁさぁさぁ、もう一杯いっちゃお～！"}
, 
[105200303] = {id = 105200303, name = "回って回って回って…うう。お酒よりも、踊りの方がクラクラしちゃう～。"}
, 
[105200304] = {id = 105200304, name = "今晩はあたしと一緒に飲む？　それとも一緒に踊る？　難しく考えなくていいんだよぉ。まずあたしの手を握って、足を踏み出すだけだからねぇ！"}
, 
[105200305] = {id = 105200305, name = "リーダーといる時間は  まるでお酒を飲んでるときみたいに夢心地だねぇ…ふふ、 なんだかリーダーのために踊りたくなっちゃった。 ね、リーダーもほら、一緒に踊りまわろうよ～！"}
, 
[105200401] = {id = 105200401, name = "金の皿は美食を追い求めるこの世でもっとも素晴らしい組織なんだ！　ふふ…詳しく説明しようか？"}
, 
[105200402] = {id = 105200402, name = "ラフレシアの蜜はどんな味がするのか…美食家としては絶対見逃す事が出来ないな！　まぁ、食べられる機会はかなり先になるのだが…ふふ。"}
, 
[105200403] = {id = 105200403, name = "『美食の前では、人々は皆平等である』――と育て親の伯爵に教わったのだが、リーダーと食事をしているとそうは思えないな。もっと、自分が特別な存在になったように感じる、というか…"}
, 
[105200404] = {id = 105200404, name = "リーダーと一緒にいると、料理がさらに美味しく感じるな。ラフレシアの蜜でさえも、叶いそうにないぐらいに――な。"}
, 
[105200405] = {id = 105200405, name = "『金の皿』会長である私が開発したスペシャルメニューだ。さぁ、召し上がれ！前菜ばかりなんだけど…"}
, 
[105200501] = {id = 105200501, name = "ん…？　もしやリーダーさんも、女の子同士の恋愛に興味がおありで…！？"}
, 
[105200502] = {id = 105200502, name = "リ、リーダーさんも、エイプリルさんとオリヴィアさんが仲が良すぎると思いますよね？　そう、どう見ても特別な関係でしかないあの距離感…！　はぁ～！！"}
, 
[105200503] = {id = 105200503, name = "ま、また新しい女の子を連れて戻ってきてくれたんですか…！？　はぁ、本当にリーダーさんは…女の子の楽園を作る達人ですね…！"}
, 
[105200504] = {id = 105200504, name = "うう…徹夜、疲れました…作品が完成するときは、本当に最高なんですが…ふわぁ…でも、限界…リーダーさん、膝を貸してください…"}
, 
[105200505] = {id = 105200505, name = "私の作品、読んでくれたんですか！？　わぁ…う、嬉しいですっ…これからも、私の最初の読者になって…女の子について語り合いましょう…！"}
, 
[105200601] = {id = 105200601, name = "…リ、リーダーさん…そんなにじっと見つめられたら、穴に隠れたくなっちゃいますっ…！"}
, 
[105200602] = {id = 105200602, name = "リーダーさん…私のことをじっと見つめるのやめてください…恥ずかしいです…あっ…もしかしてリーダーさんも穴が掘りたいんですか？　そ、それならそうと早く言ってくれれば…"}
, 
[105200603] = {id = 105200603, name = "リーダーさんは私と一緒にいてくれるんですか？　なら…私も穴を我慢しますね…！"}
, 
[105200604] = {id = 105200604, name = "リーダーさんのような優しい人と一緒なら…穴に隠れなくても安心ですっ。"}
, 
[105200605] = {id = 105200605, name = "私、前よりも自信を持てるようになったと思うんです。だから…あの、言わせてくださいっ。私、リーダーさんが大好きです！"}
, 
[105200701] = {id = 105200701, name = "なんだい、リーダー。僕と踊ってくれるのかい？"}
, 
[105200702] = {id = 105200702, name = "僕の手綱を握っていいのはリーダーだけだよ。ふふ"}
, 
[105200703] = {id = 105200703, name = "『すべての人が主人になれないし、すべての主人が従者から忠誠を誓われる価値がない』"}
, 
[105200704] = {id = 105200704, name = "『恋は盲目で、恋人たちは自分の失敗に気づかなくなる』僕はこの状況に陥ったらしい、マイ ロード。"}
, 
[105200705] = {id = 105200705, name = "『one love,one lifetime.』 君と一緒にこのシーンを演じることができて光栄です。"}
, 
[105200801] = {id = 105200801, name = "リーダー、一緒にお菓子食べませんか？　みんなでお菓子パーティしたら、もっと美味しく感じますよ～♪"}
, 
[105200802] = {id = 105200802, name = "お願いします、リーダー！ どうか特訓に付き合ってくださいっ。 リーダーと一緒にいる時だけ、 ちゃんと戦えてる気がするんです～！"}
, 
[105200803] = {id = 105200803, name = "ふふふ～♪　美味しいお菓子はもうすぐできますよ。そうそう、ひとつだけリーダーのため、特別に作ったんです！　楽しみにしてくださいね♪"}
, 
[105200804] = {id = 105200804, name = "リーダーにお菓子を食べて喜んでもらえると、すごく幸せなんです～♪　これから先もいっぱいお菓子を作らせてくださいね♪"}
, 
[105200805] = {id = 105200805, name = "リーダー、疲れたなら早く寝ましょうね～・ お菓子を食べるのもいいですけど、 ゆっくり休むのも大事ですから。 私もずっと隣で手を握っててあげますからね～♪"}
, 
[105210101] = {id = 105210101, name = "なっ…お、おい、おまえっ…！　今、俺様に何をしたっ！？　俺様を子供扱いしているのなら、ひっかくぞ！"}
, 
[105210102] = {id = 105210102, name = "お前は成長が見込めるからな、俺様が面倒を見て力を伸ばしてやろう。俺様の部下になれたこと、光栄に思え！"}
, 
[105210103] = {id = 105210103, name = "近頃ますます腕が上がったようだな、新入り。だが、まだまだだ！　ウィチタ団の幹部として自覚を持ち、努力を続けろ！"}
, 
[105210104] = {id = 105210104, name = "しっかり俺様について来るんだぞ。俺様から離れると危険だからな！　わかったなら、もっと近くに寄るんだ。ほら。"}
, 
[105210105] = {id = 105210105, name = "いいか、俺様を決して裏切るなよ。なにせお前は、俺様にもっとも近い存在――右腕なんだからな！　だからその…誇りに思え、俺様に従え！"}
, 
[105210201] = {id = 105210201, name = "リーダー様、手を洗い忘れてはいませんか？　もしや、そちらのゴミと一緒に処分されたいということでしょうか。そうでなければ、今すぐ手を洗っていただけるとネネルは嬉しく思います。"}
, 
[105210202] = {id = 105210202, name = "汚いものは無視できません。 すぐに掃除をいたします。ですが、どうしても掃除をしきれないならーー　抹殺するのみ、です。"}
, 
[105210203] = {id = 105210203, name = "何度も言いましたが、 冒険を終えたらまず手を洗い、 お風呂に入って着替えてください。 そのままベッドに座ってはいけません、 甘えてもいけません！"}
, 
[105210204] = {id = 105210204, name = "リーダー様、食べるものは綺麗に洗わないといけませんよ。ですから…さぁ、そのパンを貸してください。ネネルがよく洗って差し上げますから。"}
, 
[105210205] = {id = 105210205, name = "長い間リーダー様と生活をして、やっとわかりました。 リーダー様だけでは心配ですから、 これからはすべてわたくしにお任せください。 まずは、リーダー様の全身を清潔にいたしましょう。"}
, 
[105210301] = {id = 105210301, name = "こら、何してるの！　…今のは、ただの挨拶？　ま、まぁ大事なことだけど…簡単に女の子に触らないの！"}
, 
[105210302] = {id = 105210302, name = "私の秘密、他の人に言ってないよね？　私はちゃんと見てるから、騙されないよ！"}
, 
[105210303] = {id = 105210303, name = "おかしいなぁ。私の秘密をばらされないようリーダーを見張ってたのに…いつの間にか、私の他の秘密まで知られちゃってるんだもん。変だよね…"}
, 
[105210304] = {id = 105210304, name = "リーダーとずっと一緒にいたら、ちょっとだけ恋とか愛っていうのがわかってきたような…あっ、な、なんだか顔が熱くなってきた…！　だ、だめだめ…！"}
, 
[105210305] = {id = 105210305, name = "きみはキスに興味があるの…？　わ、私は、その…ううっ…まだ心の準備が出来てないし…せめてもう少し、ロマンチックな雰囲気が欲しいしっ…！"}
, 
[105210401] = {id = 105210401, name = "何、リーダー。わたしを探してたの？　何か用事でも…あ、ご馳走をおごってくれるとか！？　…ああ、そう。ただ声をかけただけね…"}
, 
[105210402] = {id = 105210402, name = "これだけわたしを気にするなんて、リーダーってもしかして敬虔な信者か何か？　それなら…少しぐらい、寄付してくれてもいいのよ？"}
, 
[105210403] = {id = 105210403, name = "帰る場所もない、貧しい子供達を救うには、今のわたしじゃまだ力不足なの…だからせめて、もっとたくさん仕事を寄こして、リーダー！"}
, 
[105210404] = {id = 105210404, name = "いつもわたしに優しくして、手助けしてくれて……リーダーこそ、身近な神様みたいね。"}
, 
[105210405] = {id = 105210405, name = "たまに考えるの。教会と神に恩返しが終わったら、わたしは何したらいいんだろう、って。…まぁ、リーダーの側にいればいいわよね。"}
, 
[105210501] = {id = 105210501, name = "応援ありがとうっす。でも、握手には時間制限があるっすよ。リーダーと言えど、ファンは平等に扱わないとっすから！"}
, 
[105210502] = {id = 105210502, name = "ラ～♪　ラララ〜♪　うーん…何か物足りない感じがするっすね。リーダーもアドバイスくださいっす！"}
, 
[105210503] = {id = 105210503, name = "自分、本当は天才なんかじゃないっす。ただ一生懸命、練習してるだけで…。でも、リーダーがいつもそばにいるから頑張れるっす。ありがとうっす、リーダー。"}
, 
[105210504] = {id = 105210504, name = "皆の期待に応えるため、リーダーの思い遣りに応えるため…自分、一生懸命に歌い続けるっすよ！"}
, 
[105210505] = {id = 105210505, name = "リーダーには、公演の特等席にいてほしいっす。自分…リーダーのために歌うっすから。"}
, 
[105210601] = {id = 105210601, name = "リーダー、何の石を持っているですぞ？　早くジブンに見せるですぞ！　…あっ、リーダー…。これはただの、ガラス球ですぞ…"}
, 
[105210602] = {id = 105210602, name = "ジブンはどんな相手でも誤魔化したりせず、 本当の宝石の価値を伝えますぞ。何せジブンは、 恩師のような善き宝石商を目指しているのですからですぞ！"}
, 
[105210603] = {id = 105210603, name = "一粒の宝石には一つの物語があるのですぞ。単に貴重な石というだけではないこと、みっちり教えてやるですぞ…！"}
, 
[105210604] = {id = 105210604, name = "宝石の話を聞いてくれて、宝石探しにも付き合ってくれて…リーダーは本当に素晴らしいお人ですぞ！　もっともっと、一緒に冒険しましょうですぞー！"}
, 
[105210605] = {id = 105210605, name = "今まで見つけたどんな宝石も、リーダーの輝きには釣り合わないですぞ。いつかはかならず、リーダーにピッタリの宝石を見つけてみせますぞ！"}
, 
[105210701] = {id = 105210701, name = "ひゃうっ！！！　…な…何の、用…！？"}
, 
[105210702] = {id = 105210702, name = "ん…あ、あの…リーダーに、触られるのは…ち、ちょっと、緊張、します…"}
, 
[105210703] = {id = 105210703, name = "わ、私、もっと強くなりたいし…ち、ちゃんとしたいから…いろいろ、教えてほしい…！"}
, 
[105210704] = {id = 105210704, name = "リ、リーダーがいろんなこと、教えてくれたおかげで…今、皆と仲良くなれたって…思う。ありがとう、リーダー…"}
, 
[105210705] = {id = 105210705, name = "リーダー、い、いつもありがとう…いつか自分の一座を持てたら…か、必ず、リーダーのために最高のショーを見せるからっ…！　や、約束、だから！"}
, 
[105210801] = {id = 105210801, name = "こら、頭を撫でないのっ。もう、あたしは子供じゃないんだぞ。"}
, 
[105210802] = {id = 105210802, name = "リーダーくんから信頼を得られて、とても嬉しいよ。 その期待にちゃんと応えるために、 もっと面白いものをリーダーくんには見せてあげる。"}
, 
[105210803] = {id = 105210803, name = "リーダーくんに恩返しをするためには、 リーダーくんと出会ってから経験した冒険の経緯を  全て書き残しておきたい。 それをまとめて新しい冒険録を出したいから  楽しみにしててね。"}
, 
[105210804] = {id = 105210804, name = "もしも危険な冒険で、 リーダーくんの身が危なくなったときには… あたしがなんとしてでも守ってみせるから。 ふふ、もちろん本気だよ"}
, 
[105210805] = {id = 105210805, name = "やっと気づいたんだ。リーダーくんが傍にいてくれるから、安心して前へ踏み出せるって！　ふふ。今度の冒険録の主人公は、あたしたち二人に決まりだよ！"}
, 
[105210901] = {id = 105210901, name = "リーダー、何か配達物はありませんか？　今なら手が空いていますから、特急でお届けできますよ！"}
, 
[105210902] = {id = 105210902, name = "新人だとしても、他の皆さんと同じように接してくださいね。他の人たちがやっていることなら、私だってやりきってみせますから！"}
, 
[105210903] = {id = 105210903, name = "私は配達人ですからね。地図にある道もない道も、すべて覚えているんです。もし何かの役に立てそうなら声をかけてくださいね。"}
, 
[105210904] = {id = 105210904, name = "リーダーと一緒なら私、どこへでも行ける気がしますっ。さぁ、仕事を続けましょう！"}
, 
[105210905] = {id = 105210905, name = "手紙や物だけでなく、その気持ちを届けるのが私の仕事であって、責任なんです。…でも近頃は、ちょっと自信がないんです。私の気持ちは、ちゃんとリーダーに伝わってるのかなって…"}
, 
[105211001] = {id = 105211001, name = "うぇへへ、リーダーも絵に興味ありそうれすねぇ。一緒に描いてみるれすかぁ？"}
, 
[105211002] = {id = 105211002, name = "うぅ～…毒薬の飲みすぎで、ちょっとフラフラれすぅ…ああ、心配しないでくらさい。ベロちゃんの身体に、毒薬は毒じゃないれすからぁ…"}
, 
[105211003] = {id = 105211003, name = "ベロちゃんが得意なのは絵を描くことれすが～…リーダーが別のことしてほしいなら、ベロちゃんやってみるれすよ～。"}
, 
[105211004] = {id = 105211004, name = "うぇへへ！　どうしたんれすか、リーダー。今日はピンクでフワフワじゃないれすかぁ…！　ベロちゃんは今、絶賛お絵描き中れしたぁ。あぁ、ピンクのリーダーも書き足したいれすがぁ…紙が足りないれすぅ…"}
, 
[105211005] = {id = 105211005, name = "たとえひとつの物事れも～、それに対して深く知ったほうが良い作品が描けるものれすよぉ！だから、もっとリーダーを教えてほしいれす～。そしたら、リーダーにもっと良い絵を見せれるれすからぁ～！"}
, 
[105220101] = {id = 105220101, name = "リーダーさんの優しさに触れると、とても落ち着いて…安心できて…それにとっても頼もしく思うんです～。"}
, 
[105220102] = {id = 105220102, name = "私の詩や演奏がリーダーさんの励みになるのなら、いつでもあなたのために歌いますよ～。"}
, 
[105220103] = {id = 105220103, name = "リーダーさんのような英雄のため、輝かしい叙事詩を綴りたいです～。でも、すぐに書けるものでもないですよね～。ゆっくり、素晴らしい物語を探さなくちゃ～。"}
, 
[105220104] = {id = 105220104, name = "私が神様に試練を与えられたとき、リーダーさんが現れて、さまよう私の行くべき道を照らしてくれました…きっとその何もかもが、神様のお導きなんです～。"}
, 
[105220105] = {id = 105220105, name = "リーダーさんと一緒にいると、胸がドキドキうるさくて止まらないんです～。どうして胸がこんなに熱くなるんでしょう～…リーダーさんともっとくっついたら、理由がわかるでしょうか～？　なんて、ふふ…"}
, 
[105220201] = {id = 105220201, name = "こういうことをされるのは、恥ずかしいですけど…リーダーさんが私のことを普通の女の子として構ってくれるのは…とっても嬉しいです。えへへ…"}
, 
[105220202] = {id = 105220202, name = "このプライドに入れて、本当に良かったです。皆さんとっても優しくて、とっても楽しくて…私ももっと頑張って、皆さんの力になりたいですっ。"}
, 
[105220203] = {id = 105220203, name = "田舎で育ったので、家事も一通りできるし、体力もあるんですっ。だからリーダーさん、もっといろんなことを手伝わせてくださいっ。"}
, 
[105220204] = {id = 105220204, name = "最近の私、変なんです…。リーダーさんの前だと、自分が小さな女の子みたいで…リーダーさんのことを思い出しただけで、顔が赤くなって…"}
, 
[105220205] = {id = 105220205, name = "ふあ、リーダーさんの匂い…安心します…。…ふふ。こんなに近いと、リーダーさんの胸の音も聞こえそうですね…あの、もう少しこのままでいていいですか…？"}
, 
[105220301] = {id = 105220301, name = "きゃっ…リ、リーダーさん、急にどうしたんですか？　リーダーさんに触れられると、びっくりして…ドキドキ、しちゃいます"}
, 
[105220302] = {id = 105220302, name = "今まで、友達がペットぐらいしかいなくて…でも、リーダーさんと皆さんと出会えたおかげで毎日楽しくて…とっても幸せですっ"}
, 
[105220303] = {id = 105220303, name = "ワタシ、海辺の村で生まれたんです。遠くまで浅瀬が続いていて、水がとっても綺麗に澄んでいて…いつか、リーダーさんにも見て欲しいです。ふふ"}
, 
[105220304] = {id = 105220304, name = "リーダーさんの側にいるだけで、何もできない自分は嫌ですっ…だから、あのっ…これからは、何でも頼ってください！"}
, 
[105220305] = {id = 105220305, name = "リーダーさんの手は、 いつもすごく…あたたかいですね。 あなたの隣にずっといられるよう、 ワタシ、頑張りますから… どうか、私にあなたを守らせてください。"}
, 
[105220401] = {id = 105220401, name = "ふわぁ、眠くなってきちゃった…リーダーがいてくれて良かったよ、ちょっとだけ肩貸してね…むにゃ…。"}
, 
[105220402] = {id = 105220402, name = "リーダーはやっぱり変だよね、ワタシの占いが全然当たらないもん。…ふふ。でもだから、面白いんだよねぇ。"}
, 
[105220403] = {id = 105220403, name = "…あぁ、そこは…うぅん、リーダーだめだよぉ…んん、あれ…？　今のリーダーは夢だったの？　…まぁいいやぁ。ねぇ本物のリーダー。少しだけ、ぎゅってしていい？"}
, 
[105220404] = {id = 105220404, name = "リーダーの未来は見れないけど…そばにいさせてね。占いじゃわからない先のこと、一緒に確かめようよ。"}
, 
[105220405] = {id = 105220405, name = "眠くなってきちゃったぁ…リーダー、今日も一緒に夢の世界に行こうねぇ。…それから、朝日も一緒に迎えようね？"}
, 
[105220501] = {id = 105220501, name = "何してるんだ？　用がないなら、どこかに行ってくれ。私にはやることがたくさんあるんだ、暇人だと思うなよ。"}
, 
[105220502] = {id = 105220502, name = "メンバーもリーダーも、放っておくのが心配なやつばかりだな…別に、おまえを責めてるわけじゃない。"}
, 
[105220503] = {id = 105220503, name = "おまえが手助けをしてくれるのは助かる。…だが、おまえも無理はするなよ。倒れられたら困るやつがたくさんいるんだからな。"}
, 
[105220504] = {id = 105220504, name = "近頃、あまり私のところに来ないな。…べ、別にさみしいなんて言っていない！　変なことを考えるのはやめろ、包帯で口をふさぐぞ！"}
, 
[105220505] = {id = 105220505, name = "あぁ本当にうっとうしいやつだ、私の気持ちを乱してばかり…！　…いったいどうすれば、リーダーを私のものにできるんだ…"}
, 
[105220601] = {id = 105220601, name = "うひゃあっ！？　に、人間め、貴様…我輩に触れるとは！　…我輩があまりに魅力的であるせいか、ククク…ハーッハッハッ――ゲホッ！　ゲホゲホッ…！"}
, 
[105220602] = {id = 105220602, name = "我輩は冥夜の堕天使だぞ、簡単に籠絡できると思うなよっ！"}
, 
[105220603] = {id = 105220603, name = "うむ、貴様の働きはとても良い。だが、この程度で私が…いや、我輩が『ありがとう』なんて口にするなんて思わないことだなっ。"}
, 
[105220604] = {id = 105220604, name = "ククク…この冥夜の堕天使が、貴様と契約を結んでやろう。――って、き、貴様が想像してるような意味ではない！　ただ…黙示録に貴様の名を刻んだだけだ！"}
, 
[105220605] = {id = 105220605, name = "貴様…！　もしや我輩と、この冥夜の力を共有したいのか？　…ならば、我輩の手を取るといい。それで我輩のすべては、貴様のものだっ…"}
, 
[105220701] = {id = 105220701, name = "リーダーは、あたしのことを姉貴とでも思ってるのか？　…まぁ、もうちょっと頼るぐらいならいいけどな…"}
, 
[105220702] = {id = 105220702, name = "リーダーのおかげで、鍛冶師として成長できてる気がするよ。これからもよろしくな。"}
, 
[105220703] = {id = 105220703, name = "いつも気ぃ遣ってくれてありがとな。…けどなんか、あたしがあたしらしくいられないっつーか…いや、でも、その…あ、ありがとな。"}
, 
[105220704] = {id = 105220704, name = "あたしはリーダーとそれなりに仲が良いと思ってるけど、妹達のとは違う仲が良いっていうか…ん？　…あ、あたし、今変なこと言ってないよな…！？"}
, 
[105220705] = {id = 105220705, name = "リーダーには世話になってるからな。だからあたしは力の限り、何よりも硬く、何よりも綺麗な鎧をリーダーに作ってやるよ。"}
, 
[105300101] = {id = 105300101, name = "戦う者として、貴君のリーダーとしての適性を認めている。貴君の隣にいて相応しい者となるため、努力しよう。よろしく頼む。"}
, 
[105300102] = {id = 105300102, name = "な、なんだ急に…その、私はこうした交流が不得手でな…だから慣れるまで、もう少し時間をくれ。"}
, 
[105300103] = {id = 105300103, name = "交換日記はちゃんと書いてるか？ どんなことでもかいてくれ。 そうすれば、互いにより理解を深められるだろう。 ああ…いや、変な意味ではないんだが…。"}
, 
[105300104] = {id = 105300104, name = "今読んでいる本には、『毎日抱擁を交わしながら、起床と就寝の挨拶をする』と親密になれると書いてあった。だから私たちも、今日からやってみないか？"}
, 
[105300105] = {id = 105300105, name = "もし本当に平和な日が訪れた時には…私はリーダーと、もっと親交を深めたいと思う。共に穏やかに暮らして、毎日、朝と夕暮れを迎えるんだ。…ふっ。今はまだ、遠い夢だがな。"}
, 
[105300201] = {id = 105300201, name = "リーダー、一緒に特訓するか？　いろんな特訓方法を教えてやるぜ、絶対リーダーのためになるしな！"}
, 
[105300202] = {id = 105300202, name = "リーダー、何か手伝ってやろうか？ほら、普段迷惑かけてるしな。たまには役立たせてくれよ！"}
, 
[105300203] = {id = 105300203, name = "メイはいいやつだし、友達だからな。俺が全力で守ってやるんだ。もちろん、リーダーも同じだからな！"}
, 
[105300204] = {id = 105300204, name = "ひとりでいるより、リーダーと一緒に特訓したら、 戦ったりするほうがもっと楽しいぜ！ メイを守るみたいに、 リーダーのこともきっちり守ってやる！"}
, 
[105300205] = {id = 105300205, name = "リーダーと一緒にいると強くなれて楽しいぞ！俺が強くなって楽しくなるために、これからも俺のそばにいてくれ！"}
, 
[105300301] = {id = 105300301, name = "リーダーさんは、私ともっと親密な関係になりたいですか？　私ならいつでも、お付き合いしますよ。"}
, 
[105300302] = {id = 105300302, name = "ふふ…リーダーさんに見つめてもらえると、本当に嬉しいです。でも…他の女の子は見ないで、私だけを見てくれると、もっともっと嬉しいです…。"}
, 
[105300303] = {id = 105300303, name = "リーダーさん以外、誰も私を理解していないんです。ふふ…でも他人なんて、どうでもいいんです。私にとって、リーダーさんだけいれば充分ですから。"}
, 
[105300304] = {id = 105300304, name = "リーダーさんと出会ってから、 寂しくなくなったんです。だからこれからも、 リーダーさんが行くどんな場所にも、 必ず付いて行かせてくださいね。"}
, 
[105300305] = {id = 105300305, name = "ああ、やっぱり私を理解してくれるのはリーダーさんだけ…。リーダーさん、私たちはかけがえのない存在ですよね？　だから、互いのすべてを独占しあいましょう…うふふふ…！"}
, 
[105300401] = {id = 105300401, name = "リーダーはなでられるの、好き？　ふふ、じゃあフラワーが、いっぱい『よしよし』してあげるね。"}
, 
[105300402] = {id = 105300402, name = "大丈夫、フラワーはここにいるよー。リーダーのご用が済んだら、一緒に遊びに行こうね。約束だよー。"}
, 
[105300403] = {id = 105300403, name = "フラワーね、リーダーと一緒にいるのが好きだよー。空がとっても高いこととか、大地がとっても広いことを、教えてもらえるから。ふふ。"}
, 
[105300404] = {id = 105300404, name = "よしよし。リーダーはとっても頑張ってて、えらいね。フラワーにも、手伝わせてね。どんなちっちゃいことでも、フラワー、がんばるね。"}
, 
[105300405] = {id = 105300405, name = "リーダー、いろんな世界を教えてくれて、ありがとう。フラワー、もっともっと、たくさんのことが知りたいな。だから、どんな場所へもフラワーを連れて行って。リーダーと見る世界はね、とってもきれいって思うから。"}
, 
[105300501] = {id = 105300501, name = "メイドは二十四時間、主人に付き添うものだからね。夜まで待たなくともーー大丈夫だよ？"}
, 
[105300502] = {id = 105300502, name = "お姉さんの毛並みはなかなかのものだけど、肌のほうも自信があるんだ。ほら、触って確かめてみるかい？"}
, 
[105300503] = {id = 105300503, name = "リーダー、大好きだよ。…どうだい、何か感じたかな？　…おや、おかしいな。ちゃんと少しだけ、気持ちを込めたんだけど。"}
, 
[105300504] = {id = 105300504, name = "君が目の前にいてくれる幸せを大事にしたいんだ。この幸せを守るためなら、お姉さんはなんでもやるよ。"}
, 
[105300505] = {id = 105300505, name = "この先は、いつまでも君と共にありたいと思っているよ。 …ふふ、お姉さんだってたまには本気で、 愛を囁くぐらいするさ。 だから、ちゃんと聞いてくれリーダー。 …愛しているよ。本当に、ね"}
, 
[105300601] = {id = 105300601, name = "このベル様になれなれしく話しかけないの！　そんな簡単に認めたりしないんだから！"}
, 
[105300602] = {id = 105300602, name = "過酷で危険な宝探しに付き合ってくれるのは…その…た、助かるわ！　でもお宝を発見するのは、難しいものよ。絶対にめげたりしない心の準備はできてるかしらっ？"}
, 
[105300603] = {id = 105300603, name = "ふたりで宝探しっていうのも、なかなか楽し…じゃなくてっ！　私は孤高のトレジャーハンター、ただ助手を連れてるだけよっ！"}
, 
[105300604] = {id = 105300604, name = "最近、なかなかいい仕事をしてるじゃない。あと少しで私の相棒として認めてもいいかも…って、まだ喜ぶのは早いんだからね！　その、ちょっと認めてあげただけなんだから！"}
, 
[105300605] = {id = 105300605, name = "あんたのおかげでずいぶん助かってるしね、これから手に入れたお宝は半分分けてあげる。…な、何よ、良い提案しただけなんだから変な目で見ないで！　もうっ、いっそお宝全部あげるわよ！　おつりもいらないから、こっち見るなっ！　ばかっ！"}
, 
[105300701] = {id = 105300701, name = "アイリスはねー、小っちゃい頃はかくれんぼとか追いかけっことか、いつもあたしに負けて泣きそうになってたんだー。あはは、可愛いよねー。"}
, 
[105300702] = {id = 105300702, name = "厄介なことがあったら、あたしに任せていいよ。その代わり、楽しいことを見つけてくれるのが条件ね。それでいいなら、交渉成立ー。"}
, 
[105300703] = {id = 105300703, name = "あたしみたいな適当な人間といると、大変じゃない？　…まあ、どうするかは好きにすればいいけどねー。"}
, 
[105300704] = {id = 105300704, name = "リーダーってば、またあたしの気を散らして…仕事に影響出たらどうすんのさ。…それに、リーダーを守れなかったら、大変でしょ？"}
, 
[105300705] = {id = 105300705, name = "あたし達の絆を祝して、ハイタッチを…あ、それより手をつないだほうがいいかな。それとも、抱きしめあうとか。んー、迷うなー…まぁ、全部やっちゃう？"}
, 
[105310101] = {id = 105310101, name = "ああ、触れられるだけで全身が痺れちゃいます…リーダーさん…今夜はラヴィを寝かさないでください♪"}
, 
[105310102] = {id = 105310102, name = "どうかラヴィにもっと触れてください…私にとってリーダーさんは運命の人。お互いのすべてを知るべきですから♪"}
, 
[105310103] = {id = 105310103, name = "リーダーさん、どうかチャンスをください！　あなたのことをどれぐらい好きか証明させてください、毎晩…愛の巣で！"}
, 
[105310104] = {id = 105310104, name = "初めて会ったときから、私はもうリーダーさんのものでしたが…あれから毎日リーダーさんの愛を貰えて、ラヴィは本当に幸せです！"}
, 
[105310105] = {id = 105310105, name = "この出会いは運命ですから…何があっても、あなたのことは絶対に諦めません。この身が果てるまで…愛に生きましょう！"}
, 
[105310201] = {id = 105310201, name = "はい、何かご命令ですか？ いつでもお声がけください。"}
, 
[105310202] = {id = 105310202, name = "わ、私の頭を撫でたいのですか…？　そ、その…も、もちろん構いません！　主人の意志は私の意志。私の意見など、聞く必要はありません。"}
, 
[105310203] = {id = 105310203, name = "リ、リーダー。あなたの優しさは嬉しく思いますが…どうか、おやめください。その…困りますから。"}
, 
[105310204] = {id = 105310204, name = "リーダー、どうかもっとご命令ください。 私はご命令をいただかないと… 何をすればいいのか、わからなくなってしまいます…"}
, 
[105310205] = {id = 105310205, name = "いつも優しくしていただいて、 仲間と言っていただいて、 私を救い、希望さえ与えてくれた… そんなあなたのためならば、 この身は惜しくありません。 あなたをいつまでも守り続けるため、 私は自らの身も守りましょう。"}
, 
[105310301] = {id = 105310301, name = "この身体は、いつでも戦いを渇望しているの…だから私が退屈で死ぬ前に、どうにかしてちょうだい、リーダー。"}
, 
[105310302] = {id = 105310302, name = "私はもっとたくさん、激しい戦いがしたくて、リーダーは面倒なクエストを解決しなきゃいけない…でしょ？　私たち、とってもいい関係になれそうね。"}
, 
[105310303] = {id = 105310303, name = "私をこれだけ信用するなんて、あなたは馬鹿なのかしら。それとも、ただ大胆なだけ？　…おかしな人間だわ、本当に。"}
, 
[105310304] = {id = 105310304, name = "あなたと肩を並べて戦うことが、 こんなにも快感になるとは思わなかったわ。 ねぇ、あなたはこれから先、 私のそばを離れたりしないわよね？"}
, 
[105310305] = {id = 105310305, name = "あなたの周りのレッドはみんな強くなるでしょう。なら、もしあなたを私のものにできたら――最強になれるってことじゃない？　ねぇ。あなたも、至上の戦いを見てみたくはないかしら？"}
, 
[105310401] = {id = 105310401, name = "どー…うー…もー…リー…ダー…って、ナマケモノのモノマネなんかさせるな。"}
, 
[105310402] = {id = 105310402, name = "あんたの顔を見るたび、 ゲームがセーブできなかったことを思い出すっ......。 せめて代わりに、ゲームより面白いこと見つけてきてよね。"}
, 
[105310403] = {id = 105310403, name = "あんたの周りのすごい子たち、きっともっと強くなりたいだろうから、クエストをたくさん任せてあげなさい。あたしはその間、お休みしてるから。…何、文句でもある？"}
, 
[105310404] = {id = 105310404, name = "あんたと長く付き合っていくと、自分がどんどん普通の女の子になっていく気がする…。…なんでもない。あんたはこれからも、私に面白いこと見せてくれればいいの。"}
, 
[105310405] = {id = 105310405, name = "いつかあんたも、あたしみたいな神様になったりして。ま、なれないほうが幸せだし、元の居場所に戻りたいんだから関係ないか。…この先は、まだまだ遠いけど…ね。"}
, 
[105310501] = {id = 105310501, name = "腹を満たすほど食わねば  逆さに吊り下がり寝るも平気でござるよーー どうだ？これなら迷惑にならんでござろう？"}
, 
[105310502] = {id = 105310502, name = "拙者の国元？大陸の極東の果てでござる……それはそれは長い道のりでござるよ。"}
, 
[105310503] = {id = 105310503, name = "拙者は修行をするでござる。 強くなるための修行だ。 屋敷の外へ出て何やらではないでござるよ。"}
, 
[105310504] = {id = 105310504, name = "わかったわかった、心配してくれてるでござるな。もうここは拙者の家でござる。"}
, 
[105310505] = {id = 105310505, name = "修行の果て、仁義の真粋、共に探してくれないか？ 拙者はこなたの鋭き刃でござる。"}
, 
[105310601] = {id = 105310601, name = "リーダー様、退屈なのですか？　ふふ。ではわたくしがお付き合いしましょう。おしゃべりでもお買い物でも、なんでも言ってくださいね。"}
, 
[105310602] = {id = 105310602, name = "リーダー様は頼もしい方ですね。ブリジットがいないときでも、リーダー様がいるとほっとします。"}
, 
[105310603] = {id = 105310603, name = "すべてを守れる存在になるためには、もっと強くなって、多くを知らねばなりません。ですから、わたくしをどこへなりとも連れて行ってください、リーダー様！"}
, 
[105310604] = {id = 105310604, name = "リーダー様と同じように辛抱強く、優しく、強く……そして、いつでも皆様を希望で満たす存在になりたいのです。"}
, 
[105310605] = {id = 105310605, name = "わたくしにとって、リーダー様は特別な存在です。わたくしを救い、知らない多くを教えてくれた方…いつまでも、この恩は忘れません。ですから、あなたが苦しく思ったときには、わたくしが手を差し伸べます。どうか、その手を迷わず取ってくださいね。"}
, 
[105300801] = {id = 105300801, name = "な、何触ってんのよ、馬鹿！いやらしい！変態！すけべ！ああもうっ、ノエルに全部言ってやるんだからー！覚悟してなさいよ！"}
, 
[105300802] = {id = 105300802, name = "未来の歌姫と握手でもしたいのかしら？うふふっ、きっと自慢できるいい思い出になるでしょうね。さぁさぁ、ほらほら、握手ぐらい何度でもしてあげるわよ♪"}
, 
[105300803] = {id = 105300803, name = "暇なら一緒に歌の練習をするわよ！ドーレーミーファーソー♪　…ほら、見てないであんたもやるの！あんたの歌も悪くないんだから…思いっきり歌えばいいのっ！"}
, 
[105300804] = {id = 105300804, name = "緊張しても、あんたの顔を思い出すとちょっと落ち着くのよね…。なんか、不思議…。…で、でも、落ち着くにはまだ、全然足りないんだから！調子に乗らないで！"}
, 
[105300805] = {id = 105300805, name = "い、いつもはその…ちゃんと言えないけど…あんたはあたしの大切なコーチで…大切なファンだと思ってる。あたしのために、いろんなことしてくれて…だからその…そ、それなりに感謝してるってこと…ちゃんと、わかってよね。"}
, 
[105110701] = {id = 105110701, name = "リーダーは暇人なのか？プライドのリーダーって忙しいんじゃないのか？おしごとしなくていいのか？ま、パロネスも暇だし、遊んでくれるならなんでもいいけどな！　"}
, 
[105110702] = {id = 105110702, name = "おっ、なんだ。パロネスの懐にしまったイタズラグッズが気になるのか～？よーし、それじゃあリーダーも一緒にイタズラをするぞ！怒られはしちゃうけど…すっごく楽しいぞ！"}
, 
[105110703] = {id = 105110703, name = "ううん…そうやってとんとんされると…眠気が…。お昼寝してもいいか…。リーダー…ダメって言われても無理だ…うとうとが止まらないぞ…。ぐぅ…。"}
, 
[105110704] = {id = 105110704, name = "パロネスに構ってほしいのか？リーダーもパロネスのことが好きすぎるぞー、にゃははっ！じゃあさっそく、思う存分くすぐってやる！こちょこちょこちょこちょー！"}
, 
[105110705] = {id = 105110705, name = "ここに来てから大好きなヤツがいっぱい増えたぞ。もちろん…一番大好きなのは、リーダーだ！リーダーは構ってくれるし、パロネスを助けてくれたし、良いヤツだからだ！パロネスの一番、誇っていいぞ！"}
, 
[105301001] = {id = 105301001, name = "あまり不埒な真似をすると、手のひらに穴が開くわよ？"}
, 
[105301002] = {id = 105301002, name = "何か用かしら？クエストでも冒険でも、力になれるなら手を貸すから声をかけて。"}
, 
[105301003] = {id = 105301003, name = "私はあなたと仲良くしたくて、ここにいるわけじゃないわ。だから…馴れ馴れしくするのはやめて。"}
, 
[105301004] = {id = 105301004, name = "リーダーは私といるのが怖くないの？だって、あなたは弱いアンレッドで…私がその気になれば、いつでも仕留められるのに…"}
, 
[105301005] = {id = 105301005, name = "見極めるべきあなたの隣で、こんなにも安らいでしまっている…おかしな話ね…。でも、あなたに常識なんて意味がないってわかってる。あなたは本当に…特別な人よ。"}
, 
[105300901] = {id = 105300901, name = "ふふっ、気安く触れるのね。これがあなたじゃなかったら、今頃命はなかったわ。見逃す代わりに…楽しい戦いを用意して頂戴ね。"}
, 
[105300902] = {id = 105300902, name = "ふふ…私に触れるとき、自分がどんな顔をしているかわかってる？私が戦ってるときみたいに…とっても興奮した顔してるわよ？"}
, 
[105300903] = {id = 105300903, name = "夏の一日が長いわ。でも…面白いと思える戦いは一瞬で終わってしまうの。とっても寂しいから…今晩はなぐさめてくれないかしら、リーダー？"}
, 
[105300904] = {id = 105300904, name = "ちょうどよかった。商人のお嬢ちゃんから『日焼け止め』というものを貰ったの。リーダー、暇なら塗るのを手伝ってくれるかしら？体の隅から隅まで…たっぷりと。"}
, 
[105300905] = {id = 105300905, name = "あなたは私が思いつきもしなかった、新しい快感を教えてくれる…でも、そのせいでもっと快感が欲しくてたまらなくなるの…。ねぇリーダー、早く…戦わせてちょうだい？"}
, 
[105211201] = {id = 105211201, name = "うひゃっ。リーダー、くすぐったいよー！水着だと、いつもよりすっごくくすぐったい！だから、もうやめてー！"}
, 
[105211202] = {id = 105211202, name = "どうしたの、リーダー？あっ、もしかして…喉が渇いてきちゃった？それじゃあ海の家でお水もらってこよっか！"}
, 
[105211203] = {id = 105211203, name = "リーダー、『浮き輪』っていう遊び道具があるんだって！キラキラの海でぷかぷかって浮けるらしいんだ。ノンノもリーダーとぷかぷかしてみたいなぁ！"}
, 
[105211204] = {id = 105211204, name = "この間、イザベラさんに日焼け止めを塗るコツを教えてもらったの！リーダーも塗ってほしかったら、ノンノに言ってね！　背中から足まで…綺麗に塗ってあげるよ！"}
, 
[105211205] = {id = 105211205, name = "あのね、みんなで涼しくなろうって怖～い話をしたの。そしたら、頭から怖～い話が離れなくなっちゃって…だから…ねぇリーダー…。今晩、一緒に寝てもいい…？　"}
, 
[105211101] = {id = 105211101, name = "ふふ、大丈夫ですよ。こんなに近くにいるんですから、あなたのことを忘れたりしません。…ええと、でもお名前だけ教えてもらっていいですか？"}
, 
[105211102] = {id = 105211102, name = "実は私、道に迷うことはないんです。だから今度、一緒にお出掛けでもどうでしょうか？リーダーさんと一緒なら、きっとどんな場所でも素敵な思い出が作れる気がするんです。"}
, 
[105211103] = {id = 105211103, name = "あなたの手はすごく温かいんですね。ずっと触れていたら…火傷してしまいそうです。だから私に触れるときは、優しくお願いしますね…♪"}
, 
[105211104] = {id = 105211104, name = "忘れっぽいままでもいいと思うことがあるんです。だってそうすれば、何度も“いいこと”が聞けたりしますから。ふふ、それじゃあ早速…あなたの素敵なお名前を、私の耳元で聞かせてくれますか？"}
, 
[105211105] = {id = 105211105, name = "あなたは私の宝物です。あなたと過ごした時間も、すべて…。今では忘れることよりも、あなたに忘れられてしまうほうが…怖いです。どうか私といた今この時を、覚えていてくださいね。"}
, 
[105220801] = {id = 105220801, name = "おっと、お触りは厳禁だよ。たとえ対価に豪邸を用意してもね。もしルルリカに同じことしたら…生まれたのを後悔させてあげる。なーんてね♪"}
, 
[105220802] = {id = 105220802, name = "あたし今、どんな豪邸に住むか考えるので忙しいんだよね。あ、いっそリーダーも一緒に考える？　リーダーはどういう豪邸がいい？　思いっきり派手なの想像してよ！"}
, 
[105220803] = {id = 105220803, name = "ちょっとー、あたしに絡むんなら、食事ぐらいおごってもらわないと困るよー♪　ふふふ。じゃあさっそくレストランデートへ行こうかね、リーダー？"}
, 
[105220804] = {id = 105220804, name = "何、甘えたいの？　ルルリカ以外の面倒は見ないようにしてるんだけど…。ま、あんたには普段世話になってるし…特別に、ちょっとぐらいは世話してあげてもいいかな？　ちょっとだけ、ね。ふふ。"}
, 
[105220805] = {id = 105220805, name = "あたしの考えることは、ずっと豪邸とルルリカのことだけだったのに…最近は、あんたが勝手に頭の中に出てくるんだ。別にあんたについて考えることないはずなのに…なんだか、心が安らぐ気がするんだ。"}
, 
[105301101] = {id = 105301101, name = "きゃっ…い、いけませんリーダーさん…。わたしなんかに触れたりしたら…何が起こるかわかりませんから…もうこんなことは、やめてください…"}
, 
[105301102] = {id = 105301102, name = "このドレスは、ずいぶん前にハミィがくれたもので…古くなってきたんですけど、大事に手入れして着てるんです。ふふ…リーダーさんも気に入ってくれたなら、とっても嬉しいです。"}
, 
[105301103] = {id = 105301103, name = "「どうか私を導いて、孤独から救ってくれ。あなたのそばに、私が必要だと言ってくれ」――きゃっ…リ、リーダーさん、いらしてたんですね…。い、いえ…今のはその、ただ好きな劇のセリフで…告白とかじゃ…あぅ…"}
, 
[105301104] = {id = 105301104, name = "ふふ、ルリはここにいますよ。リーダーさんの前なら…物陰じゃなくても、大丈夫みたいです。ううん、むしろあなたの前にずっと――い、いえ、やっぱり、なんでもありません…"}
, 
[105301105] = {id = 105301105, name = "ルリにどんな役を演じて欲しいですか？　あなたが望む役なら、なんでもやってみたいんです。…あなただけの前で、あなただけのための、特別なルリを…ふふ。"}
, 
[105301201] = {id = 105301201, name = "かわいいミアと一緒にいるんだし、リーダーもうれしいに決まってるよね！うふふっ♪これからももーっと可愛くなって、もーっと嬉しくさせてあげる♪"}
, 
[105301202] = {id = 105301202, name = "リーダー、ミアに見とれちゃった？ふふーん。そんなに見たいなら、どこでもいくらでも見ていいんですけど！右からでも、左からでも、上からでも、下からでもっ…って、下からだけはダメなんですけど！"}
, 
[105301203] = {id = 105301203, name = "おさわりは禁止なんですけど…まあ、リーダーなら少しぐらい、許してあげてもいいけど。だって…ミアのお洋服が可愛いから、素材が気になっちゃったんでしょ！？それならしょうがないもんっ！許すっ！"}
, 
[105301204] = {id = 105301204, name = "誰にかわいいって思ってもらえるかも大事だって、最近思うようになったんだ。その理由とか、相手とかはー…秘密！女の子はね、秘密を持ってるともっと可愛くなるし♪"}
, 
[105301205] = {id = 105301205, name = "いつかかわいいじゃすまないくらい、リーダーを夢中にさせちゃうファッションを作って見せるから！リーダーだけにしか見せない、とっておきのミアを覚悟してて！"}
, 
[105220901] = {id = 105220901, name = "ふわぁ…仮装もパーティもいたずらも…楽しそうだけど、眠気には勝てないよ…。おやすみ、リーダー…すぅ…"}
, 
[105220902] = {id = 105220902, name = "今、占いをしてたところだよぉ。ハロウィンパーティが上手くいくかって。でも…リーダーがいるなら、占うまでもなく大成功だね。ふふ。"}
, 
[105220903] = {id = 105220903, name = "どうしたの、リーダー？　何か懺悔したいことでもあるの？　ふふ…じゃあシスタークインが特別に、リーダーの悩みを聞いてあげる～。占いのおまけつきでねぇ。"}
, 
[105220904] = {id = 105220904, name = "カボチャのクッキー、一緒に作らない？　中にね、占いが書かれた小さな紙も入れてあげるの。もちろんリーダーの分も用意するよ～。特別に大きな大きなクッキーにするからね、ふふ。"}
, 
