[97030162] = {id = 97030162, name = "リーダーは、寝るのが好きなの～？　あたしもねぇ、大好きなんだ～。食べて寝て、食べて寝て、たまにばんばん撃って…そんな毎日を送り続けたいものだね～。"}
, 
[97030163] = {id = 97030163, name = "暇だし～久しぶりにショウちゃんとかくれんぼでもしたいなぁ。ショウちゃんはねぇ、隠れるのがすっごく上手で全然見つからなかったんだ。でも、あたしが隠れる側だとすぐ見つけるの。ずるいよねぇ～。"}
, 
[97030164] = {id = 97030164, name = "ふんふふーん♪　今ねぇ、愛しの二丁拳銃ちゃんを磨いてるんだ～。さーちゃんとうーちゃんは、構ってあげないとすぐにすねちゃうから～。よしよーし、次の戦いでもバンバン撃ってね～♪"}
, 
[101301601] = {id = 101301601, name = "你是哪个…啊，こんにちは～、東の国から来たリンさんだよ～。えっとー、特技は食べることと、ゆっくりすることと～…あと、ばんばん撃つことかな？　えへへ、よろしくね～。"}
, 
[101301611] = {id = 101301611, name = "リーダーが頑張ってたから、つい釣られて頑張っちゃっただけなのに…お腹いっぱい食べたときぐらい、幸せな気分になれちゃった～。えへへ、ありがとねリーダー。"}
, 
[101301621] = {id = 101301621, name = "頑張ったらリーダーが褒めてくれるから、調子に乗ってばんばん撃ってたら強くなっちゃった！　えへへ、本当に乗せ上手だねぇリーダーは。期待に応えて、これからももっとばんばん撃っちゃうよ～！"}
, 
[102301601] = {id = 102301601, name = "おかえり～。リーダーもまんじゅうの匂い、かぎつけちゃった？"}
, 
[103301601] = {id = 103301601, name = "すごいすごーい！　あたしもリーダーもすごーい！"}
, 
[104301601] = {id = 104301601, name = "おお～みなぎってきた！　これなら今までより…いっぱい食べられそぉ～！"}
, 
[105301601] = {id = 105301601, name = "ごめんね～リーダー。今リンさんは休憩中なんだ～。ご飯食べてお昼寝して、ご飯食べてお昼寝した後ぐらいに来てくれたら…やっと遊ぶ元気が出てくるかも～。"}
, 
[105301602] = {id = 105301602, name = "あ、隠してた食べ物、気付いちゃった？しょうがないなぁ…少しだけなら分けてしんぜよう。ほら。笹麻婆豆腐に、笹青椒肉絲に、笹酸辣湯、笹棒棒鶏…好きなものを選んでいいよ♪"}
, 
[105301603] = {id = 105301603, name = "あ～あ～いいよいいよ。好きなだけ、つんつんすればいいよっ！　…えへへ、でも意外と太ってないでしょ～？　食べても太らないのが自慢なんだ～うらやましい？"}
, 
[105301604] = {id = 105301604, name = "リーダーはあたしとふたりで、何したい？　のんびりお昼寝？　美味しいもの食べる？　きれいな景色を観に行く？　えへへ～。リーダーと一緒なら、どこでもなんでもどーんと来いっ、だよぉ！"}
, 
[105301605] = {id = 105301605, name = "旅は大変だったよ。お腹が空いたり、寂しかったり…でもリーダーと出会えたから、ここまで来て良かった～って今は思うんだ。もしかしたらあたし、リーダーに会うために旅に出たのかも？　えへへ、リーダーもそう思ってくれる～？"}
, 
[106301601] = {id = 106301601, name = "頑張ったから、お腹空いちゃったよ～。"}
