[97030241] = {id = 97030241, name = "昔は風みたいにとっても速く走れたのよ。でも…なぜか年々足が重くなって、肩もこっちゃって…これが大人になるってことなのかしら…？"}
, 
[97030242] = {id = 97030242, name = "走る特訓は毎日しているのだけど、独学じゃ限界があるみたい…。うーん、姿勢が良くないのかしら。それとも脚が鍛え足りないのかしら…？　誰かが見てくれるといいのだけど…"}
, 
[97030243] = {id = 97030243, name = "今、服をもっと軽くできないか考えていたところよ。だってほら、着てるものが軽ければ軽いほど、足が速くなるはずでしょう？　だから…うーん、もっと切り込みを深く入れちゃおうかしら…？"}
, 
[97030244] = {id = 97030244, name = "クリスマスについて考えるだけで楽しくなっちゃうの。プレゼントは何をあげようか、どんな包装紙やリボンにしようか、なんて…。ふふ、毎日こんなことばかり考えられて、私ってほんと幸せ者ね♪"}
, 
[101302401] = {id = 101302401, name = "私はヨウル。いい子達にクリスマスプレゼントを届けるのが役目よ。脚はちょっぴり遅いけど…期待して待っていてね♪"}
, 
[101302411] = {id = 101302411, name = "もう大人だから、クリスマスプレゼントはもらえないし、成長だってしないと思ってた。…でも、あなたがたくさんプレゼントをくれて、私はもっと素敵になれたの。ありがとう、リーダー♪"}
, 
[101302421] = {id = 101302421, name = "あなたのおかげで、前より速く走れてるみたい！　…でもときどき、止まってしまいたくもなるの。だって、あなたのそばにずーっといたいから♪　ふふ、今度は私が引くソリに乗って一緒にデートでもしちゃいましょうね♪"}
, 
[102302401] = {id = 102302401, name = "お疲れ様。私がいなくても、いい子にしてたかしら？"}
, 
[103302401] = {id = 103302401, name = "もっともっと速くなってみせるんだから！"}
, 
[104302401] = {id = 104302401, name = "まぁ、これならもっと速く走れちゃう！"}
, 
[105302401] = {id = 105302401, name = "こらこら、そんなことをする悪い子はプレゼントがもらえないわよ？　いい子ならまず「触っていいですか？」って聞くこと！　…って、そうじゃない？"}
, 
[105302402] = {id = 105302402, name = "なあに、リーダー。今ホットミルクでも用意して、ゆっくりしようとしていたところよ。リーダーもミルクが好きなら、一緒にどう？"}
, 
[105302403] = {id = 105302403, name = "私だってクリスマスプレゼントは欲しいものよ。たとえば…とっても軽い服とか、揺れると可愛いアクセサリーとか…それから、あなたの愛、とかね♪　ふふ、楽しみにしているわよ♪"}
, 
[105302404] = {id = 105302404, name = "あ、もしかして肩を揉んでくれるのかしら？　助かるわ、本当にずぅっと凝りっぱなしだから…。でも子供達にプレゼントを届けるためなら、これぐらいなんてことない。…あなたとこうして触れ合うこともできるし、ね♪"}
, 
[105302405] = {id = 105302405, name = "あなたって本当は、本物のサンタさんなんじゃないかしら？　だって毎日がクリスマスみたいに、私の胸をドキドキ熱くさせるんだもの。…ふふ。これじゃあ辺りの雪も全部溶けちゃいそうだわ、もう♪"}
, 
[106302401] = {id = 106302401, name = "ふふ、これからはいい子にしていることね♪"}
