[97022143] = {id = 97022143, name = "ふふふ、次はどこへ取材に行こうかな～。何せこれさえ着ていれば、東の国ならどこへでも行けるわけですから！　そうですよね、リーダー！"}
, 
[97022144] = {id = 97022144, name = "潜入取材のために、家にもいろんな変装道具があるんですよ。シスター服とか、騎士団の鎧とか…。これでまたコレクションが増えちゃいましたね！"}
, 
[101221401] = {id = 101221401, name = "はいはーい、面白いネタを探しにどこまでも…シルヴィですよー！　この格好は巫女服ってやつで、潜入取材のために買ったんです！　えへへ、結構似合ってると思いません？"}
, 
[101221411] = {id = 101221411, name = "おかげさまで神様だって気付かないぐらい、立派な巫女になれました！　リーダーにはお礼に、大吉しかないおみくじ用意しておきます！"}
, 
[101221421] = {id = 101221421, name = "リーダーがどんな私でも受け入れてくれるから、私も思いっきりムチャができるんです！　かならず最高の記事を出して見せますから、期待しててくださいね！"}
, 
[102221401] = {id = 102221401, name = "リーダーの今日の運勢は「大吉」！　ただし、私のネタ集めに協力してくれたら、ですけど～。"}
, 
[103221401] = {id = 103221401, name = "もっと見えてきましたよ、良いネタが！"}
, 
[104221401] = {id = 104221401, name = "これはもはや、本物の巫女ですよっ！　あ、もちろん本職は記者なんですけど！"}
, 
[105221401] = {id = 105221401, name = "リーダーがそんなことをするなんて、本物の悪霊に取りつかれてるんじゃ…！？　こういうときは、たしか塩を大量に飲ませればいいはずなので…リーダー、我慢してくださーい！"}
, 
[105221402] = {id = 105221402, name = "今ちょうど、神様からお告げがあったところですよ。「リーダーが特大スクープを教えてくれる」って！　当たってます！？"}
, 
[105221403] = {id = 105221403, name = "くしゅんっ…！　うう、この巫女服…冬だと結構寒いんですよね。でも…リーダーが心配そうにしてくれるのは、嬉しいですね。えへへ。"}
, 
[105221404] = {id = 105221404, name = "リーダー、取材させてくださいっ。普段の私と巫女服の私、どちらが好きですか！　いつだってリーダーが好きな私でいたいので、ここは素直にお願いしますっ！"}
, 
[105221405] = {id = 105221405, name = "私の今年の目標は、リーダーの記事を一面に載せることです！　だって最高のリーダーだって、皆さんに知ってほしいですから！　…でも、私しか知らないリーダーの顔も残しておいてくださいね♪"}
