, 
[101301711] = {id = 101301711, name = "馬子にも衣裳…かもしれないが、振袖が少しは似合うようになったか？　…わかってる、これはリーダーのおかげだ。礼を言う。"}
, 
[101301721] = {id = 101301721, name = "リーダーには、今年もこの調子で世話になるだろうな。…それに見合う働きは約束する。ひとまず今は…甘酒で乾杯だ。"}
, 
[102301701] = {id = 102301701, name = "おせちを食べたら仕事に行くぞ、リーダー。"}
