[104301701] = {id = 104301701, name = "慣れない格好であっても、私は成長してみせる。"}
, 
[105301701] = {id = 105301701, name = "振袖に興味を持つ気持ちはわかる。でも…あまり気安く触るな。もし着崩れたら、私達だけじゃ直せない。"}
, 
[105301702] = {id = 105301702, name = "…さ、触るな。…その、こちらに来てから、おせちやお雑煮をたくさん食べすぎたせいか…体型が…いや、なんでもない。とにかく触るな！"}
, 
[105301703] = {id = 105301703, name = "…手を差し出すな。振袖でも、ひとりで歩ける。でも…段差があるときには声をかける。…助かる、ありがとう。"}
, 
[105301704] = {id = 105301704, name = "…風が冷たいから、私の風よけになってもいい。だから…つまり…私のそばにいてくれて、いい。"}
, 
[105301705] = {id = 105301705, name = "…リーダーも、来年は着物を着るといい。それで私と年を越して、初詣へ行って、甘酒を飲むんだ。…ふふ。楽しみが増えるのは、悪くないだろう？"}
