[117110501] = {id = 117110501, name = "ふっふっふ…ギャンブルの必勝法を手に入れました！　今度カジノに行ったら、ディーラーをこてんぱんにしてやります！"}
, 
[117110502] = {id = 117110502, name = "リーダー、どうして冒険なんて大変な仕事をやってるんですか？　もっと楽に稼ぐ方法があるかもしれないのに…"}
, 
