[117310201] = {id = 117310201, name = "主人の意志に従い、主人の命令を実行する…これこそ、私が存在する意味です。"}
, 
[117310202] = {id = 117310202, name = "どこにいても、私はあなたの忠実なしもべです。リーダーのご命令をお待ちしています。"}
, 
[117310203] = {id = 117310203, name = "あなたが望むことであれば、いかなる努力も…いいえ、なんとしてでも叶えてみせます！"}
, 
[117310301] = {id = 117310301, name = "今のうちに、武器の手入れでもしようかしら。いつでも戦えるようにしておかなくちゃいけないもの。"}
, 
[117310302] = {id = 117310302, name = "面白い戦いができるなら、すべて私に任せていいわ！"}
, 
[117310303] = {id = 117310303, name = "みんなとできる、良い『遊び』があるんだけど…ふふ、安心しなさい。問題は起こらないようにするから。"}
, 
[117310401] = {id = 117310401, name = "ま、ここも悪くないんじゃない？　労働しなくてよくて、暇つぶしの道具があればね"}
, 
[117310402] = {id = 117310402, name = "クエストなら、あっちの子たちに任せたら？あたしは忙しいから。…だらだらするのに。"}
, 
[117310403] = {id = 117310403, name = "誰かゲームとかテレビ、持ってきてくれないかなー…でも電気ないから意味ないかー……"}
, 
[117310501] = {id = 117310501, name = "能ある鷹は爪を隠す……心頭滅却すれば火もまた涼し……"}
, 
[117310502] = {id = 117310502, name = "天晴だ、こなた……寝るのに適した場所でござるな！"}
, 
[117310503] = {id = 117310503, name = "良き風景でござるな……共にお茶を飲むでござるか？"}
, 
[117310601] = {id = 117310601, name = "少しでも世界が平和になるのなら…わたくしは、強くなりたいのです。"}
, 
[117310602] = {id = 117310602, name = "どんなことでも、わたくしにやらせていただけませんか？　わたくしはもっといろんな経験をしなくてはいけませんからっ。"}
, 
[117310603] = {id = 117310603, name = "ブリジットは今どちらにいるでしょうか？　皆様と仲良くやっているといいのですが…"}
, 
[117300801] = {id = 117300801, name = "いっけない、もう仕事に行かないと！まぁ、今日も喫茶店のほうだけど。"}
, 
[117300802] = {id = 117300802, name = "歌の練習は他の子に聞かれたくないのよね…。だって、まだ下手っぴだし…"}
, 
[117300803] = {id = 117300803, name = "ちょっとこのプライド、可愛い子が多すぎじゃない？あんたまさか…顔で選んでるんじゃ…"}
, 
[117110701] = {id = 117110701, name = "にひひっ…今度はどこに落とし穴を掘ろうかな…"}
, 
[117110702] = {id = 117110702, name = "ここにはパロネスを構ってくれるヤツが多い！うん、満足だ！"}
, 
[117110703] = {id = 117110703, name = "あーっ、かくれんぼ見つかっちゃった！次はリーダーが隠れる番だ！"}
, 
[117301001] = {id = 117301001, name = "物陰や死角にばかり目が向いてしまうのは、私の良くない癖ね。"}
, 
[117301002] = {id = 117301002, name = "このプライドは、本当に自由ね。…嫌いな雰囲気では、ないけれど。"}
, 
[117301003] = {id = 117301003, name = "私のことは気にしなくて結構よ。これでも好きにやってるの。"}
, 
[117300901] = {id = 117300901, name = "リーダーは平和すぎることを退屈だと思わないのかしら？私に言ってくれれば、もっと刺激的な夏を送らせてあげるけど？"}
, 
[117300902] = {id = 117300902, name = "この格好は涼しいし危なくて気に入ってるけど…日差しが当たる場所にずっとはいたくないわ。私はそういうレッドなの。"}
, 
[117300903] = {id = 117300903, name = "リーダーは直接戦わないとはいえ、夏ごときでバテているようじゃ私の相手は務まらないわよ。"}
, 
[117211201] = {id = 117211201, name = "他のみんなの水着も見てみたいな～。きっとすっごく可愛いと思うんだっ！リーダーも見てみたいよね？"}
, 
[117211202] = {id = 117211202, name = "ノンノも上手に泳げるようになりたいなぁ…。泳げるようになったら、海の底まで探検に行きたい！そしたら楽しそうだもん！"}
, 
[117211203] = {id = 117211203, name = "海から上がった後は、体がずーんってするよね。それがなんだか気持ち良くて不思議なの。…でも、ちょっと眠くなっちゃったりして…ふわぁ。"}
, 
[117211101] = {id = 117211101, name = "まだまだ皆さんの顔も名前も覚えられていませんが…ここはにぎやかで、とても楽しいです。"}
, 
[117211102] = {id = 117211102, name = "ここにいる方々は、リーダーさんのお友達でしょうか？それとも…ご家族とか？"}
, 
[117211103] = {id = 117211103, name = "このお部屋は、急に寒くなったりしませんか…？　"}
, 
[117220801] = {id = 117220801, name = "ここさえあれば、ホテル代が浮いて助かるねー！　節約節約ーっと♪　あ、ちなみに食事とかもあれば、もっと助かるんだけど…どうかなー？"}
, 
[117220802] = {id = 117220802, name = "こういう物陰があるところは助かるよ。ルルリカは、とにかく人目につかない場所へ行くためなら手段を選ばないからね。「例え火の中水の中」…って、嘘じゃなかったんだよね…"}
, 
[117220803] = {id = 117220803, name = "むむっ、あのメンバーも女優に向いてるかも！　…なーんちゃって。あたしはルルリカの面倒を見るのに精いっぱいだからね、他の子に手出ししないよ♪"}
, 
[117301101] = {id = 117301101, name = "ふふ…ずっと宿暮らしだったから、こういう場所にいられるのはなんだか不思議な気持ちです。ずっとここに、いられたら…なんて、わがままを言っちゃいけませんよね。"}
, 
[117301102] = {id = 117301102, name = "ここにいる皆さん、とっても優しくて…お芝居の練習を手伝ってくれたりするんです…。でも…恥ずかしくて目を合わせられなくて、気が付いたら物陰に隠れてしまって…うぅ、申し訳ないです…"}
, 
[117301103] = {id = 117301103, name = "い、いつもなら、たくさんの人がいると緊張しちゃうんですが…ここはなんだか、とっても心地いい気がします…どうしてでしょう…？"}
, 
[117301201] = {id = 117301201, name = "このハウスくらいのクローゼットが欲しいかも。パパにおねだりしよっかな？"}
, 
[117301202] = {id = 117301202, name = "いつもかわいくいるためには、家の中でもしっかりキメるのが大事なの！"}
, 
[117301203] = {id = 117301203, name = "ねえリーダー、足のマッサージとか…してほしいんですけど。"}
, 
[117301301] = {id = 117301301, name = "菓子で腹もいっぱいになったし、ひと眠りしたいところでござるが…この格好で昼寝などしたら、本物の死体と勘違いされそうでござるな。"}
, 
[117301302] = {id = 117301302, name = "ぴょんぴょんぴょんっ、もひとつおまけにぴょん！　でござる。…ふむ、少しずつ本物のキョンシーに近づいているでござるな。"}
, 
[117301303] = {id = 117301303, name = "拙者がぴょんぴょんと近づくと、他のメンバーがキャアっと声を上げるでござる。楽しんでもらえて何よりでござるよ。"}
, 
[117220901] = {id = 117220901, name = "本来見えないものが見えやすくなる季節があるんだ。それをリーダーの世界では、『ハロウィン』って呼ぶんだね。"}
, 
[117220902] = {id = 117220902, name = "お菓子が欲しくなったらいつでも言ってね。いつでも渡せるよう用意してあるの。"}
, 
[117220903] = {id = 117220903, name = "おお神よ～。…あ、今はシスターらしく練習してたところだよ。アイリスにもいろいろ教えてもらおうかなぁ。"}
, 
[117301401] = {id = 117301401, name = "うーん……改めて見てみると、きれーで目立つ人たちばっかり！　でもロールも負けないよう、目立ってみせるよー！"}
, 
[117301402] = {id = 117301402, name = "ここはリーダーもみんなもいるから、全然寂しくないね、えへへ。リーダー、ロールをずーっとここにいさせてね？"}
, 
[117301403] = {id = 117301403, name = "お外でひなたぼっこも大好きだけど…広いお部屋で、のんびりするのも大好き～！　今日はここでお昼寝してごろごろしよーっと！"}
, 
[117221001] = {id = 117221001, name = "サンタは煙突から家に侵入すると言いますね。しかし、サンタが汚れた絵は見たことがありませんから…きっと煙突を掃除しながら入るのでしょう。サンタ、なかなか侮れませんね。"}
, 
[117221002] = {id = 117221002, name = "リーダー様、外の雪だるまをご覧になりましたか？　メンバーの方々がお作りになったようです。…さすがのわたくしも、あちらは排除いたしませんよ。"}
, 
[117221003] = {id = 117221003, name = "部屋の飾りつけをするというなら、まずはもちろん、完膚なきまでに掃除をしてからです。"}
, 
[117301501] = {id = 117301501, name = "リボンがすぐに緩んでしまうから、結び直しているところだよ。だって君以外の前でこれがほどけたら、大変だろう？"}
, 
[117301502] = {id = 117301502, name = "お嬢達のプレゼントに、本や紅茶を用意しているんだ。そうそう、オリヴィアのお嬢にも可愛いぬいぐるみを贈ってあげないと。"}
, 
[117301503] = {id = 117301503, name = "せっかくだから、ここもいろいろと飾り付けをしたいね。パーティが盛り上がるし、準備している時間も楽しいものだからね。"}
, 
[117301601] = {id = 117301601, name = "みんな～、一休みしておやつでも食べようよ～。ほら、リーダーもたーんとお食べー♪"}
, 
[117301602] = {id = 117301602, name = "ばんばん撃ちたい気分だし、外へ行こうかな～ってさっきから思ってるんだ。でも、ここにいるとのんびりしたさが止まらなくてぇ～…ぐうたら～。"}
, 
[117301603] = {id = 117301603, name = "早くクエストや遠征に行きたいな～♪　だってほら、みんなで海とか山とか行くの楽しいから！　…あ、もちろん戦いもするよ。こう、気持ちがやるぞー！　ってなったら、ね？"}
, 
[117302401] = {id = 117302401, name = "ここにいるのはいい子ばっかりね♪　クリスマスプレゼントは期待しててちょうだい♪"}
, 
[117302402] = {id = 117302402, name = "いち、に、いち、に…ふぅ。こういう時間こそ来たるべき配達に備えて、ちょっとでも鍛えなくちゃ！"}
, 
[117302403] = {id = 117302403, name = "外に出て走るのは楽しいけど、家の中も好きなのよ。次の年のプレゼントについて、ゆっくり考えられるし…♪"}
, 
[117221401] = {id = 117221401, name = "ここじゃ変装する意味はないんですが…皆さんに可愛いって言ってもらえるので、つい♪"}
, 
[117221402] = {id = 117221402, name = "はっ、お札にこっそり記事のネタを書き込めば…より潜入取材がはかどるのでは！？"}
, 
[117221403] = {id = 117221403, name = "もしエセ巫女が必要なときは声をかけてください。それっぽいことしてみせますから！"}
, 
[117301701] = {id = 117301701, name = "…もらった餅が余っているから、メンバー達に配るところだ。…リーダーもいる？"}
, 
[117301702] = {id = 117301702, name = "…こたつに、みかん…お雑煮に、甘酒…別に、学んだ言葉を言っているだけ。全部欲しいとか、そういうんじゃない。"}
, 
[117301703] = {id = 117301703, name = "さっき、エイプリルに「帯回しがしたい」と言われて…断ったが、リーダーはどういうものか知っている？"}
, 
[97030301] = {id = 97030301, name = "リーダーは、休憩中か？…私も空を眺めるのは好きだから、気持ちはわかる。何もしない時間は…いいものだ。"}
, 
[97030302] = {id = 97030302, name = "…私を見つめるのが、そんなに楽しいのか？プデルのようにすぐ顔色を変えたり、尻尾を振ったり下げたりはしないが…。"}
, 
[97030303] = {id = 97030303, name = "『ペンデュラム』は、振り子を意味する。決して正義にも悪にも振り切らないことでこそ、手に入れられるものがあるからだ。"}
, 
[97030304] = {id = 97030304, name = "あなたはいつも、メンバーを気にかけているな。それゆえに、厚い信頼が得られているんだろう。…私も見習わねばな。"}
, 
[101303001] = {id = 101303001, name = "…クーニャだ。ペンデュラムのボスを務めている。リーダーに借りた恩は、必ず返させてもらうとしよう。"}
, 
[101303011] = {id = 101303011, name = "父が倒れた後、私は一人で立ち向かうしかないと思っていたが…どうやら違ったようだ。私には、プデルもリーダーもそばにいる…こんなに心強いことはない。"}
, 
[101303021] = {id = 101303021, name = "恩返しをするはずが、あなたには面倒を見てもらうばかりだ。このままだと、一生あなたのもとを離れられないかもしれないな。…しかし、それも悪くはない。"}
, 
[102303001] = {id = 102303001, name = "次にどこかへ行くときには、声をかけてくれ。私も共に行動したい。"}
, 
[103303001] = {id = 103303001, name = "強くなるとは、こうも嬉しいものか…"}
, 
[104303001] = {id = 104303001, name = "私は未熟だ。だがそれはまだ、強くなれる…ということか。"}
, 
[105303001] = {id = 105303001, name = "そんなことをすると、陰で見ている部下達がリーダーの命を奪いかねない。やめておいたほうがいい。"}
, 
[105303002] = {id = 105303002, name = "こうした立場にいると、人を判断せざるを得ない。敵か、味方か、愚か者か、そうでないか。…だから、あなたのような存在がとても頼もしいんだ。"}
, 
[105303003] = {id = 105303003, name = "…ふっ。私にイタズラを仕掛けるなんて、あなたぐらいだ。お返しに、私もあなたの背中を引っかいて…痕でもつけてしまおうか？"}
, 
[105303004] = {id = 105303004, name = "あなたは、撫でるのが上手だな。とても気持ちがいい。…だから、もっと撫でてくれるか？　頭も、顎も…その他のところも。"}
, 
[105303005] = {id = 105303005, name = "リーダー。どうか強いと言ってくれ、可愛いと言ってくれ。手を取ってくれ、撫でてくれ。…あなたのすべてが、私の力になる。"}
, 
[106303001] = {id = 106303001, name = "この程度でお終いか？"}
, 
[107303001] = {id = 107303001, name = "…私は未熟者だな。"}
, 
[108303001] = {id = 108303001, name = "ペンデュラムの名にかけて…"}
, 
[111303001] = {id = 111303001, name = "はぁっ！"}
, 
[111303002] = {id = 111303002, name = "行くぞ"}
, 
[111303003] = {id = 111303003, name = "愚かな"}
, 
[111303004] = {id = 111303004, name = "まだやるか？"}
, 
[111303005] = {id = 111303005, name = "ふっ"}
, 
[111303006] = {id = 111303006, name = "腹はくくったか？"}
, 
[112303001] = {id = 112303001, name = "くっ"}
, 
[112303002] = {id = 112303002, name = "何を！"}
, 
[112303003] = {id = 112303003, name = "やめておけ"}
, 
[112303004] = {id = 112303004, name = "なっ"}
, 
[112303005] = {id = 112303005, name = "うっ"}
, 
[112303006] = {id = 112303006, name = "舐められたものだな"}
, 
[113303001] = {id = 113303001, name = "父に顔向けできないな…"}
, 
[114303001] = {id = 114303001, name = "諦めが悪いな"}
, 
[114303002] = {id = 114303002, name = "歯を食いしばれ"}
, 
[115303001] = {id = 115303001, name = "我がペンデュラムに刃向かうならば――貴様を、潰すまで"}
, 
[115303002] = {id = 115303002, name = "私の敵になるとはどういうことか、わからせてやろう"}
, 
[116303001] = {id = 116303001, name = "外から狙われやすい窓はあるか？"}
, 
[117303001] = {id = 117303001, name = "…年頃の女性が多い場は、あまり慣れていないんだ。失礼があったら、すまない。"}
, 
[117303002] = {id = 117303002, name = "このプライドは、とても良い組織だな。それもリーダー、あなたの人徳があってこそだろう。"}
, 
[117303003] = {id = 117303003, name = "ここにいると、メンバー達がいろんなことをするから飽きないな。…だから、とても気に入っている。"}
, 
[97030261] = {id = 97030261, name = "私って、やっぱり普通に見えちゃうかなぁ…？　いろんな作戦で動きやすいけど、もっとマフィアっぽくもなってみたいような…"}
, 
[97030262] = {id = 97030262, name = "もともと癖っ毛だったんですが、レッドになってからさらに癖が強くなって、毎朝まとめるのが大変なんです。クーニャちゃんが「可愛い」って言ってくれるのが救いですかね…えへへ。"}
, 
[97030263] = {id = 97030263, name = "クーニャちゃんはちょっと怖く見えますが…とっても優しいんです。意外と抜けているところもあって…ふふ、可愛いんですよ。"}
, 
[97030264] = {id = 97030264, name = "私がもっと強くなるために必要なのは、身体能力に思考に経験に…うぅ、何もかも足りないなぁ…"}
, 
[101302601] = {id = 101302601, name = "プデルです…ええと、私はクーニャちゃんに比べたら何にもできないですが…でも私にできることは、全力でやりますっ…！　よろしくお願いします！"}
, 
[101302611] = {id = 101302611, name = "あ、ありがとうございます、リーダーさんっ…！　昔の私が見たら「まるで自分じゃないみたい」って驚きそうなぐらいです、ふふ！"}
, 
[101302621] = {id = 101302621, name = "私の人生が変わった瞬間が、二度あるんです。クーニャちゃんと出会ったとき、そして…リーダーさん。あなたと出会ったときです。素敵な人達に囲まれて…本当に、この道を選んで良かったです…！"}
, 
[102302601] = {id = 102302601, name = "今日の仕事のために、まずは作戦会議しましょうか！"}
, 
[103302601] = {id = 103302601, name = "わあっ…これでもっと戦える！"}
, 
[104302601] = {id = 104302601, name = "私、もっと強くなれるんだ…！"}
, 
[105302601] = {id = 105302601, name = "わっ…び、びっくりしたぁ…！　リーダーさん、急につつかないでください…私なんて驚かせても、面白くないですしっ…"}
, 
[105302602] = {id = 105302602, name = "どうしましたか？　もしかして何か相談事ですかね…？　ええと、お役に立てるかわかりませんが…私で良ければ一緒に考えさせてください。"}
, 
[105302603] = {id = 105302603, name = "い、いけません、リーダーさん！　私の身体は、本当に貧相なので…！　もっと筋肉をつけて、敵を思いっきり吹っ飛ばせるようになったときには、また…って、私は何の話をっ！？"}
, 
[105302604] = {id = 105302604, name = "ううっ…リ、リーダーさんがそばにいると、尻尾が勝手にぶんぶん動いちゃって困ります…。あっ、いえっ！　し、尻尾の動きには何の意味もないですよ！　はい、本当に…！"}
, 
[105302605] = {id = 105302605, name = "私は、特別になりたいんです！　クーニャちゃんやリーダーさんみたいに、信頼される存在に…！　だってあなた達は、私にとって…代わりのいない、とても大切な人達だから…！"}
, 
[106302601] = {id = 106302601, name = "やったぁ、作戦勝ちですねっ！"}
, 
