, 
[97022133] = {id = 97022133, name = "リーダー、もし暇なら『レイナちゃんのどこが可愛いか』についておしゃべりしましょうか。もちろん、レイナちゃんはぜーんぶ可愛いですが♪"}
, 
[97022134] = {id = 97022134, name = "たまに怖くなるんです。レイナちゃんが日々こんなに可愛くなっていったら…この先私、どうなっちゃうんだろうって…。幸せのあまり死んでもおかしくないですよね…。"}
, 
[101221301] = {id = 101221301, name = "初めまして、タネハと言います。突然ですが、可愛い女の子はお好きですか？　なら世界一可愛い女の子を教えてあげますね。それはもちろん、レイナちゃんです！"}
, 
[101221311] = {id = 101221311, name = "リーダーは、女の子を特別にする才能がありますね。ふふ…私もレイナちゃんに、少し追いつけた気がします♪"}
, 
[101221321] = {id = 101221321, name = "レイナちゃんが幸せなのを見守れればいいって思っていました。今は…あなたの幸せな顔も見たいって思うんです。三人で過ごせる時間を守るためなら、私はなんにでもなってみせます。"}
, 
[102221301] = {id = 102221301, name = "今日のレイナちゃんもとっても可愛いですから、見逃しちゃいけませんよ！"}
, 
[103221301] = {id = 103221301, name = "レイナちゃんへの愛も高まります♪"}
, 
[104221301] = {id = 104221301, name = "ありがとうございます、これでもっとレイナちゃんのためになれます！"}
, 
[105221301] = {id = 105221301, name = "ふふ、人肌恋しくなる時はありますよね。でも…もしレイナちゃんに同じことをしていたら…そのときリーダーに何をしちゃうのか、自分でも想像がつきません。"}
, 
[105221302] = {id = 105221302, name = "私に何か用ですか？　レイナちゃんに関することなら、いつでもなんでもいくらでもおしゃべりしますよ♪"}
, 
[105221303] = {id = 105221303, name = "はっ…どうして気づいたんですか！？　胸元にレイナちゃんの似顔絵を隠し持っていることを…！　仕方がありません、リーダーには特別に一枚差し上げましょう。"}
, 
[105221304] = {id = 105221304, name = "私よりもレイナちゃんと仲良くなる人がいたら、嫉妬しちゃうんじゃないかって思ってたんです。でも…リーダーにはそんな気持ち浮かばないんです。…レイナちゃん好きの同志、だからですかね？"}
, 
[105221305] = {id = 105221305, name = "私が世界で一番好きなのはレイナちゃんで、世界で一番頼りにしているのはリーダーなんです。そしてどちらも…一緒にいるだけで、心があたたかくなるんです。こんな素敵なふたりに出会えた私は、本当に幸せ者です。"}
, 
[106221301] = {id = 106221301, name = "えへへ、レイナちゃんに褒めてもらえるかな？"}
