[97030223] = {id = 97030223, name = "次はどんな曲をみんなで歌おうかなって、考えてました。…今度はリーダーも一緒に、合唱しませんか？　大きな声を出すの、とっても楽しいですよ。"}
, 
[97030224] = {id = 97030224, name = "おしゃべりは、誰かに届かないと寂しいけど…歌はひとりでも楽しかったから、好きになったんだと思います。"}
