[101302201] = {id = 101302201, name = "わたしはレイナ。誰にも届かなかったこの声を、あなたが受け取ってくれたから、わたしはここにいるんです…。えへへ…これからよろしくお願いします。"}
, 
[101302211] = {id = 101302211, name = "みんなと言葉が交わせること、この歌声が届くこと…。すべては、あなたがくれた喜びで…毎日が、怖いぐらい幸せです…ふふ。"}
, 
[101302221] = {id = 101302221, name = "わたしを見つけてくれただけでも、感謝しきれないのに…あなたはわたしも知らない、素敵なわたしをたくさん教えてくれる…。わたしを特別にしてくれてありがとう、リーダー。"}
, 
[102302201] = {id = 102302201, name = "こんにちは、リーダー。ふふ、挨拶できるのって、気持ちいい…♪"}
, 
[103302201] = {id = 103302201, name = "どんどん声が、響くようになってる…。"}
, 
[104302201] = {id = 104302201, name = "わたしの声を、歌を、もっと遠くへ届けたい…！"}
, 
[105302201] = {id = 105302201, name = "こ、こんにちは…！　…ご、ごめんなさい。おしゃべりが久しぶりだから、やっぱり緊張しちゃって…んん、ふぅ…挨拶の練習、したほうがいいかも…"}
, 
[105302202] = {id = 105302202, name = "ひゃっ…ふふっ、や、やめて…！　く、くすぐったがりだから、変な声出ちゃう…！　はぁ…はぁ…へ、変な声を聞かれるのって、結構恥ずかしい…。"}
, 
[105302203] = {id = 105302203, name = "あの…リーダーはどんな歌が好きか、教えてくれますか？　あ、えっと、ただちょっと気になっただけで…教えてくれたら、わたしもその歌を…歌ってみたいなと思っただけで…。"}
, 
[105302204] = {id = 105302204, name = "他人の目を気にしたことは、あまりないと思うんですが…リーダーから、わたしがどう見えるのか気になります。…すこしぐらいは、可愛く映っていたらうれしいな。ふふ。"}
, 
[105302205] = {id = 105302205, name = "今ならどんな言葉でも、伝えられるはずなのに…あなたを前にすると、息が詰まって、言葉にできなくなるんです。その代わりにどうか…わたしの瞳を見てください。ここに映るすべてが、あなたへの思い、だから…"}
