[117302202] = {id = 117302202, name = "タネハが時々「新衣装のため」って言って、サイズを測ってくれるんですけど…くすぐったくて、つい逃げちゃうんです。"}
, 
[117302203] = {id = 117302203, name = "元気になる歌、落ち着く歌、悲しくなる歌…リーダーは、今どんな歌が聞きたい気分ですか？"}
, 
[97030251] = {id = 97030251, name = "…疲れたか？　ならば某のことは気にせず、遠慮なく休まれるといい。寝ている間は某が警護しておこう。"}
, 
[97030252] = {id = 97030252, name = "静かな夜に刀を研いでいると様々な思念が浮かんでくる…。祖父母のこと、故郷のこと、師匠のこと、剣のこと…それから、リーダー殿のこともな。"}
, 
[97030253] = {id = 97030253, name = "浮世は見るに耐えんもので溢れている。故にリーダーも、時には目を閉じてみるといい。まことに美しいものは目を閉じていても見えることがわかるからな。"}
, 
[97030254] = {id = 97030254, name = "某で役に立てることがあれば、何でも言い付けるといい。何、目を閉じていようとも、案外とできることは多いものだ。"}
, 
[101302501] = {id = 101302501, name = "某はキキョウ、しばし貴公の世話になる。代わりに――貴公に仇なす者には、我が刃を以って相手をしよう。"}
, 
[101302511] = {id = 101302511, name = "某なりの義を果たすつもりで身を置いていたが…いつの間にか、ここを離れがたく思うようになった。某の刃、叶うならばこれからも貴公のためにこそ振るわせてほしい。"}
, 
[101302521] = {id = 101302521, name = "飛べない雛鳥であった某は、仇を得て強くなった。今再び成長できたのは…貴公のおかげだ。大切な何かのために強くなる…悪くないものだな…。"}
, 
[102302501] = {id = 102302501, name = "戻られたか。…息災ならば何よりだ。"}
, 
[103302501] = {id = 103302501, name = "刃は常に鋭く…振るうべきときのために。"}
, 
[104302501] = {id = 104302501, name = "新たな極意を得たやもしれん…。"}
, 
[105302501] = {id = 105302501, name = "リーダー殿、そう急に触れるな。某が反射的に、貴公を傷つけることもありうるのだからな。…貴公も、痛い思いはしたくないだろう？"}
, 
[105302502] = {id = 105302502, name = "貴公が気にかけてくれるのはありがたいことだが、某は面白い話はひとつもできないぞ。…だが、貴公が興味があると言ってくれるならば、旅の話でもするとしよう。"}
, 
[105302503] = {id = 105302503, name = "何、既に貴公には十分すぎるほど手を借りた。だが、もしまだ手を貸してくれるというならば…美味い飯屋へ案内してもらおう。そして貴公の飯の代金を、某が持つ。それでどうだ？"}
, 
[105302504] = {id = 105302504, name = "貴公の気配は、実に独特だ。どこか優しく、あたたかく、柔らかで…つまり、心地いいということだ。まさか育ての親以外でこのような者に会えるとは、人生もそう悪いものではないな。"}
, 
[105302505] = {id = 105302505, name = "貴公と出会って、今まで考えもしなかったことを思うようになった。例えば某にも、貴公と静かに暮らすような道もあるのではないか、とな…。…ふっ、某も所詮はただの人だったということか…。"}
, 
