[18220732] = {id = 18220732, name = "奥義2", remark = "そんなにあたしの武器が味わいたいのか？", voice_path = "Yewna_Battle_C_2", sort = 111, bubble_id = "14220700:2:441:247,14220701:2:441:247,14220702:2:441:247"}
, 
[18220733] = {id = 18220733, name = "戦闘勝利", remark = "いい武器があれば、どんな戦いも怖くないだろ？", voice_path = "Yewna_Win_1", sort = 120, bubble_id = "14220700:2:441:247,14220701:2:441:247,14220702:2:441:247"}
, 
[18220734] = {id = 18220734, name = "戦闘失敗", remark = "悪い、あたしの鍛錬がまだ足りなかったみたいだ…", voice_path = "Yewna_Fail_1", sort = 130, bubble_id = "14220700:2:441:247,14220701:2:441:247,14220702:2:441:247"}
, 
[18220601] = {id = 18220601, name = "メンバー募集", remark = "まさか我輩を召喚する、勇敢な人間がいるとはな…！　我が名は冥夜の堕天使カノン！　ククク…貴様のその勇気、称賛してやろう！", voice_path = "Canon_Get_1", sort = 10, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220602] = {id = 18220602, name = "ログインボイス", remark = "ククク…やっと来たか、我が盟友！　この深淵で待ちわびていたぞ！", voice_path = "Canon_In_1", sort = 20, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220603] = {id = 18220603, name = "待機ボイス1", remark = "天界か…ああ、もちろん知っている。いつの日か我輩は、天界を占領してみせると決めているのだ…！　人間よ、そのときは貴様の同行を許可しよう！　…こ、こらリーダー、断ろうとするんじゃないっ！", voice_path = "Canon_Main_1", sort = 30, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220604] = {id = 18220604, name = "待機ボイス2", remark = "ククク…この黙示録は我が生涯の記憶、そして多くの秘密が記されている…フフ、見てみたいだろう！　貴様が忠誠を誓うなら、見せてやらなくもないが！　どうだ、契約するか！", voice_path = "Canon_Main_2", sort = 31, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220605] = {id = 18220605, name = "待機ボイス3", remark = "フン！　神もしょせん、我輩の故郷である天界の記憶を封印することはできないのだ！　ん…天界はどんなところかだと？　そ、それはもちろん…て、天界らしいところだ！", voice_path = "Canon_Main_3", sort = 32, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220606] = {id = 18220606, name = "突破", remark = "ククク…よみがえれ、我が身に潜む暗き力よ！", voice_path = "Canon_Stage_1", sort = 40, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220607] = {id = 18220607, name = "立ち絵獲得1", remark = "光なき闇夜でさえ…この冥夜の力と影で、何もかも覆い隠してみせよう…！", voice_path = "Canon_Stage_2", sort = 41, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220608] = {id = 18220608, name = "立ち絵獲得2", remark = "堕天使の完全なる復活だ！　ククク…我輩がこの世界に破滅と恐怖をもたらしてみせよう！　アーッハッハッハッハー！", voice_path = "Canon_Stage_3", sort = 42, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220609] = {id = 18220609, name = "覚醒", remark = "貴様がこれまで我輩に捧げてきたもの…すべてたしかに受け取ったぞ！　…そ、その、あの…あ、ありがとう！", voice_path = "Canon_Star_1", sort = 50, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220610] = {id = 18220610, name = "好感度Lv1", remark = "うひゃあっ！？　に、人間め、貴様…我輩に触れるとは！　…我輩があまりに魅力的であるせいか、ククク…ハーッハッハッ――ゲホッ！　ゲホゲホッ…！", type = 1, voice_path = "Canon_Interaction_1", sort = 140, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220611] = {id = 18220611, name = "好感度Lv3", remark = "我輩は冥夜の堕天使だぞ、簡単に籠絡できると思うなよっ！", type = 1, voice_path = "Canon_Interaction_2", sort = 141, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220612] = {id = 18220612, name = "好感度Lv5", remark = "うむ、貴様の働きはとても良い。だが、この程度で私が…いや、我輩が『ありがとう』なんて口にするなんて思わないことだなっ。", type = 1, voice_path = "Canon_Interaction_3", sort = 142, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220613] = {id = 18220613, name = "好感度Lv7", remark = "ククク…この冥夜の堕天使が、貴様と契約を結んでやろう。――って、き、貴様が想像してるような意味ではない！　ただ…黙示録に貴様の名を刻んだだけだ！", type = 1, voice_path = "Canon_Interaction_4", sort = 143, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
[18220614] = {id = 18220614, name = "好感度Lv9", remark = "貴様…！　もしや我輩と、この冥夜の力を共有したいのか？　…ならば、我輩の手を取るといい。それで我輩のすべては、貴様のものだっ…", type = 1, voice_path = "Canon_Interaction_5", sort = 144, bubble_id = "14220600:2:437:249,14220601:2:437:249,14220602:2:437:249"}
, 
