[18300501] = {id = 18300501, name = "メンバー募集", remark = "サラだ。かわいいリーダーには特別に、この世界の楽しみ方をお姉さんが案内してあげようか。ふふ。", voice_path = "Sara_Get_1", sort = 10, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
, 
[18300502] = {id = 18300502, name = "ログインボイス", remark = "お帰り、リーダー。ご飯にするかい、お風呂にするかい？　それとも…お姉さんにするかい？", voice_path = "Sara_In_1", sort = 20, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
, 
[18300503] = {id = 18300503, name = "待機ボイス1", remark = "リーダー。私の遊びに付き合ってくれるなら、とっておきのご奉仕をしてあげよう。…って、どちらも一緒かな？", voice_path = "Sara_Main_1", sort = 30, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
, 
[18300504] = {id = 18300504, name = "待機ボイス2", remark = "こちらのお仕事は終わったよ。他に命令はあるかな？　家事から戦闘まで…もちろん、夜のことも任せてくれ。", voice_path = "Sara_Main_2", sort = 31, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
, 
[18300505] = {id = 18300505, name = "待機ボイス3", remark = "私の過去が知りたいなら…ほら、口を開けて。あーん。…ふふ、ケーキを食べるときのリーダーも愛らしいね。", voice_path = "Sara_Main_3", sort = 32, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
, 
[18300506] = {id = 18300506, name = "待機ボイス4", remark = "リーダー、オリヴィアのお嬢を見なかったかな？ 探しているんだが、どこにもいなくてね。 やれやれ、彼女が怪我をしたら私が怒られてしまうよ。", voice_path = "Sara_Main_4", sort = 33, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
, 
