[18300508] = {id = 18300508, name = "立ち絵獲得1", remark = "かわいいリーダーに頼られてうれしいよ、ふふ。リーダーが望むなら、お姉さんはいつでも助けに来よう。", voice_path = "Sara_Stage_2", sort = 41, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
