[18300511] = {id = 18300511, name = "好感度Lv1", remark = "メイドは二十四時間、主人に付き添うものだからね。夜まで待たなくともーー大丈夫だよ？", type = 1, voice_path = "Sara_Interaction_1", sort = 140, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
