, 
[18300513] = {id = 18300513, name = "好感度Lv5", remark = "リーダー、大好きだよ。…どうだい、何か感じたかな？　…おや、おかしいな。ちゃんと少しだけ、気持ちを込めたんだけど。", type = 1, voice_path = "Sara_Interaction_3", sort = 142, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
