[18300514] = {id = 18300514, name = "好感度Lv7", remark = "君が目の前にいてくれる幸せを大事にしたいんだ。この幸せを守るためなら、お姉さんはなんでもやるよ。", type = 1, voice_path = "Sara_Interaction_4", sort = 143, bubble_id = "14300500:2:476:255,14300501:2:476:255,14300502:2:476:255"}
