[18300401] = {id = 18300401, name = "メンバー募集", remark = "フラワーだよ。フラワーはね、えっと…『よしよし』ができるよ。疲れたら、たくさん『よしよし』してあげるね。それから、お仕事終わったら、一緒に遊ぼうね。えへへ。", voice_path = "Flower_Get_1", sort = 10, bubble_id = "14300400:2:492:263,14300401:2:308:270,14300402:2:492:263"}
, 
[18300402] = {id = 18300402, name = "ログインボイス", remark = "フラワーは、リーダーが来ると『うれしいー』ってなるよ。えへへ", voice_path = "Flower_In_1", sort = 20, bubble_id = "14300400:2:492:263,14300401:2:308:270,14300402:2:492:263"}
