[18300403] = {id = 18300403, name = "待機ボイス1", remark = "リーダーの願いは、フラワーの願いでもあるの。フラワー、良い子にするから。リーダーの話をよく聞いて、一生懸命頑張るから…リーダーのお願い、叶えようね。", voice_path = "Flower_Main_1", sort = 30, bubble_id = "14300400:2:492:263,14300401:2:308:270,14300402:2:492:263"}
, 
[18300404] = {id = 18300404, name = "待機ボイス2", remark = "リーダー、リーダー。あのね、早口言葉を練習したから聞いてほしいな。いくよー。なまむぎ、なまごめ、なまタバ…あう…舌、噛んじゃった…", voice_path = "Flower_Main_2", sort = 31, bubble_id = "14300400:2:492:263,14300401:2:308:270,14300402:2:492:263"}
, 
[18300405] = {id = 18300405, name = "待機ボイス3", remark = "フラワーね、とっても楽しいんだ。このプライドに入ってね、みんなと遊べて、リーダーがいて…すっごく『良かったー』って思うよ。ありがとう、リーダー。", voice_path = "Flower_Main_3", sort = 32, bubble_id = "14300400:2:492:263,14300401:2:308:270,14300402:2:492:263"}
, 
[18300406] = {id = 18300406, name = "待機ボイス4", remark = "フラワーのことね、いろんな知らない人が気に入ってくれるんだ。でも、他のところに行こうって思わないの。リーダーと一緒にいるのが、フラワーはいちばん楽しいから。だからリーダー、フラワーを置いていったりしないでね。", voice_path = "Flower_Main_4", sort = 33, bubble_id = "14300400:2:492:263,14300401:2:308:270,14300402:2:492:263"}
, 
