[18300103] = {id = 18300103, name = "待機ボイス1", remark = "勤勉はいいことだ。だが、適度な休憩はすべきだぞ。 目的を達成するまえに、リーダーが倒れては事だがらな。", voice_path = "Gloria_Main_1", sort = 30, bubble_id = "14300100:2:386:240,14300101:2:386:240,14300102:2:386:240"}
