[18300104] = {id = 18300104, name = "待機ボイス2", remark = "戦いにはいつも悲傷と絶望が伴うものだ。 だが、平和を守るためには戦うしかない…  なんてことだ…！", voice_path = "Gloria_Main_2", sort = 31, bubble_id = "14300100:2:386:240,14300101:2:386:240,14300102:2:386:240"}
