[18300106] = {id = 18300106, name = "待機ボイス4", remark = "私たちの目的は遠い。だが、せめて近づけるよう、私にできることがあったら言ってくれ。貴君も手を貸してくれると嬉しい。", voice_path = "Gloria_Main_4", sort = 33, bubble_id = "14300100:2:386:240,14300101:2:386:240,14300102:2:386:240"}
