[18300111] = {id = 18300111, name = "好感度Lv1", remark = "戦う者として、貴君のリーダーとしての適性を認めている。貴君の隣にいて相応しい者となるため、努力しよう。よろしく頼む。", type = 1, voice_path = "Gloria_Interaction_1", sort = 140, bubble_id = "14300100:2:386:240,14300101:2:386:240,14300102:2:386:240"}
