[18302403] = {id = 18302403, name = "待機ボイス1", remark = "昔は風みたいにとっても速く走れたのよ。でも…なぜか年々足が重くなって、肩もこっちゃって…これが大人になるってことなのかしら…？", voice_path = "youru_Main_1", sort = 30, bubble_id = "14302400:2:407:225,14302401:2:219:261,14302402:2:407:225"}
