[18302405] = {id = 18302405, name = "待機ボイス3", remark = "今、服をもっと軽くできないか考えていたところよ。だってほら、着てるものが軽ければ軽いほど、足が速くなるはずでしょう？　だから…うーん、もっと切り込みを深く入れちゃおうかしら…？", voice_path = "youru_Main_3", sort = 32, bubble_id = "14302400:2:407:225,14302401:2:219:261,14302402:2:407:225"}
, 
[18302406] = {id = 18302406, name = "待機ボイス4", remark = "クリスマスについて考えるだけで楽しくなっちゃうの。プレゼントは何をあげようか、どんな包装紙やリボンにしようか、なんて…。ふふ、毎日こんなことばかり考えられて、私ってほんと幸せ者ね♪", voice_path = "youru_Main_4", sort = 33, bubble_id = "14302400:2:407:225,14302401:2:219:261,14302402:2:407:225"}
