[18303008] = {id = 18303008, name = "立ち絵獲得1", remark = "父が倒れた後、私は一人で立ち向かうしかないと思っていたが…どうやら違ったようだ。私には、プデルもリーダーもそばにいる…こんなに心強いことはない。", voice_path = "Coonya_Stage_2", sort = 41, bubble_id = "14303000:2:385:220,14303001:2:388:252,14303002:2:388:252"}
