[18303012] = {id = 18303012, name = "好感度Lv3", remark = "こうした立場にいると、人を判断せざるを得ない。敵か、味方か、愚か者か、そうでないか。…だから、あなたのような存在がとても頼もしいんだ。", type = 1, voice_path = "Coonya_Interaction_2", sort = 141, bubble_id = "14303000:2:385:220,14303001:2:388:252,14303002:2:388:252"}
