[18303013] = {id = 18303013, name = "好感度Lv5", remark = "…ふっ。私にイタズラを仕掛けるなんて、あなたぐらいだ。お返しに、私もあなたの背中を引っかいて…痕でもつけてしまおうか？", type = 1, voice_path = "Coonya_Interaction_3", sort = 142, bubble_id = "14303000:2:385:220,14303001:2:388:252,14303002:2:388:252"}
