[18302613] = {id = 18302613, name = "好感度Lv5", remark = "い、いけません、リーダーさん！　私の身体は、本当に貧相なので…！　もっと筋肉をつけて、敵を思いっきり吹っ飛ばせるようになったときには、また…って、私は何の話をっ！？", type = 1, voice_path = "Pudel_Interaction_3", sort = 142, bubble_id = "14302600:2:303:229,14302601:2:435:220,14302602:2:435:220"}
