[45083024] = {id = 45083024, name = "幸せな配達員", remark = "良い子達の期待を裏切らないよう、もっともっと速く走らなくちゃ！", des = "良い子達の期待を裏切らないよう、もっともっと速く走らなくちゃ！\n\n「幸せを届けに」\n通常攻撃/特技/奥義を発動する場合、目標の十字列にいる敵に（攻撃*35%）のダメージを拡散する。拡散ダメージにより相手が死亡した場合、撃破による特殊効果及びSP回復は発動しない。\n\n「努力の賜物」\nラウンド開始ごとに、会心を5%上昇させる。", des_array = "「幸せを届けに」\n通常攻撃/特技/奥義を発動する場合、目標の十字列にいる敵に（攻撃*35%）のダメージを拡散する。拡散ダメージにより相手が死亡した場合、撃破による特殊効果及びSP回復は発動しない。\n\n「努力の賜物」\nラウンド開始ごとに、会心を5%上昇させる。", icon_path = "SkillIcon:45083024", type = 6, targetType = 0, damage = 0, damage_up = 0, damage_rate = 0, damage_rate_up = 0, strike_prob = 0, fall_prob = 0, buff_odds = 0, buff_list = "0", show_id = 0, add_attr = "0", add_attr_type = 0, fc = 0, need_quality = 0, need_star = 0, sort_index = 3}
