[75104009] = {id = 75104009, remark = "なんで～！？　すっごい良い誘い方したじゃんっ！\nどうしてフランちゃーん！？", type = 1, option_ids = "0", next = 75104010, role_ids = "75201003:75201001:75201002", expression = "0:exp_shock:0", action = "0:shock:0", action_type = "0", role_type = 75201001, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "April_Plot_172"}
, 
[75104010] = {id = 75104010, remark = "私は、この大図書館から離れるつもりはない。\nそれが先代の管理人…祖母との約束だから。", type = 1, option_ids = "0", next = -1, role_ids = "75201003:75201001:75201002", expression = "0", action = "0", action_type = "0", role_type = 75201003, position = "64:606:10050,418:606:10000,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "francesca_Plot_34"}
, 
[75104101] = {id = 75104101, remark = "約束？", type = 1, option_ids = "0", next = 75104102, role_ids = "75201002", expression = "exp_shock", action = "0", action_type = "0", role_type = 75201002, position = "418:606:10050", role_admission_type = "75201002:50:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "Olivia_Plot_133"}
, 
[75104102] = {id = 75104102, remark = "そう。私の一族は大図書館の管理人であり、ここを守り続けることをあらゆる国々に任されている。", type = 1, option_ids = "0", next = 75104103, role_ids = "75201002:75201003", expression = "0", action = "0", action_type = "0", role_type = 75201003, position = "195:606:10000,642:606:10050", role_admission_type = "75201003:-50:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "francesca_Plot_35"}
, 
[75104103] = {id = 75104103, remark = "少しずつ忘れ去られ、祖母と私だけしかいなくなった頃\n…とどめを刺したのは、騎士団だった。", type = 1, option_ids = "0", next = 75104104, role_ids = "75201002:75201003", expression = "0:exp_said", action = "0", action_type = "0", role_type = 75201003, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "francesca_Plot_36"}
, 
[75104104] = {id = 75104104, remark = "あいつらが、王国の恥ずべき歴史を消すため、\n焚書をしに来て――", type = 1, option_ids = "0", next = 75104105, role_ids = "75201002:75201003", expression = "0:exp_angry", action = "0:angry", action_type = "0", role_type = 75201003, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "francesca_Plot_37"}
, 
[75104105] = {id = 75104105, remark = "もしかして\nそれであんなに、罠を張るようになったの？", type = 1, option_ids = "0", next = 75104106, role_ids = "75201002:75201003", expression = "exp_said:0", action = "0", action_type = "0", role_type = 75201002, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "Olivia_Plot_134"}
, 
[75104106] = {id = 75104106, remark = "ああ、祖母の教えだ。もはや現代は、\n何者も立ち入らせるべきでないと判断してな。", type = 1, option_ids = "0", next = 75104107, role_ids = "75201002:75201003", expression = "0:angry", action = "0", action_type = "0", role_type = 75201003, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "francesca_Plot_38"}
, 
[75104107] = {id = 75104107, remark = "…だから、私はここを守り続けるために、\nプライドには参加できない。", type = 1, option_ids = "0", next = 75104108, role_ids = "75201002:75201003", expression = "0:said", action = "0", action_type = "0", role_type = 75201003, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "francesca_Plot_39"}
, 
[75104108] = {id = 75104108, remark = "ふーむ、フランちゃんの事情はわかったよ。\nひとりでここにい続けるって、すごい決心だし。", type = 1, option_ids = "0", next = 75104109, role_ids = "75201002:75201003:75201001", expression = "0:0:exp_said", action = "0:0:said", action_type = "0", role_type = 75201001, position = "64:606:10000,418:606:10000,772:606:10050", role_admission_type = "75201001:-50:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001, effect_sound = 0, dialogue_sound = "April_Plot_173"}
, 
