[75451901] = {id = 75451901, remark = "面倒なことになったわね…ただの潜入調査だったのに\nキャストレッドに見つかるなんて…", type = 1, option_ids = "0", next = 75451902, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", role_admission_type = "75200511:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
