[75451902] = {id = 75451902, remark = "こっちはふたり、あちらは何人いるかもわからない…\n殺されてもおかしくない…", type = 1, option_ids = "0", next = 75451903, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
