[75451904] = {id = 75451904, remark = "私は…ある人の命令であなたを監視していた。\nそれは、私を救ってくれた大切な人なの。", type = 1, option_ids = "0", next = 75451905, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75451905] = {id = 75451905, remark = "その人のために私はここにいて、\nあなたを見張っていた。でも…", type = 1, option_ids = "0", next = 75451906, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75451906] = {id = 75451906, remark = "あなたと過ごしているうちに、\n一瞬だけ任務のことを忘れることがあったの。", type = 1, option_ids = "0", next = 75451907, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75451907] = {id = 75451907, remark = "――勘違いしないで、遺言とかじゃないわ。\nただ…私のことを少しでも知って、信頼してほしいの。", type = 1, option_ids = "0", next = 75451908, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75451908] = {id = 75451908, remark = "私を信じてくれるなら…あなたひとりで表に出て。\nそして一瞬でいいの、敵の気を引いて。", type = 1, option_ids = "0", next = 75451909, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75451909] = {id = 75451909, remark = "大丈夫。絶対、敵にあなたを殺させたりしない。\n…あなたは絶対に、私が守るから。", type = 1, option_ids = "75505191:75505192", next = 0, role_ids = "75200511", expression = "0", action = "0", action_type = "0", role_type = 75200511, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
