{
[75465001] = {id = 75465001, remark = "やぁ、リーダー。クリスマスパーティは\nまだ途中なのに、こっちに来たのかい？", type = 1, option_ids = "0", next = 75465002, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", role_admission_type = "75200641:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465002] = {id = 75465002, remark = "もしかして、私を気にかけてくれたのかな。\nふふ、だとしたら嬉しいよ。", type = 1, option_ids = "0", next = 75465003, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465003] = {id = 75465003, remark = "…本当のことをいうと、君ならきっと気づいてくれる、\nと期待していたところだ。", type = 1, option_ids = "0", next = 75465004, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465004] = {id = 75465004, remark = "――ふたりで過ごす時間も素敵だけれど…", type = 1, option_ids = "0", next = 75465005, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465005] = {id = 75465005, remark = "みんなが楽しむ中、こうしてこっそりふたりで\n抜け出す背徳感と喜びも…代え難いものがあるね。", type = 1, option_ids = "0", next = 75465006, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465006] = {id = 75465006, remark = "と言っても、君はプライドのリーダーで\n大勢が君の帰りを待ちわびている。", type = 1, option_ids = "0", next = 75465007, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465007] = {id = 75465007, remark = "ただ、ほんの今だけは…\n私だけのリーダーでいて欲しいな。", type = 1, option_ids = "75506417:75506418", next = 0, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465008] = {id = 75465008, remark = "…とても嬉しいよ。けれど、その優しさには\nときどき、寂しくも思わせられるな。", type = 1, option_ids = "0", next = 75465010, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465009] = {id = 75465009, remark = "そんなことを言うなら…\n今晩は、君を独り占めしてしまおうか…？", type = 1, option_ids = "0", next = 75465010, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465010] = {id = 75465010, remark = "…でも、もしずっとふたりだけでいたいと\n思ってくれた時には…私のリボンをほどくといい。", type = 1, option_ids = "0", next = 75465011, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465011] = {id = 75465011, remark = "そうしたら…\n私はずっと、ずっと…君のそばで――", type = 1, option_ids = "0", next = 75465012, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
