[75465212] = {id = 75465212, remark = "簡単なものしか対応いたしかねますが、リーダー様も\n何か縫い物がございましたら、ご相談ください。", type = 1, option_ids = "0", next = 75465213, role_ids = "75200652:75200071", role_type = 75200652, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
