, 
[75465004] = {id = 75465004, remark = "――ふたりで過ごす時間も素敵だけれど…", type = 1, option_ids = "0", next = 75465005, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465005] = {id = 75465005, remark = "みんなが楽しむ中、こうしてこっそりふたりで\n抜け出す背徳感と喜びも…代え難いものがあるね。", type = 1, option_ids = "0", next = 75465006, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465006] = {id = 75465006, remark = "と言っても、君はプライドのリーダーで\n大勢が君の帰りを待ちわびている。", type = 1, option_ids = "0", next = 75465007, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465007] = {id = 75465007, remark = "ただ、ほんの今だけは…\n私だけのリーダーでいて欲しいな。", type = 1, option_ids = "75506417:75506418", next = 0, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465008] = {id = 75465008, remark = "…とても嬉しいよ。けれど、その優しさには\nときどき、寂しくも思わせられるな。", type = 1, option_ids = "0", next = 75465010, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465009] = {id = 75465009, remark = "そんなことを言うなら…\n今晩は、君を独り占めしてしまおうか…？", type = 1, option_ids = "0", next = 75465010, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465010] = {id = 75465010, remark = "…でも、もしずっとふたりだけでいたいと\n思ってくれた時には…私のリボンをほどくといい。", type = 1, option_ids = "0", next = 75465011, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465011] = {id = 75465011, remark = "そうしたら…\n私はずっと、ずっと…君のそばで――", type = 1, option_ids = "0", next = 75465012, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465012] = {id = 75465012, remark = "――メリークリスマス、リーダー。\n共に良い夜を送るとしよう…ふふ。", type = 1, option_ids = "0", next = -1, role_ids = "75200641", role_type = 75200641, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465101] = {id = 75465101, remark = "…近頃はすっかり冬らしくなりましたね、\nリーダー様。", type = 1, option_ids = "0", next = 75465102, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", role_admission_type = "75200331:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465102] = {id = 75465102, remark = "クリスマスも近づいて、\n街は賑わい、人の行き交いも増え…", type = 1, option_ids = "0", next = 75465103, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465103] = {id = 75465103, remark = "――そうなれば街に汚れが\n増えていくのは必然のこと…", type = 1, option_ids = "0", next = 75465104, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465104] = {id = 75465104, remark = "すなわち、わたくしの出番でございます。", type = 1, option_ids = "0", next = 75465105, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465105] = {id = 75465105, remark = "何をしてでもどんな手を使ってでも…\nあらゆる場所から不潔をなくしてみせましょう。", type = 1, option_ids = "75506501:75506502", next = 0, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465106] = {id = 75465106, remark = "そんなことはございません。\n掃除をすることに快楽を覚えているなど、決して。", type = 1, option_ids = "0", next = 75465108, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465107] = {id = 75465107, remark = "…なるほど。たしかに自らの実力を振るうことが\nできる点では、正しいように思います。", type = 1, option_ids = "0", next = 75465108, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465108] = {id = 75465108, remark = "リーダー様も、もし見過ごせない汚れが\nありましたら、いつでもご用命を。", type = 1, option_ids = "0", next = 75465109, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465109] = {id = 75465109, remark = "些細な汚れであろうと、雪ひと粒であろうと…\n徹底的に片付けてみせましょう。", type = 1, option_ids = "0", next = -1, role_ids = "75200331", role_type = 75200331, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465201] = {id = 75465201, remark = "ふむ、なるほど。\n想像よりも動きやすい衣装でございます。", type = 1, option_ids = "0", next = 75465202, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", role_admission_type = "75200652:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
