[75465204] = {id = 75465204, remark = "その服、後ろのほうに小さいけど穴が開いてるみたい…\n何年も使っていたせいね…", type = 1, option_ids = "0", next = 75465205, role_ids = "75200652:75200071", role_type = 75200071, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465205] = {id = 75465205, remark = "うぅ、縫って直したいところだけど、裁縫は苦手だし…\nだからネネル、悪いけどやっぱりいつもの服で…", type = 1, option_ids = "0", next = 75465206, role_ids = "75200652:75200071", role_type = 75200071, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
