[75465213] = {id = 75465213, remark = "ともかく…こちらで仕事に支障はございません。\n出発準備にとりかかるといたしましょう。", type = 1, option_ids = "0", next = 75465214, role_ids = "75200652:75200071", role_type = 75200652, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
